import type { CallSiteLike } from '@tapjs/stack'
import * as stack from '@tapjs/stack'
import type { BaseOpts } from './base.js'
import { Extra } from './index.js'

// test that a CallSiteLike is "useful", ie, has a file/line/col
const useful = (at: CallSiteLike | undefined) =>
  !!at?.fileName &&
  typeof at.lineNumber === 'number' &&
  typeof at.columnNumber === 'number'

/**
 * Create an {@link @tapjs/core!index.Extra} object based on a thrown Error
 */
export const extraFromError = (
  er: any,
  extra?: Extra,
  options?: BaseOpts
) => {
  // the yaml module puts big stuff here, pluck it off
  // otherwise it's quite noisy when we throw as a result of
  // trying to parse invalid tap diagnostics.
  if (
    !!er &&
    typeof er === 'object' &&
    er.source &&
    typeof er.source === 'object' &&
    er.source.context
  ) {
    const { context, ...source } = er.source
    er.source = source
  }

  // pull out all fields from options, other than anything starting
  // with tapChild, or anything already set in the extra object.
  options = options ?? {}
  extra = Object.assign(
    extra ?? {},
    Object.fromEntries(
      Object.entries(options).filter(
        ([k]) => !/^tapChild/.test(k) && !(k in (extra ?? {}))
      )
    )
  )

  if (!er || typeof er !== 'object') {
    extra.error = er
    return extra
  }

  const st = stack.captureError(er)
  if (st && st.length) {
    extra.stack = st.map(c => String(c)).join('\n')
    let at: CallSiteLike | undefined
    // walk down the stack until we find the first "useful" stack frame
    for (let i = 0; i < st.length; i++) {
      if (useful(st[i])) {
        at = st[i]
        break
      }
    }
    extra.at = at || st[0]
  } else if (typeof er.stack === 'string') {
    // if we failed to capture it, but it has a stack, then that means
    // that all of the stack frames were internal, because the error was
    // generated from native code in a dep that tap ignores (or if not
    // native code, then something else that escaped the async-hook-domain).
    // A common cause of this is import() errors.
    extra.stack = ''
    extra.at = null
  }

  if (er.name && er.name !== 'Error') {
    extra.type = er.name
  }

  // grab any other rando props
  const { message: _, name: __, stack: ___, ...props } = er
  Object.assign(extra, props)

  return extra
}

import { argv, proc } from './proc.js'
/**
 * Get the name of the main script for this process
 */
export const mainScript = (def: string = 'TAP') => {
  if (
    //@ts-ignore
    typeof repl !== 'undefined' ||
    //@ts-ignore
    proc._forceRepl ||
    //@ts-ignore
    '_eval' in proc
  ) {
    return def
  }
  return argv[1] || def
}

/**
 * This is not very useful in many cases outside of the tap runner itself,
 * where it's used to replay previous stored test results.
 *
 * @module
 */
import { Minipass } from 'minipass'
import { createReadStream } from 'node:fs'
import { relative, sep } from 'node:path'
import { Base, BaseOpts, TapBaseEvents } from './base.js'
import { cwd as procCwd } from './proc.js'
import { throwToParser } from './throw-to-parser.js'

/**
 * Options that may be provided to the {@link @tapjs/core!tap-file.TapFile}
 * class
 */
export interface TapFileOpts extends BaseOpts {
  /**
   * Optionally provide a stream of content to treat as the file's TAP data
   */
  tapStream?:
    | NodeJS.ReadableStream
    | Minipass<Buffer>
    | Minipass<string>
  /**
   * The file to read, required if tapStream not provided
   */
  filename?: string
  /**
   * just used to calculate the default name from the filename
   * ignored otherwise
   */
  cwd?: string
}

export interface TapFileEvents extends TapBaseEvents {}

/**
 * Class representing a file as a TAP stream
 *
 * @internal
 */
export class TapFile extends Base<TapFileEvents> {
  caughtName: string = 'tapFileError'
  emitName: string = 'tapFile'
  cwd?: string
  filename?: string
  tapStream?:
    | NodeJS.ReadableStream
    | Minipass<Buffer>
    | Minipass<string>
  constructor(options: TapFileOpts) {
    const { filename, tapStream, cwd = procCwd } = options
    const name = TapFile.getName(options.name, filename, cwd)
    super({ ...options, name })

    this.filename = filename
    this.cwd = cwd
    this.tapStream = tapStream
    this.tapStream?.pause()
  }

  static getName(
    name?: string,
    filename?: string,
    cwd: string = procCwd
  ) {
    if (name) return name
    if (!filename) return 'file input'
    const rel = relative(cwd, filename)
    return (rel.startsWith('..' + sep) ? filename : rel).replace(
      /\.tap$/,
      ''
    )
  }

  main(cb: () => void) {
    const { tapStream, filename } = this
    if (filename && !tapStream) {
      this.tapStream = createReadStream(filename)
    } else if (!this.tapStream) {
      throw new Error('either tapStream or filename must be provided')
    }
    this.tapStream.on('error', er => {
      er.tapCaught = this.caughtName
      this.threw(er)
    })
    if (this.options.timeout) {
      this.setTimeout(this.options.timeout)
    }
    const s = this.tapStream as Minipass
    s.pipe(this.parser)
    if (this.parent) {
      this.parent.emit(this.emitName, this)
    }
    s.once('end', cb)
    s.once('error', cb)
    this.tapStream.resume()
  }

  threw(er: any, extra?: any) {
    return throwToParser(this.parser, super.threw(er, extra))
  }
}

/**
 * The base class that is extended by all TAP-generating classes
 *
 * @module
 */

import { Domain } from 'async-hook-domain'
import { AsyncResource } from 'async_hooks'
import { Minipass } from 'minipass'
import { hrtime } from 'node:process'
