/**
 * Events emitted by the Base class
 */
export interface TapBaseEvents extends Minipass.Events<string> {
  /**
   * emitted when a timeout occurs
   */
  timeout: [threw?: Extra]
  /**
   * emitted when the test bails out
   */
  bailout: [reason?: string]
  /**
   * emitted when the test is complete
   */
  complete: [results: FinalResults]
}

/**
 * Wrapper for the async-hook-domain that catches errors thrown during
 * test operation.
 *
 * @see {@link https://npmjs.com/async-hook-domain}
 */
export class TapWrap extends AsyncResource {
  test: Base
  onDestroy?: () => void
  constructor(test: Base) {
    super(`tap.${test.constructor.name}`)
    this.test = test
  }
  emitDestroy() {
    this.onDestroy?.()
    super.emitDestroy()
    return this
  }
}

const debug =
  (name: string) =>
  (...args: any[]) => {
    const prefix = `TAP ${process.pid} ${name}: `
    const msg = format(...args).trim()
    console.error(prefix + msg.split('\n').join(`\n${prefix}`))
  }

/**
 * A number indicating an amount of milliseconds
 */
export type MILLISECONDS = number

/**
 * Options that may be passed to any TAP-generating class
 */
export interface BaseOpts extends Extra {
  /**
   * Bail out on the first failure
   */
  bail?: boolean
  /**
   * Treat any unknown non-TAP data as an error
   * May be set at run-time by the TAP stream using `pragma +strict`
   */
  strict?: boolean
  /**
   * Do not emit the `TAP version 14` line.
   */
  omitVersion?: boolean
  /**
   * Do not elide empty lines and other unnecessary whitespace
   */
  preserveWhitespace?: boolean
  /**
   * Skip this test entirely, emitting a `# SKIP` directive
   */
  skip?: boolean | string
  /**
   * Mark this test as to be done later, emitting a `# TODO` directive
   */
  todo?: boolean | string
  /**
   * Amount of time in milliseconds before this test times out
   */
  timeout?: MILLISECONDS

  /**
   * track passes in the results lists, default false
   */
  passes?: boolean

  /**
   * treat todo tests as failures, default false
   */
  failTodo?: boolean

  /**
   * treat skip tests as failures, default false
   */
  failSkip?: boolean

  /**
   * The amount of time that this test took to complete.
   *
   * Typically, this is not set explicitly, but inferred from the actual
   * time spent. However in some cases, it may be reported by the
   * top-level `# time=...` comment in a TAP subprocess stream.
   */
  time?: number
  /**
   * The TAP data from a buffered test.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  tapChildBuffer?: string
  /**
   * The stack where this test was initiated
   */
  stack?: string

  /**
   * Parent test of this test
   */
  parent?: Base | TestBase
  /**
   * The name of this test
   */
  name?: string
  /**
   * Numeric identifier attached to child tests. Most of the time, this is
   * set based on the `TAP_CHILD_ID` environment variable.
   */
  childId?: number
  /**
   * Any arbitrary data that is provided to this test object. Often, this
   * is set in a `t.before()` or `t.beforeEach()` method. Scalar values
   * are inherited by child tests. Object values are extended in child
   * tests using `Object.create()`.
   */
  context?: any
  /**
   * Number of spaces that this test is indented.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  indent?: string
  /**
   * True to output LOTS AND LOTS of noisy debugging information.
   * Set at the top level by the `TAP_DEBUG` environment variable.
   */
  debug?: boolean
  /**
   * Parser to use for this TAP stream.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  parser?: Parser
  /**
   * True if this test should be buffered, and only processed once
   * complete.
   *
   * Defaults to true when `t.jobs` is set to a value greater than 1.
   *
   * If set false in that case, then the test will NOT be run in parallel,
   * so this provides a way to control the parallelism within a test suite.
   */
  buffered?: boolean

  /**
   * Setting silent:true in a subtest option makes it completely excluded
   * from test output, UNLESS it fails.
   *
   * This was used historically for a few things which are now implemented
   * with a more sophisticated promise management system, but can be handy in
   * some rare situations.
   */
  silent?: boolean
}

export class Base<
  Events extends TapBaseEvents = TapBaseEvents
> extends Minipass<string, string, Events> {
  /**
   * Set upon test completion when a child test is ready to be processed by its
   * parent.
   */
  readyToProcess: boolean = false
  /**
   * Options provided to this test
   */
  options: BaseOpts
  /**
   * number of spaces to indent the TAP stream
   */
  indent: string
  /**
   * TapWrap AsyncResource that limits the async-hook-domain
   *
   * @group Internal Machinery
   */
  hook: TapWrap
  // this actually is deterministically set in the ctor, but
  // in the hook, so tsc doesn't see it.
  /**
   * the async-hook-domain that catches throws and Promise rejections
   *
   * @group Internal Machinery
   */
  hookDomain!: Domain
  /**
   * The timer that fires when the test times out
   *
   * @group Internal Machinery
   */
  timer?: NodeJS.Timeout

  /**
   * Set to true when the test times out, so its failure status can be
   * determined later.
   */
  timedOut: boolean = false
  /**
   * The tap parser attached to this TAP stream
   *
   * @group Internal Machinery
   */
  parser: Parser
  /**
   * Method that writes to stderr when `debug: true` is set in the options,
   * or no-ops otherwise
   */
  debug: (...args: any[]) => void
  /**
   * The count of all assertions that this stream emitted
   *
   * @group Test Reflection
   */
  counts: Counts
  /**
   * Lists of todo, skip, and failure test points. If `passes: true` is
   * set in the options, then passing test points will also be tracked.
   *
   * @group Test Reflection
   */
  lists: Lists
  /**
   * the name of this test
   *
   * @group Test Reflection
   */
  name: string
  /**
   * Set on completion. The results of the test run.
   *
   * @group Test Reflection
   */
  results?: FinalResults
  /**
   * Parent test of this TAP stream
   *
   * @group Internal Machinery
   */
  parent?: Base | TestBase

  /**
   * Nesting level, for serialization to node test runner
   *
   * Note that this is zero for parent-less tests, and *also* zero
   * for the first level of children.
   */
  nestingLevel: number = 0

  /**
   * Bail out on the first failed test point
   */
  bail: boolean
  /**
   * Treat non-TAP data as an error.
   * May be set with `pragma +strict` in the TAP stream, or unset with
   * `pragma: -strict`.
   */
  strict: boolean
  /**
   * Do not emit the `TAP version 14` line at the start
   */
  omitVersion: boolean
  /**
   * Do not elide extraneous whitespace and empty lines.
   */
  preserveWhitespace: boolean

  /**
   * Unrecoverable TAP protocol errors in the stream
   *
   * @group Test Reflection
   */
  errors: TapError[]

  /**
   * Numeric identifier for this test
   */
  childId: number

  /**
   * Any arbitrary data that is provided to this test object. Often, this
   * is set in a `t.before()` or `t.beforeEach()` method. Scalar values
   * are inherited by child tests. Object values are extended in child
   * tests using `Object.create()`.
   */
  context: any

  /**
   * the TAP stream data for buffered tests
   *
   * @internal
   *
   * @group Internal Machinery
   */
  output: string

  /**
   * True if this test should be buffered and only processed on completion
   *
   * @internal
   *
   * @group Internal Machinery
   */
  buffered: boolean

  /**
   * True if this test emitted a bailout
   *
   * @group Test Reflection
   */
  bailedOut: string | boolean

  /**
   * high resolution bigint time when this test started
   *
   * @group Internal Machinery
   */
  start: bigint
  #started: boolean = false

  /**
   * Amount of time in milliseconds that this test took to complete.
   *
   * @group Test Reflection
   */
  time: MILLISECONDS

  /**
   * High resolution time in ns that this test took to complete.
   *
   * @group Test Reflection
   */
  hrtime: bigint

  /**
   * True if this test should be buffered and only emit data if it fails
   */
  silent: boolean

  /**
   * A `Deferred` promise wrapper that is resolved when this test completes.
   *
   * @group Internal Machinery
   */
  deferred?: Deferred<FinalResults>

  #printedOutput: boolean = false

  #writable: boolean = true

  constructor(options: BaseOpts = {}) {
    super({ encoding: 'utf8' })
    // always use the constructor name as toStringTag, so we get
    // [object Test] or [object TAP] and the appropriate typeName
    // in stack traces.
    Object.defineProperty(this, Symbol.toStringTag, {
      value: this.constructor.name,
      configurable: true,
    })
    // all tap streams are sync string minipasses
    this.hook = new TapWrap(this)
    this.options = options
    this.counts = new Counts()
    this.lists = new Lists()

    this.silent = !!options.silent

    // if it's null or an object, inherit from it.  otherwise, copy it.
    const ctx = options.context
    if (ctx !== undefined) {
      this.context =
        typeof ctx === 'object' ? Object.create(ctx) : ctx
    } else {
      this.context = null
    }

    this.bail = !!options.bail
    this.strict = !!options.strict
    this.omitVersion = !!options.omitVersion
    this.preserveWhitespace = options.preserveWhitespace !== false
    this.buffered = this.silent
      ? options.buffered === undefined
      : !!options.buffered
    this.bailedOut = false
    this.errors = []
    this.parent = options.parent
    if (this.parent?.parent) {
      this.nestingLevel = this.parent.nestingLevel + 1
    }

    this.time = 0
    this.hrtime = 0n
    this.start = 0n
    this.childId = options.childId || 0
    // do we need this?  couldn't we just call the Minipass
    this.output = ''
    this.indent = options.indent || ''
    this.name = options.name || '(unnamed test)'
    this.hook.runInAsyncScope(() => {
      this.hookDomain = new Domain((er, type) => {
        /* c8 ignore start */
        if (!er || typeof er !== 'object') {
          er = { error: er }
        }
        /* c8 ignore stop */
        ;(er as { tapCaught?: string }).tapCaught = type
        this.threw(er)
      })
    })
    this.debug = !!options.debug ? debug(this.name) : () => {}

    this.parser =
      options.parser ||
      new Parser({
        bail: this.bail,
        strict: this.strict,
        omitVersion: this.omitVersion,
        preserveWhitespace: this.preserveWhitespace,
        passes: this.options.passes,
        name: this.name,
      })
    this.#setupParser()

    // ensure that a skip or todo on a child class reverts
    // back to Base's no-op main.
    if (options.skip || options.todo) {
      this.main = Base.prototype.main
    }
  }

  #isFilterSkip(res: Result) {
    return (
      typeof res.skip === 'string' &&
      /^filter: (only|\/.*\/)$/.test(res.skip)
    )
  }

  #onParserResult(res: Result) {
    this.counts.total++
    const type = res.todo
      ? 'todo'
      : res.skip
      ? 'skip'
      : !res.ok
      ? 'fail'
      : 'pass'
    this.counts[type]++
    if (type === 'pass' && this.options.passes) {
      this.lists.pass.push(res)
    } else if (type === 'todo') {
      this.lists.todo.push(res)
    } else if (type === 'skip') {
      if (!this.#isFilterSkip(res)) this.lists.skip.push(res)
    } else if (type === 'fail') {
      this.lists.fail.push(res)
    }
  }

  #setupParser() {
    this.parser.on('line', l => this.#online(l))
    this.parser.once('bailout', reason => this.onbail(reason))
    this.parser.on('complete', result => this.oncomplete(result))
    this.parser.on('result', res => this.#onParserResult(res))
  }

  /**
   * Set the amount of time in milliseconds before this test is considered
   * a timeout. The time is counted from right now, so for example, repeatedly
   * calling `t.setTimeout(100)` can keep it going indefinitely, as long as
   * you call it more often than every 100ms.
   *
   * Calling `setTimeout(0)` will remove the timer and allow the test to run
   * indefinitely.
   *
   * @group Test Lifecycle Management
   */
  setTimeout(n: MILLISECONDS) {
    if (this.timer) {
      clearTimeout(this.timer)
    }
    if (n <= 0) {
      this.timer = undefined
      delete this.options.timeout
    } else {
      this.options.timeout = n
      this.timer = setTimeout(() => this.timeout(), n)
      /* c8 ignore start */
      if (this.timer.unref) this.timer.unref()
      /* c8 ignore stop */
    }
  }

  /**
   * Called when a timeout occurs. Only exposed because it has to be called
   * and/or extended by other classes, which all have their own sorts of
   * timeout behavior specific to the type of thing they represent.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  timeout(
    options: { expired?: string; message?: string } = {
      expired: this.name,
      message: 'timeout!',
    }
  ) {
    this.timedOut = true
    const { message = 'timeout!' } = options
    this.setTimeout(0)
    options.expired = options.expired || this.name
    // timeouts don't generally have a useful callsite information,
    // and no sense trying to capture it from @tapjs/stack
    const extra: Extra = {
      ...options,
      message,
      stack: '',
      at: {},
    }

    const threw = this.threw({ message }, extra)
    delete extra.stack
    delete extra.at
    if (threw) {
      this.emit('timeout', threw)
    }
  }

  /**
   * Run the `main` test function. Called by
   * {@link @tapjs/core!test-base.TestBase} when starting a subtest.
   * Initializes the TapWrap hook
   *
   * @internal
   *
   * @group Internal Machinery
   */
  runMain(cb: () => void) {
    this.debug('BASE runMain')
    this.start = hrtime.bigint()
    this.#started = true
    this.hook.runInAsyncScope(this.main, this, cb)
  }

  /**
   * Returns true if this test has begun
   *
   * @group Test Reflection
   */
  get started() {
    return this.#started
  }

  /**
   * True if the test has printed *some* output of any kind
   *
   * @group Test Reflection
   */
  get printedOutput() {
    return this.#printedOutput
  }

  /**
   * Boolean indicating whether the underlying stream can be written to,
   * or if it has been ended.
   */
  get streamWritable() {
    return this.#writable
  }

  /**
   * The main test function. For this Base class, this is a no-op. Subclasses
   * implement this in their specific ways.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  main(cb: () => void) {
    cb()
  }

  /**
   * Stream write method.
   *
   * For buffered tests, this collects the output in the
   * {@link @tapjs/core!base.Base#output}
   * field. Sets {@link @tapjs/core!base.Base#printedOutput} to `true` when
   * called.
   *
   * Note: you *probably* never need to call this. Instead, use the various
   * assertion methods and other parts of the API.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  write(c: string) {
    this.#printedOutput = true
    if (this.buffered || this.silent) {
      // need the silent output if it fails
      this.output += c
      return true
    }

    if (!this.#writable) {
      return false
    }

    return super.write(c)
  }

  /**
   * Method called when the parser encounters a bail out
   *
   * Extended by {@link @tapjs/core!test-base.TestBase}
   */
  onbail(reason?: string) {
    this.bailedOut = reason || true
    this.emit('bailout', reason)
  }

  /**
   * Method called when parser emits a line of TAP data
   *
   * @internal
   *
   * @group Internal Machinery
   */
  #online(line: string) {
    this.debug('LINE %j', line, [this.name, this.indent])
    return this.write(this.indent + line)
  }

  /**
   * Method called when the parser completes and emits its final results
   *
   * Extended by {@link @tapjs/core!worker.Worker} and
   * {@link @tapjs/core!tap.TAP} classes
   *
   * @internal
   *
   * @group Internal Machinery
   */
  oncomplete(results: FinalResults) {
    if (this.start) {
      this.hrtime = hrtime.bigint() - this.start
      this.time =
        results.time || Math.floor(Number(this.hrtime) / 1000) / 1000
    }

    this.debug('ONCOMPLETE %j %j', this.name, results)

    // should only ever happen once, but just in case
    /* c8 ignore start */
    if (this.results) {
      Object.assign(results, this.results)
    }
    /* c8 ignore stop */

    this.results = results
    this.emit('complete', results)
    const errors = results.failures
      .filter(f => f.tapError)
      .map(f => {
        delete f.diag
        delete f.ok
        return f
      })

    if (errors.length) {
      this.errors = errors
    }

    this.#writable = false
    super.end()
  }

  /**
   * extension point for plugins that want to be notified when the test
   * is about to end, whether explicitly or implicitly.
   *
   * If the function returns a Promise, it will be awaited before ending
   * the TAP stream.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  onbeforeend(): Promise<void> | void {}

  /**
   * extension point for plugins that want to be notified when the test
   * is completely done, and terminating its parser.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  onEOF(): Promise<void> | void {}

  /**
   * extension point for TestBase to know when a child tests is done being
   * processed and it's safe to move on to the next one.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  ondone() {}

  /**
   * EventEmitter emit method, but closes the
   * {@link @tapjs/core!base.Base#hook} and
   * {@link @tapjs/core!base.Base#hookDomain} when emitting `'end'`.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  emit<Event extends keyof Events>(
    ev: Event,
    ...data: Events[Event]
  ) {
    const ret = super.emit(ev, ...data)
    if (ev === 'end') {
      this.ondone()
      this.hook.emitDestroy()
      this.hookDomain.destroy()
    }
    return ret
  }

  /**
   * Mark the test as ended. In this base class, this is a no-op, but
   * in {@link @tapjs/core!test-base.TestBase}, it also triggers the other
   * end-of-test operations.
   */
  end() {
    return this
  }

  /**
   * Method called when an unrecoverable error is encountered in a test.
   *
   * Typically, in tests you would not call this, you'd just actually throw
   * an error.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  threw(
    er: any,
    extra?: Extra,
    proxy: boolean = false,
    ended: boolean = false
  ): Extra | void | undefined {
    this.debug('BASE.threw', er)
    this.hook.emitDestroy()
    this.hookDomain.destroy()
    if (typeof er === 'string') {
      er = { message: er }
    } else if (!er || typeof er !== 'object') {
      er = { error: er }
    }
    if (this.name && !proxy) {
      er.test = this.name
    }

    const message = messageFromError(er)
    if (!extra) {
      extra = extraFromError(er, extra)
    }
    extra.message = message

    this.parser.ok = false

    // if possible to handle it here, then return the info so that this
    // Base subclass can do its thing
    if (
      !ended &&
      !this.results &&
      /* c8 ignore start */
      (this.parser.planEnd === -1 ||
        this.parser.count < this.parser.planEnd)
      /* c8 ignore stop */
    ) {
      return extra
    }

    // if we ended, we have to report it SOMEWHERE, unless we're
    // already in the process of bailing out, in which case it's
    // a bit excessive. Do not print it here if it would trigger
    // a plan exceeded error, or if we already have results.
    this.debug(
      'Base.threw, but finished',
      this.name,
      this.results,
      extra.message
    )
    const alreadyBailing =
      (this.results?.ok === false && this.bail) ||
      this.parser.bailedOut ||
      this.results?.bailout
    if (this.results) this.results.ok = false
    if (this.parent) {
      this.parent.threw(er, extra, true)
    } else if (!alreadyBailing) {
      // not already bailing out, so print the error as best we can
      if (!er.stack) {
        console.error(er)
      } else {
        /* c8 ignore start */
        const name = er.name || 'Error'
        /* c8 ignore stop */
        console.error('%s: %s', name, extra.message)
        console.error(diags(extra))
      }
    }
    // we are already bailing out, and this is the top level,
    // just make our way hastily to the nearest exit.
  }

  /**
   * returns true if the test has not as yet encountered any failures
   *
   * @group Test Reflection
   */
  passing() {
    return this.parser.ok && this.results?.ok !== false
  }
}

/**
 * JSON object representation of {@link @tapjs/core!counts.Counts}
 */
export interface CountsJSON {
  total: number
  pass: number
  fail?: number
  skip?: number
  todo?: number
  complete?: number
}

/**
 * Class representing a count of all the assertions in a test
 *
 * The {@link @tapjs/core!counts.Counts#complete} field is only used when counting tests and
 * tracking their completion status.
 *
 * @internal
 */
export class Counts {
  total: number = 0
  pass: number = 0
  fail: number = 0
  skip: number = 0
  todo: number = 0
  complete: number = 0
  constructor(c?: Counts | CountsJSON) {
    if (c) Object.assign(this, c)
  }
  toJSON(): CountsJSON {
    const c: CountsJSON = {
      total: this.total,
      pass: this.pass,
    }
    if (this.fail) c.fail = this.fail
    if (this.todo) c.todo = this.todo
    if (this.skip) c.skip = this.skip
    if (this.complete) c.complete = this.complete
    return c
  }
}

import { parseTestArgs, TestArgs } from './parse-test-args.js'
import {
  PromiseWithSubtest,
  TestBase,
  TestBaseOpts,
} from './test-base.js'

/**
 * A very minimal Test class with no plugins, which can be used in tap tests.
 *
 * It is essentially just the TestBase class, but automatically starting in the
 * constructor, and with a .test() method so that it can be used somewhat like
 * a "normal" Test instance.
 *
 * The reason that this method does not live on TestBase itself is that it
 * would make it more awkward to define on the Test class, with all its plugins
 * and extensions.
 *
 * Only useful if you want a Test without any plugins, for some reason.
 */
export class Minimal extends TestBase {
  constructor(opts: TestBaseOpts) {
    super(opts)
    if (!this.parent) this.runMain(() => {})
  }
  /**
   * Spawn a subtest that is also a {@link @tapjs/core!minimal.Minimal}
   */
  test(
    name: string,
    extra: TestBaseOpts,
    cb: (t: Minimal) => any
  ): PromiseWithSubtest<Minimal>
  test(
    name: string,
    cb: (t: Minimal) => any
  ): PromiseWithSubtest<Minimal>
  test(
    extra: TestBaseOpts,
    cb: (t: Minimal) => any
  ): PromiseWithSubtest<Minimal>
  test(cb: (t: Minimal) => any): PromiseWithSubtest<Minimal>
  test(
    ...args: TestArgs<Minimal, TestBaseOpts>
  ): PromiseWithSubtest<Minimal> {
    const extra = parseTestArgs<Minimal, TestBaseOpts>(...args)
    return this.sub(
      Minimal,
      extra,
      this.test
    ) as PromiseWithSubtest<Minimal>
  }
}

import { Extra, MessageExtra } from './index.js'

/**
 * Normalize the {@link @tapjs/core!index.MessageExtra} arguments to test
 * assertion methods
 */
export const normalizeMessageExtra = (
  defaultMessage: string,
  [message, extra]: MessageExtra
): [string, Extra] => {
  if (message && typeof message === 'object') {
    return [defaultMessage, message]
  }

  return [message || defaultMessage, extra || {}]
}

import type { Base, BaseOpts } from './base.js'

import { TestOpts } from '@tapjs/test'

export type Opts = Exclude<BaseOpts, 'parent'> & { parent?: any }

/**
 * Argument signatures that may be passed to a subtest method.
 *
 * If a callback is not provided, then the test is marked as `todo`.
 */
export type TestArgs<T extends Base, O extends Opts = Opts> =
  | []
  | [name: string]
  | [cb: ((t: T) => any) | false]
  | [extra: O]
  | [name: string | number, cb: ((t: T) => any) | false]
  | [name: string | number, extra: O]
  | [extra: O, cb: ((t: T) => any) | false]
  | [
      name: string | number,
      extra: O,
      cb: false | ((t: T) => any),
      defaultName?: string
    ]

/**
 * Normalize the arguments provided to a subtest method
 */
export const parseTestArgs = <T extends Base, O extends Opts = Opts>(
  ...args: TestArgs<T, O>
): O => {
  let name: string | null | undefined = undefined
  let extra: O | null | undefined = undefined
  let cb: ((t: T) => any) | null | undefined = undefined

  // this only works if it's literally the 4th argument.
  // used internally.
  const defaultName = args[3] || ''

  for (let i = 0; i < 3 && i < args.length; i++) {
    const arg = args[i]
    if (
      name === undefined &&
      (typeof arg === 'string' || typeof arg === 'number')
    ) {
      name = '' + arg
    } else if (arg && typeof arg === 'object') {
      extra = arg
      if (name === undefined) name = null
    } else if (typeof arg === 'function') {
      if (extra === undefined) extra = {} as O
      if (name === undefined) name = null
      cb = arg as (t: T) => any
    } else if (arg === false) {
      // it's handy while developing to put a ! in front of a
      // function to temporarily make a test todo
      continue
    } else if (typeof arg !== 'undefined') {
      throw new TypeError('invalid test argument: ' + typeof arg)
    }
  }

  if (!extra) extra = {} as O

  const bex = extra as BaseOpts
  if (!cb && defaultName !== '/dev/stdin') {
    bex.todo = bex.todo || true
  }

  if (!name && bex.name) name = bex.name

  if (!name && cb && cb.name) name = cb.name

  name = name || defaultName
  bex.name = name
  const opts = extra as TestOpts
  opts.cb = cb || todoCb
  return opts as O
}

const todoCb = () => {
  throw new Error('callback called for TODO test')
}

/**
 * grab some basic process stuff safely at startup
 *
 * @module
 */
/**
 * A reference to the global `process` object, if available
 */
export const proc =
  typeof process === 'object' && process ? process : undefined
/**
 * A reference to `process.argv`, if available
 */
export const argv = proc?.argv || []
/**
 * A reference to `process.cwd()`, if available. Note that this is not
 * updated if `process.chdir()` is called.
 */
export const cwd = proc?.cwd?.() || '.'
/**
 * A reference to `process.env`, if available.
 */
export const env = proc?.env || {}

import { Base, TapBaseEvents } from './base.js'

import { ProcessInfo, WithExternalID } from '@tapjs/processinfo'
import {
  ChildProcess,
  ChildProcessByStdio,
  IOType,
  SpawnOptions,
  StdioOptions,
} from 'node:child_process'
import { basename } from 'node:path'
import { Readable, Stream, Writable } from 'node:stream'
import { format } from 'node:util'
import { Extra } from './index.js'
import { TestBaseOpts } from './test-base.js'
import { throwToParser } from './throw-to-parser.js'

/**
 * A child process that is known to have a stdout stream
 */
export type ChildProcessWithStdout = ChildProcessByStdio<
  null | Writable,
  Readable,
  null | Readable
>

const hasStdout = (p: ChildProcess): p is ChildProcessWithStdout =>
  !!p.stdout

/**
 * Events emitted by the {@link @tapjs/core!spawn.Spawn} class
 */
export interface SpawnEvents extends TapBaseEvents {
  /**
   * Emitted immediately before the child process is spawned. If the
   * options are mutated, then the changes *will* take effect.
   */
  preprocess: [WithExternalID<SpawnOptions>]
  /**
   * Emitted immediately after the child process is spawned, providing
   * the actual `ChildProcess` object as an argument
   */
  process: [ChildProcessWithStdout]
}

/**
 * Options that may be provided to the {@link @tapjs/core!spawn.Spawn} class
 */
export interface SpawnOpts extends TestBaseOpts {
  cwd?: string
  command?: string
  args?: string[]
  /**
   * Passed to child_process.spawn's 'stdio' option
   *
   * No matter what is specified here, stdout is *always* set to 'pipe',
   * and stdio[3] is *always* set to 'ipc', because TAP uses these internally
   * to communicate test results and timeout, respectively.
   *
   * So, this is only to set the behavior of stdin and stderr.
   */
  stdio?: StdioOptions
  env?: { [k: string]: string } | NodeJS.ProcessEnv
  exitCode?: number | null
  signal?: string | null
  /**
   * Used for tracking the test process for tap --changed etc
   * Typically set to the test name. Unlikely that you should
   * ever set this, outside of tap itself.
   */
  externalID?: string
}

/**
 * Class representing a spawned TAP process
 *
 * Instantiated by `t.spawn()`, typically.
 *
 * @internal
 */
export class Spawn extends Base<SpawnEvents> {
  declare options: SpawnOpts
  cwd: string
  command: string
  args: string[]
  stdio: [
    IOType | Stream | number | null | undefined,
    'pipe',
    IOType | Stream | number | null | undefined,
    'ipc'
  ]
  env: { [k: string]: string } | NodeJS.ProcessEnv
  proc: null | ChildProcess = null
  cb: null | (() => void) = null
  externalID?: string

  // doesn't have to be cryptographically secure, just a gut check
  #childKey: string = String(Math.random())

  #timedOut?: { expired?: string }

  #childId: string

  constructor(options: SpawnOpts) {
    // figure out the name before calling super()
    const command = options.command
    if (!command) {
      throw new TypeError('no command provided for t.spawn()')
    }
    const cwd =
      typeof options.cwd === 'string' ? options.cwd : process.cwd()
    const args = options.args || []

    options.name = options.name || Spawn.procName(cwd, command, args)
    super(options)

    this.externalID = options.externalID
    this.cwd = cwd
    this.command = command
    this.args = args
    if (options.stdio) {
      if (typeof options.stdio === 'string') {
        this.stdio = [options.stdio, 'pipe', options.stdio, 'ipc']
      } else {
        const [stdin, _, stderr] = options.stdio
        /* c8 ignore start */
        if (stdin === 'ipc' || stderr === 'ipc') {
          throw new Error(
            'cannot spawn subtest with ipc in stdin or stderr'
          )
        }
        /* c8 ignore stop */
        this.stdio = [stdin, 'pipe', stderr, 'ipc']
      }
    } else {
      this.stdio = [0, 'pipe', 2, 'ipc']
    }

    const env = options.env || process.env
    this.#childId = String(options.childId || env.TAP_CHILD_ID || '0')
    this.env = {
      ...env,
      TAP_CHILD_ID: this.#childId,
      TAP: '1',
      TAP_BAIL: this.bail ? '1' : '0',
      TAP_CHILD_KEY: this.#childKey,
    }
  }

  endAll() {
    if (this.proc) {
      this.proc.kill('SIGKILL')
      this.parser.abort('test unfinished')
    }
    this.#callCb()
  }

  #callCb() {
    if (this.cb) {
      this.cb()
    }
    this.cb = null
  }

  main(cb: () => void) {
    this.cb = cb
    this.setTimeout(this.options.timeout || 0)

    this.parser.on('comment', c => {
      const tomatch = c.match(/# timeout=([0-9]+)\n$/)
      if (tomatch) {
        this.setTimeout(+tomatch[1])
      }
    })

    this.env.TAP_JOB_ID = String(this.options.jobId || 0)
    const options = {
      cwd: this.cwd,
      env: this.env,
      stdio: this.stdio,
      externalID: this.externalID,
    }
    this.parent?.emit('spawn', this)

    this.emit('preprocess', options)
    const proc = (this.proc = ProcessInfo.spawn(
      this.command,
      this.args,
      options
    ))
    /* c8 ignore start */
    if (!hasStdout(proc)) {
      return this.threw(
        'failed to open child process stdout',
        this.options
      )
    }
    /* c8 ignore stop */
    proc.stdout.pipe(this.parser)

    proc.on('message', msg => {
      const m = msg as {
        key: string
        child: string
        setTimeout: number
      }
      if (
        !!msg &&
        typeof msg === 'object' &&
        m.key === this.#childKey &&
        m.child === this.#childId
      ) {
        this.setTimeout(m.setTimeout)
        return
      }
      this.comment(...(Array.isArray(msg) ? msg : [msg]))
    })

    proc.on('close', (code, signal) => {
      this.#onprocclose(code, signal)
    })
    proc.on('error', er => this.threw(er))
    this.emit('process', proc)
  }

  comment(...args: any[]) {
    const body = format(...args)
    const message =
      ('# ' + body.split(/\r?\n/).join('\n# ')).trim() + '\n'
    // it's almost impossible to send a message that will arrive
    // AFTER the stdout closes, as this only happens when the worker
    // thread closes, but it is theoretically possible, since messages
    // are asynchronous.
    /* c8 ignore start */
    if (this.parser.results) {
      if (this.parent && !this.parent.results) {
        this.parent.parser.write(message)
      } else {
        console.log(message.trimEnd())
      }
    } else {
      /* c8 ignore stop */
      this.parser.write(message)
    }
  }

  #onprocclose(code: number | null, signal: string | null) {
    this.options.exitCode = this.options.exitCode || code
    this.options.signal = this.options.signal || signal
    if (this.#timedOut) super.timeout(this.#timedOut)
    this.debug('SPAWN close %j %s', code, signal)

    // spawn closing with no tests is treated as a skip.
    if (
      this.results &&
      this.results.plan &&
      this.results.plan.skipAll &&
      !code &&
      !signal
    ) {
      this.options.skip =
        this.results.plan.skipReason || 'no tests found'
    }

    if (code || signal) {
      if (this.results) {
        this.results.ok = false
      }
      this.parser.ok = false
    }
    return this.#callCb()
  }

  timeout(options: { expired?: string } = { expired: this.name }) {
    // defer calling super.timeout() until we actually kill the process.
    this.#timedOut = options
    // try to send the timeout signal.  If the child test process is
    // using node-tap as the test runner, and not caught in a busy
    // loop, it will trigger a dump of outstanding handles and refs.
    // If that doesn't do the job, then we fall back to signals.
    // Unfortunately, termination signals on windows cannot be caught,
    // so this is the only way to get that information in most cases.
    const proc = this.proc
    if (proc) {
      try {
        proc.send(
          {
            tapAbort: 'timeout',
            key: this.#childKey,
            child: this.#childId,
            // If the process ends while/before sending this message,
            // then just ignore it. the eventual kills will be no-ops,
            // and since we're done with this process, the success here
            // doesn't matter.
            /* c8 ignore start */
          },
          () => {}
        )
      } catch {}
      /* c8 ignore stop */

      // this whole bit has to be ignored because there is no way to test
      // signals on Windows without mocking to the point of irrelevance
      /* c8 ignore start */
      const t = setTimeout(() => {
        // try to give it a chance to note the timeout and report handles
        try {
          proc.kill('SIGALRM')
        } catch (er) {}
        const t = setTimeout(() => {
          const { signal, exitCode } = this.options
          if (!signal && exitCode === undefined) {
            // that didn't work, use forceful termination
            proc.kill('SIGKILL')
          }
        }, 500)
        if (t.unref) t.unref()
        proc.once('close', () => clearTimeout(t))
      }, 500)
      if (t.unref) t.unref()
      proc.once('close', () => clearTimeout(t))
      /* c8 ignore stop */
    }
  }

  threw(er: any, extra?: Extra): Extra | void | undefined {
    return throwToParser(this.parser, super.threw(er, extra))
  }

  static procName(cwd: string, command: string, args: string[]) {
    return (
      command === process.execPath
        ? basename(process.execPath) +
          ' ' +
          args
            .map(a =>
              a.indexOf(cwd) === 0
                ? './' +
                  a.substring(cwd.length + 1).replace(/\\/g, '/')
                : a
            )
            .join(' ')
            .trim()
        : command + ' ' + args.join(' ')
    ).replace(/\\/g, '/')
  }
}

/**
 * Module that provides the location of the `@tapjs/core` module
 * Abstracted so that it can be built in two different ways depending
 * on whether we're building for CJS or ESM.
 * @module
 */
// this form only works in the mjs.
// it'll be overwritten by the tap-dir-cjs.js output in cjs mode
import { basename, dirname } from 'path'
import { fileURLToPath } from 'url'
//@ts-ignore
const u = dirname(dirname(fileURLToPath(import.meta.url)))
// ignore this because we can't possibly import it from src anyway,
// but it feels right to be complete here.
/* c8 ignore start */
export const tapDir = basename(u) === 'dist' ? dirname(u) : u
/* c8 ignore stop */

import * as stack from '@tapjs/stack'
import type { Test } from '@tapjs/test'
import { isPromise } from 'is-actual-promise'
import { Minipass } from 'minipass'
import assert from 'node:assert'
import { relative } from 'node:path'
import { hrtime } from 'node:process'
import { Readable } from 'node:stream'
import { format } from 'node:util'
import { FinalResults, Result as ParserResult } from 'tap-parser'
import { Deferred } from 'trivial-deferred'
import { Base, BaseOpts } from './base.js'
import { esc } from './esc.js'
import { extraFromError } from './extra-from-error.js'
import { mainScript } from './main-script.js'
import { argv, cwd } from './proc.js'
import { Spawn } from './spawn.js'
import { Stdin } from './stdin.js'
import { Result, TestPoint } from './test-point.js'
import { Waiter } from './waiter.js'
import { Worker } from './worker.js'

import { IMPLICIT } from './implicit-end-sigil.js'
import {
  Extra,
  MessageExtra,
  TapBaseEvents,
  TapFile,
} from './index.js'
import { normalizeMessageExtra } from './normalize-message-extra.js'

const VERSION = 'TAP version 14\n'

/**
 * Options that can be passed to TestBase objects
 */
export interface TestBaseOpts extends BaseOpts {
  /**
   * The number of jobs to run in parallel. Defaults to 1
   */
  jobs?: number

  /**
   * Test function called when this Test is executed
   * This is usually not set on the extra object, but as an argument to
   * the `t.test(..)` method, just defined here so TS doesn't complain
   * when we reference it in the various flow control machinery.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  cb?: (...args: any[]) => any
}

const queueEmpty = <T extends TestBase>(t: T) =>
  t.queue.length === 0 ||
  (t.queue.length === 1 && t.queue[0] === VERSION)

/**
 * Sigil to put in the queue to signal the end of all things
 */
const EOF = Symbol('EOF')

export type { EOF }

/**
 * Entries in the {@link @tapjs/core!test-base.TestBase#queue} awaiting
 * processing
 */
export type QueueEntry =
  | string
  | TestPoint
  | Base
  | typeof EOF
  | Waiter
  | [method: string, ...args: any[]]
  | (() => any)

/**
 * the promise returned by t.test(), t.spawn(), etc.
 * If a subtest was not created (because of being marked skipped,
 * the parent having bailed out, etc.) then the `subtest` field
 * will be set to `null`.
 */
export interface PromiseWithSubtest<S extends Base>
  extends Promise<FinalResults | null> {
  subtest: S | null
}

/**
 * Events emitted by TestBase and inherited classes
 */
export interface TestBaseEvents extends TapBaseEvents {
  /**
   * Emitted when a subtest begins running
   *
   * @event
   */
  subtestStart: [p: Base]
  /**
   * Emitted when a subtest is completed and no longer active, but may
   * not yet have been processed by the parent test.
   *
   * @event
   */
  subtestEnd: [p: Base]
  /**
   * Emitted when a subtest begins to be processed.
   *
   * @event
   */
  subtestProcess: [p: Base]
  /**
   * Emitted when a subtest is added to its parent's management
   *
   * @event
   */
  subtestAdd: [p: Base]
  /**
   * Emitted whenever the test has an assertion result, with the minimal
   * `{ ok, message, extra }` result object.
   *
   * @event
   */
  result: [res: Result]
  /**
   * Emitted when the parser emits a result, with the full parser result
   * object
   *
   * @event
   */
  assert: [res: ParserResult]
  /**
   * Emitted when a child test is initiated that will process stdin
   * as a TAP stream
   *
   * @event
   */
  stdin: [s: Stdin]
  /**
   * Emitted when a child test is initiated that will process a subprocess
   * output as a TAP stream
   *
   * @event
   */
  spawn: [s: Spawn]
  /**
   * Emitted when a child test is initiated that will process a node
   * Worker thread's output as a TAP stream
   *
   * @event
   */
  worker: [w: Worker]
  /**
   * Emitted when a child tests is initiated that replays a .tap file.
   *
   * @event
   */
  tapFile: [tf: TapFile]
  /**
   * Emitted when the test is in an idle state, not waiting
   * for anything, with nothing in its queue. Used by the root
   * {@link @tapjs/core!tap.TAP} singleton to know when to automatically
   * terminate.
   *
   * @event
   */
  idle: []
}

/**
 * The TestBase class is the parent class of {@link @tapjs/test!index.Test},
 * and passed
 * to all plugins at instantiation time.
 *
 * This implements subtest functionality, TAP stream generation,
 * lifecycle events, and only the basic pass/fail assertion methods.
 *
 * All other features are added with plugins.
 */
export class TestBase extends Base<TestBaseEvents> {
  // NB: generated pluginified Test class needs to declare over this
  declare parent?: TestBase
  declare options: TestBaseOpts

  /**
   * Attached when the Test class is instantiated from a TestBase,
   * as a reference to the final plugged-in Test instance.
   * If TestBase is used directly (outside the context of a plugin)
   * or during plugin setup time, this will be undefined, so watch out.
   *
   * @group Test Reflection
   */
  t!: Test

  /**
   * A promise that resolves when the test is done.
   *
   * @group Internal Machinery
   */
  donePromise?: Promise<any> & {
    tapAbortPromise?: () => void
  }

  /**
   * The number of subtests to run in parallel, if allowed
   *
   * @group Test Lifecycle Management
   */
  jobs: number

  /**
   * Array of all subtests that have been added/scheduled,
   * and have not yet completed.
   *
   * @group Internal Machinery
   */
  subtests: Base[] = []
  /**
   * The pool of parallel tests currently in process
   *
   * @group Internal Machinery
   */
  pool: Set<Base> = new Set()
  /**
   * Queue of items awaiting processing. Can be any
   * {@link @tapjs/core!test-base.QueueEntry} item.
   *
   * @group Internal Machinery
   */
  queue: QueueEntry[] = [VERSION]
  /**
   * Function that will get this test as an argument when it is processed
   *
   * @internal
   *
   * @group Internal Machinery
   */
  cb?: (...args: any[]) => any

  /**
   * The count of all assertions made directly on this test.
   *
   * @group Test Reflection
   */
  count: number = 0

  /**
   * Set true when {@link @tapjs/core!test-base.TestBase#end} is called
   */
  ended: boolean = false

  /**
   * Show diagnostics for this test. A value of `null` means that
   * diagnostics will be shown only if the test is failing.
   */
  diagnostic: null | boolean = null

  #planEnd: number = -1
  #planAt?: stack.CallSiteLike
  #printedResult: boolean = false
  #endingAll: boolean = false
  #endingAllSub: boolean = false
  #explicitEnded: boolean = false
  #explicitPlan: boolean = false
  #promiseEnded: boolean = false
  #multiEndThrew: boolean = false
  #n: number = 0
  #noparallel: boolean = false
  #occupied: null | Waiter | Base = null
  // set to true if the end should be explicit
  #awaitingEnd: typeof IMPLICIT | boolean = false
  #pushedBeforeEnd: boolean = false
  #nextChildId: number = 1
  #currentAssert?: Function | ((..._: any) => any)
  #processing: boolean = false
  #doingStdinOnly: boolean = false
  #calledOnEOF: boolean = false
  #jobIds: Set<number>

  /**
   * Subtests that are currently in process.
   *
   * @group Internal Machinery
   */
  activeSubtests: Set<Base> = new Set()

  /**
   * Count of all asserts in this and all child tests,
   * excluding child test summary points
   *
   * @group Test Reflection
   */
  get assertTotals() {
    return this.counts
  }

  /**
   * true if the test has printed at least one TestPoint
   *
   * @group Test Reflection
   */
  get printedResult(): boolean {
    return this.#printedResult
  }

  /**
   * true if the test is currently waiting for something to finish
   *
   * @group Test Reflection
   */
  get occupied(): boolean {
    return !!this.#occupied
  }

  constructor(options: TestBaseOpts) {
    super(options)

    this.parser.on('result', r => {
      this.emit('assert', r)
    })

    this.jobs = (options.jobs && Math.max(options.jobs, 1)) || 1

    if (typeof options.diagnostic === 'boolean') {
      this.diagnostic = options.diagnostic
    }

    if (options.cb) {
      this.#setCB(options.cb)
    }
    this.#jobIds = new Set()
  }

  #setCB<T extends TestBase>(this: T, cb: (t: T) => any) {
    this.cb = (...args: any[]) =>
      this.hook.runInAsyncScope(cb, this.t || this, ...args)
  }

  /**
   * immediately exit this and all parent tests with a TAP
   * Bail out! message.
   *
   * @group Test Lifecycle Management
   */
  bailout(message?: string) {
    if (this.parent && (this.results || this.ended)) {
      this.parent.bailout(message)
    } else {
      this.#process()
      message = message ? ' ' + ('' + esc(message)).trim() : ''
      message = message.replace(/[\r\n]/g, ' ')
      this.parser.write('Bail out!' + message + '\n')
    }
    this.#end(IMPLICIT)
    this.#process()
  }

  /**
   * output a TAP comment, formatted like console.log()
   *
   * If the test is currently awaiting a child test, it will be deferred
   * until after the child test completes.
   *
   * If the test is already completed, the comment will be emitted
   * on the parent, or if no parent is available, it will be printed
   * to standard output.
   */
  comment(...args: any[]) {
    const body = format(...args)
    const message =
      ('# ' + body.split(/\r?\n/).join('\n# ')).trim() + '\n'

    if (this.results || this.ended || this.#awaitingEnd) {
      // the fallback to console.log is a bit weird,
      // but the only alternative seems to be to just lose it.
      if (this.streamWritable) {
        super.write(message)
        this.parser.emit('comment', message.trim())
      } else if (this.parent) {
        this.parent.comment(...args)
      } else {
        console.log(message.trimEnd())
      }
    } else if (this.#occupied) {
      this.queue.push(message)
      this.#process()
    } else {
      this.parser.write(message)
    }
  }

  /**
   * Called when the test times out.
   * Options are passed as diagnostics to the threw() method
   *
   * @internal
   *
   * @group Internal Machinery
   */
  timeout(
    options: Extra & { expired?: string } = { expired: this.name }
  ) {
    options.expired = options.expired || this.name
    if (this.#occupied && this.#occupied instanceof Base) {
      this.#occupied.timeout(options)
    } else {
      super.timeout(options)
    }
    this.#end(IMPLICIT)
  }

  /**
   * Set TAP pragma configs to affect the behavior of the parser.
   * Only `strict` is supported by the parser.
   */
  pragma(set: { [k: string]: boolean }) {
    const p = Object.keys(set).reduce(
      (acc, i) => acc + 'pragma ' + (set[i] ? '+' : '-') + i + '\n',
      ''
    )
    this.queue.push(p)
    this.#process()
  }

  /**
   * Specify the number of Test Points expected by this test.
   * Outputs a TAP plan line.
   *
   * @group Test Lifecycle Management
   */
  plan(n: number, comment?: string): void
  plan(n: number, comment: string, implicit: typeof IMPLICIT): void
  plan(n: number, comment?: string, implicit?: typeof IMPLICIT) {
    if (this.bailedOut) {
      return
    }

    if (this.#explicitPlan) {
      throw new Error('Cannot set plan more than once')
    }
    this.#explicitPlan = implicit !== IMPLICIT
    if (this.#explicitPlan) {
      this.#planAt = stack.at(this.plan)
    }

    if (this.#planEnd !== -1) {
      throw new Error('Cannot set plan after test has ended')
    }

    if (typeof n !== 'number' || n < 0 || n !== Math.floor(n)) {
      throw new TypeError('plan must be a non-negative integer')
    }

    // Cannot get any tests after a trailing plan, or a plan of 0
    const ending = this.count !== 0 || n === 0

    if (n === 0 && comment && !this.options.skip) {
      this.options.skip = comment
    }

    this.#planEnd = n
    comment = comment ? ' # ' + esc(comment.trim()) : ''
    this.queue.push('1..' + n + comment + '\n')

    if (ending) {
      this.#end(IMPLICIT)
    } else {
      this.#process()
    }
  }

  /**
   * A passing (ok) Test Point.
   *
   * @group Assertion Methods
   */
  pass(...[msg, extra]: MessageExtra) {
    this.currentAssert = this.pass
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('(unnamed test)', args)
    this.#printResult(true, ...me)
    return true
  }

  /**
   * A failing (not ok) Test Point
   *
   * @group Assertion Methods
   */
  fail(...[msg, extra]: MessageExtra) {
    this.currentAssert = this.fail
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('(unnamed test)', args)
    this.#printResult(false, ...me)
    return !!(me[1].todo || me[1].skip)
  }

  /**
   * The current assertion being processed. Set at the start of all
   * assertions, and used for calculating stack traces.
   *
   * @group Internal Machinery
   */
  get currentAssert() {
    return this.#currentAssert
  }
  set currentAssert(
    fn: undefined | Function | ((...a: any[]) => any)
  ) {
    if (!this.#currentAssert && typeof fn === 'function') {
      this.#currentAssert = fn
    }
  }

  // apply flags from our options onto an Extra or TestOpts object
  #inheritFlags(extra: Extra) {
    const inheritedFlags = [
      'bail',
      'debug',
      'passes',
      'failTodo',
      'failSkip',
    ] as const
    for (const k of inheritedFlags) {
      if (
        extra[k] === undefined &&
        typeof this.options[k] === 'boolean'
      ) {
        extra[k] = this.options[k]
      }
    }
  }

  /**
   * Print a Test Point.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  #printResult(
    ok: boolean,
    message: string,
    extra: Extra,
    front: boolean = false
  ) {
    this.currentAssert = this.#printResult
    this.#printedResult = true

    const n = this.count + 1
    const fn = this.currentAssert
    this.#currentAssert = undefined

    if (this.#planEnd !== -1 && n > this.#planEnd) {
      // prevent infinite regress of "plan exceeded" fails
      if (!this.passing()) return

      // the 'automatic end' can only occur with the root TAP object
      // and even then, pretty hard to trigger, since it would mean
      // going several turns of the event loop and hitting it at just
      // the right time before the process quits.
      const failMessage = this.#promiseEnded
        ? 'test assertion after Promise resolution'
        : this.#explicitEnded
        ? 'test assertion after end() was called'
        : this.#explicitPlan
        ? 'test assertion count exceeds plan'
        : /* c8 ignore start */
          'assertion after automatic end'
      /* c8 ignore stop */

      const er = new Error(failMessage, {
        cause: {
          test: this.name,
          plan: this.#planEnd,
        },
      })
      Error.captureStackTrace(er, fn)
      this.threw(er, extraFromError(er))
      return
    }

    if (extra.skip && this.options.failSkip) {
      extra.failedSkip = extra.skip
      delete extra.skip
      ok = false
    }
    if (extra.todo && this.options.failTodo) {
      extra.failedTodo = extra.todo
      delete extra.todo
      ok = false
    }

    const diagnostic =
      typeof extra.diagnostic === 'boolean'
        ? extra.diagnostic
        : typeof this.diagnostic === 'boolean'
        ? this.diagnostic
        : extra.skip || extra.todo
        ? false
        : !ok

    if (diagnostic) {
      extra.diagnostic = true
    }

    if (extra.at === null) {
      delete extra.at
      delete extra.stack
    } else if (
      typeof extra.stack === 'string' &&
      extra.stack &&
      !extra.at
    ) {
      const parsed = stack.parseStack(extra.stack)
      extra.at = parsed[0]
      extra.stack = parsed.map(c => String(c) + '\n').join('')
    } else if (!extra.at && typeof fn === 'function') {
      const showStack = !ok && !extra.skip && !extra.todo
      const showAt = showStack || extra.diagnostic === true
      if (showAt) {
        const st = stack.capture(80, fn)
        extra.at = st[0]
        if (showStack) {
          extra.stack = st.map(c => String(c)).join('\n')
        }
      }
    }

    this.count = n
    message = message + ''
    const res: Result = { ok, message, extra }

    this.#inheritFlags(extra)
    const tp = new TestPoint(ok, message, extra)

    // when we jump the queue, skip an extra line
    if (front) {
      tp.message = tp.message.trimEnd() + '\n\n'
    }

    // push to the front when we are occupied by a waiter and have ended,
    // otherwise the relevant awaited assertion will be lost.
    if (
      this.#occupied &&
      this.#occupied instanceof Waiter &&
      this.#awaitingEnd
    ) {
      front = true
    }

    if (front) {
      if (extra.tapChildBuffer || extra.tapChildBuffer === '') {
        this.#writeSubComment(tp)
        this.parser.write(extra.tapChildBuffer)
      }
      this.emit('result', res)
      this.parser.write(tp.ok + ++this.#n + tp.message)
      if (this.bail && !ok && !extra.skip && !extra.todo) {
        this.parser.write('Bail out! ' + message + '\n')
      }
    } else {
      this.queue.push(tp)
      if (this.bail && !ok && !extra.skip && !extra.todo) {
        this.queue.push('Bail out! ' + message + '\n')
      }
    }

    this.#process()
    if (this.#planEnd === this.count) {
      if (!this.#awaitingEnd && !this.#occupied) this.#end(IMPLICIT)
      else this.#awaitingEnd ||= IMPLICIT
    }
    this.#process()
  }

  /**
   * Explicitly mark the test as completed, outputting the TAP plan line if
   * needed.
   *
   * This is not required to be called if the test function returns a promise,
   * or if a plan is explicitly declared and eventually fulfilled.
   *
   * @group Test Lifecycle Management
   */
  end(implicit?: typeof IMPLICIT): this {
    this.#end(implicit)
    return this
  }

  /**
   * The leading `# Subtest` comment that introduces a child test
   *
   * @internal
   *
   * @group Internal Machinery
   */
  #writeSubComment<T extends TestPoint | Base>(p: T) {
    // name will generally always be set
    /* c8 ignore start */
    const stn = p.name ? ': ' + esc(p.name) : ''
    /* c8 ignore stop */
    const comment = `# Subtest${stn}\n`
    this.parser.write(comment)
  }
  // end TAP otput generating methods

  // flow control methods

  /**
   * Await the end of a Promise before proceeding.
   * The supplied callback is called with the Waiter object.
   *
   * This is internal, used in some plugins when a promise must be awaited
   * before proceeding. In normal test usage, it's usually best to simply use
   * an async test function and `await` promises as normal.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  waitOn(
    promise: Promise<any | void>,
    cb?: (w: Waiter) => any,
    expectReject: boolean = false
  ): Promise<void> {
    const w = new Waiter(
      promise,
      w => {
        assert.equal(this.#occupied, w)
        if (cb) cb(w)
        this.#occupied = null
        this.#process()
      },
      expectReject
    )
    // if the top of the queue is still the version line, we come
    // in after that. otherwise, it should be the next thing processed.
    if (this.queue[0] === VERSION) {
      this.queue.shift()
      this.queue.unshift(VERSION, w)
    } else {
      this.queue.unshift(w)
    }
    this.#process()
    return w.promise
  }

  #end(implicit?: typeof IMPLICIT) {
    if (this.#doingStdinOnly && implicit !== IMPLICIT) {
      throw new Error('cannot explicitly end while in stdinOnly mode')
    }
    this.debug('END %s implicit=%j', this.name, implicit === IMPLICIT)
    if (this.ended && implicit === IMPLICIT) {
      this.debug('already ended, ignore implicit end')
      return
    }

    // If onbeforeend returns a Promise, then wait for it to finish.
    const obe = this.onbeforeend
    if (obe && !this.#pushedBeforeEnd) {
      this.debug('push obe')
      this.#pushedBeforeEnd = true
      if (!queueEmpty(this) || this.#occupied) {
        this.queue.push(obe)
        this.#process()
      } else {
        const ret = obe()
        if (isPromise(ret)) {
          // this will make the next section return this.#process()
          this.waitOn(ret)
        }
      }
    }

    // beyond here we have to be actually done with things, or else
    // the semantic checks on counts and such will be off.
    if (!queueEmpty(this) || this.#occupied) {
      this.debug(
        '#end: queue not empty, or occupied',
        this.#awaitingEnd,
        this.#occupied,
        this.queue
      )
      if (!this.#awaitingEnd) {
        this.#awaitingEnd = implicit === IMPLICIT ? IMPLICIT : true
      }
      return this.#process()
    }

    if (implicit !== IMPLICIT) {
      if (this.#explicitEnded && this.#awaitingEnd !== true) {
        this.debug('multi-end')
        if (!this.#multiEndThrew) {
          this.#multiEndThrew = true
          const er = new Error(
            'test end() method called more than once'
          )
          Error.captureStackTrace(er, this.#currentAssert || this.end)
          er.cause = {
            test: this.name,
          }
          this.threw(er)
        }
        return
      }
      this.debug('set #explicitEnded=true')
      // switch from awaiting to processing the explicit end() call.
      this.#explicitEnded = true
      this.#awaitingEnd = false
    }

    if (this.#planEnd === -1 && !this.#doingStdinOnly) {
      this.debug('END(%s) implicit plan', this.name, this.count)
      const c =
        this.count === 0 && !this.parent ? 'no tests found' : ''
      this.plan(this.count, c, IMPLICIT)
    } else if (!this.ended && this.#planEnd !== -1) {
      const count = this.#endingAllSub ? this.count - 1 : this.count
      if (this.#planEnd > count) {
        this.fail(`test count(${count}) != plan(${this.#planEnd})`, {
          found: count,
          wanted: this.#planEnd,
          at: this.#planAt,
          stack: '',
        })
      }
    }

    this.debug('set ended=true')
    this.ended = true
    this.queue.push(EOF)
    this.#process()
  }

  /**
   * The full name of the test, starting with the main script name,
   * and including all parent names.
   */
  get fullname(): string {
    const main = (
      mainScript('TAP') +
      ' ' +
      argv.slice(2).join(' ')
    ).trim()
    const n: string[] = [
      (this.parent
        ? this.parent.fullname
        : main === 'TAP'
        ? 'TAP'
        : relative(cwd, main).replace(/\\/g, '/')
      ).trim(),
    ]
    // tests will generally always have a name
    /* c8 ignore start */
    const myName = (this.name || '').trim()
    /* c8 ignore stop */
    if (myName) n.push(myName)
    return n.join(' > ')
  }

  #process() {
    if (this.#processing) {
      return this.debug(' < already processing')
    }
    this.debug('\nPROCESSING(%s)', this.name, this.queue.length)
    this.#processing = true

    while (!this.#occupied) {
      const p = this.queue.shift()
      if (!p) {
        this.debug('> end of queue')
        break
      }
      if (p instanceof Base) {
        this.debug('> subtest in queue', p.name)
        this.#processSubtest(p)
      } else if (p === EOF) {
        this.debug(' > EOF', this.name)
        if (!this.#calledOnEOF) {
          this.#calledOnEOF = true
          // I AM BECOME EOF, DESTROYER OF STREAMS
          this.debug('call onEOF', this.name)
          const eofRet = this.onEOF()
          if (isPromise(eofRet)) {
            this.debug('onEOF is promise')
            this.waitOn(eofRet, w => {
              if (w.rejected) {
                // threw on the parent, since we're EOFing already
                this.debug('eofRet reject', w.value)
                this.comment('error thrown in teardown')
                this.threw(w.value)
              }
              this.queue.push(EOF)
              this.#process()
            })
            break
          }
        }
        this.debug('eof end parser', this.name)
        this.parser.end()
      } else if (p instanceof TestPoint) {
        this.debug(' > TESTPOINT')
        if (p.extra.tapChildBuffer || p.extra.tapChildBuffer === '') {
          this.#writeSubComment(p)
          this.parser.write(p.extra.tapChildBuffer)
        }
        this.emit('res', p.res)
        this.parser.write(p.ok + ++this.#n + p.message)
      } else if (typeof p === 'string') {
        this.debug(' > STRING')
        this.parser.write(p)
      } else if (p instanceof Waiter) {
        p.ready = true
        this.#occupied = p
        p.finish()
      } else if (typeof p === 'function') {
        this.debug(' > FUNCTION')
        const ret = p()
        if (isPromise(ret)) {
          this.waitOn(ret)
        }
      } else if (Array.isArray(p)) {
        this.debug(' > METHOD')
        const m = p.shift() as keyof this
        const fn = this[m] as (...a: any[]) => any
        if (typeof fn !== 'function') {
          this.debug(
            ' > weird method not found in queue??',
            m,
            typeof this[m]
          )
          continue
        }
        const ret = fn.call(this, ...p)
        if (isPromise(ret)) {
          // returned promise
          ret.then(
            () => {
              this.#processing = false
              this.#process()
            },
            (er: unknown) => {
              this.#processing = false
              this.threw(er)
            }
          )
          return
        }
        /* c8 ignore start */
      } else {
        throw new Error('weird thing got in the queue')
      }
      /* c8 ignore stop */
    }

    // waiters are serial
    const ow = !!this.#occupied && this.#occupied instanceof Waiter
    while (!this.#noparallel && !ow && this.pool.size < this.jobs) {
      const p = this.subtests.shift()
      if (!p) {
        break
      }

      if (!p.buffered) {
        this.#noparallel = true
        break
      }

      this.debug('start subtest', p)
      this.activeSubtests.add(p)
      this.pool.add(p)
      this.emit('subtestStart', p)
      if (this.bailedOut) {
        this.#onBufferedEnd(p)
      } else {
        // ts doesn't know this will always be set at this point
        /* c8 ignore start */
        p.options.jobId = this.#getJobId(p.options.childId || 0)
        /* c8 ignore stop */
        p.runMain(() => this.#onBufferedEnd(p))
      }
    }

    this.debug(
      'done processing',
      this.queue,
      this.#occupied,
      this.#awaitingEnd
    )
    this.#processing = false

    // just in case any tests ended, and we have sync stuff still
    // waiting around in the queue to be processed
    if (!this.#occupied && this.queue.length) {
      this.#process()
    } else if (this.idle) {
      this.debug(
        'idle after #process',
        this.#awaitingEnd,
        this.#occupied
      )
      if (this.#awaitingEnd) {
        this.debug('awaited end in process', this.#awaitingEnd)
        this.#end(
          this.#awaitingEnd === IMPLICIT ? IMPLICIT : undefined
        )
      }
      // the root tap runner uses this event to know when it is safe to
      // automatically end.
      this.emit('idle')
    }
  }

  // virtual "worker" id, even though it's just a pool
  #getJobId(childId: number = 0) {
    let j = childId % this.jobs
    const start = j
    while (this.#jobIds.has(j)) {
      j = (j + 1) % this.jobs
      // impossible because math
      /* c8 ignore start */
      if (j === start) return 0
      /* c8 ignore stop */
    }
    this.#jobIds.add(j)
    return j
  }

  /**
   * True if the test is currently in an idle state
   */
  get idle() {
    return (
      !this.#processing &&
      queueEmpty(this) &&
      !this.pool.size &&
      !this.subtests.length &&
      !this.#occupied &&
      // if we have a plan, don't autoend until the plan is complete.
      (this.#planEnd === -1 || this.count === this.#planEnd)
    )
  }

  #onBufferedEnd<T extends Base>(p: T) {
    // ignore ends that come in after we've already aborted
    if (this.ended && this.#endingAll) return
    this.#jobIds.delete(p.options.jobId || 0)
    p.results = p.results || new FinalResults(true, p.parser)
    p.readyToProcess = true
    const to = p.options.timeout
    const dur =
      to && p.passing()
        ? Number(hrtime.bigint() - p.start) / 1e6
        : null
    if (dur && to && dur > to) {
      p.timeout()
    } else {
      p.setTimeout(0)
    }
    this.debug(
      '%s.#onBufferedEnd',
      this.name,
      p.name,
      p.results.bailout
    )
    p.options.tapChildBuffer = p.output || ''
    p.options.stack = ''
    if (p.time) p.options.time = p.time
    if (this.#occupied === p) this.#occupied = null
    this.pool.delete(p)
    this.activeSubtests.delete(p)
    p.deferred?.resolve(p.results)
    this.emit('subtestEnd', p)
    this.#process()
  }

  #onIndentedEnd<T extends Base>(p: T) {
    // ignore ends that come in after we've already aborted
    if (this.ended && this.#endingAll) return
    this.debug('onIndentedEnd', p.name)
    this.emit('subtestProcess', p)
    // we'll generally already have a results by now, but just to be sure
    /* c8 ignore start */
    p.results = p.results || new FinalResults(true, p.parser)
    /* c8 ignore stop */
    this.#noparallel = false
    const sti = this.subtests.indexOf(p)
    if (sti !== -1) this.subtests.splice(sti, 1)
    p.readyToProcess = true
    p.options.time = p.time
    const to = p.options.timeout
    const now = hrtime.bigint()
    const dur = to && p.passing() ? Number(now - p.start) / 1e6 : null
    if (dur && to && dur > to) {
      p.timeout()
    } else {
      p.setTimeout(0)
    }
    this.debug('#onIndentedEnd %s(%s)', this.name, p.name)
    this.#occupied = null
    this.debug('OIE(%s) >shift into queue', this.name, this.queue)
    p.options.stack = ''

    if (!p.silent || !p.passing()) {
      if (p.silent) p.options.tapChildBuffer = p.output
      this.#printResult(p.passing(), p.name, p.options, true)
    }

    this.debug('OIE(%s) shifted into queue', this.name, this.queue)
    this.activeSubtests.delete(p)
    p.deferred?.resolve(p.results)
    this.emit('subtestEnd', p)
    this.#process()
  }

  /**
   * The main function that starts a test running. Generally no need
   * to call this directly.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  main(cb: () => void) {
    if (typeof this.options.timeout === 'number') {
      this.setTimeout(this.options.timeout)
    }

    const done = (er?: Error) => {
      this.donePromise = undefined
      if (er) this.threw(er)

      if (this.results || this.bailedOut) cb()
      else
        this.ondone = () => {
          super.ondone()
          cb()
        }
    }

    // This bit of overly clever line-noise wraps the call to user-code
    // in a try-catch. We can't rely on the domain for this yet, because
    // the 'end' event can trigger a throw after the domain is unhooked,
    // but before this is no longer the official "active test"
    const ret = (() => {
      if (!this.cb) return
      try {
        return this.cb(this.t || this)
      } catch (er: any) {
        if (!er || typeof er !== 'object') {
          er = { error: er, at: null }
        }
        er.tapCaught = 'testFunctionThrow'
        this.threw(er)
      }
    })()

    if (ret && ret.then) {
      this.donePromise = Object.assign(ret, {
        tapAbortPromise: done,
      })
      ret.then(
        () => {
          this.debug(
            ' > implicit end for promise?',
            this.#occupied,
            this.queue,
            this.#explicitPlan,
            this.#awaitingEnd
          )
          // the promise has ended
          // If we had an explicit plan that is now satisfied but was waiting
          // for the promise to resolve, or if there was no explicit plan, end
          // the test.
          this.#promiseEnded = true
          if (
            // not already ended
            !this.ended &&
            ((!this.#explicitPlan && !this.#awaitingEnd) ||
              (this.#explicitPlan &&
                this.#awaitingEnd &&
                this.count === this.#planEnd)) &&
            !this.#occupied
          ) {
            // this should only be possible if an explicit end()
            // has been called, because the only other source of an
            // implicit end is this function right here.
            this.#end(
              /* c8 ignore start */
              this.#awaitingEnd === IMPLICIT ? IMPLICIT : undefined
              /* c8 ignore stop */
            )
          } else {
            this.debug('await implicit end')
            this.#awaitingEnd = IMPLICIT
          }
          done()
        },
        (er: any) => {
          if (!er || typeof er !== 'object') {
            er = { error: er, at: null }
          }
          er.tapCaught = 'returnedPromiseRejection'
          done(er)
        }
      )
    } else {
      done()
    }

    this.debug('MAIN post', this.name)
  }

  #processSubtest<T extends Base>(p: T) {
    this.debug('processSubtest', p.name)
    this.debug(' > subtest')
    this.#occupied = p
    if (!p.buffered) {
      this.activeSubtests.add(p)
      this.emit('subtestStart', p)
      this.debug(' > subtest indented')
      p.pipe(this.parser, { end: false })
      if (!p.silent) this.#writeSubComment(p)
      this.debug('calling runMain', p.name)
      p.runMain(() => this.#onIndentedEnd(p))
    } else if (p.readyToProcess) {
      this.emit('subtestProcess', p)
      this.debug(' > subtest buffered, finished')
      // finished!  do the thing!
      this.#occupied = null
      if (!p.passing() || !p.silent) {
        this.#printResult(p.passing(), p.name, p.options, true)
      }
    } else {
      this.#occupied = p
      this.debug(' > subtest buffered, unfinished', p)
      // unfinished buffered test.
      // nothing to do yet, just leave it there.
      this.queue.unshift(p)
    }
  }

  /**
   * Parse stdin as the only tap stream (ie, not as a child test)
   * If used, then no other subtests or assertions are allowed.
   *
   * @group Subtest Methods
   */
  stdinOnly<T extends BaseOpts>(
    extra?: T & { tapStream?: Readable | Minipass<string | Buffer> }
  ) {
    const stream = (extra?.tapStream ?? process.stdin) as Minipass
    /* c8 ignore start */
    if (!stream) {
      throw new Error('cannot read stdin without stdin stream')
    }
    /* c8 ignore stop */

    if (
      this.queue.length !== 1 ||
      this.queue[0] !== VERSION ||
      this.#processing ||
      this.results ||
      this.#occupied ||
      this.pool.size ||
      this.subtests.length
    ) {
      throw new Error('Cannot use stdinOnly on a test in progress')
    }

    this.#doingStdinOnly = true
    this.queue.length = 0
    this.parser.on('child', p => {
      // pretend to be a rooted parser, so it gets counts.
      p.root = p
      const t = new Base({
        name: p.name,
        parent: this,
        parser: p,
        bail: p.bail,
        strict: p.strict,
        omitVersion: p.omitVersion,
        preserveWhitespace: p.preserveWhitespace,
        childId: this.#nextChildId++,
      })
      this.emit('subtestAdd', t)
      this.activeSubtests.add(t)
      this.emit('subtestStart', t)
      this.emit('subtestProcess', t)
      p.on('complete', () => {
        t.time = p.time
        this.activeSubtests.delete(t)
        this.emit('subtestEnd', t)
      })
    })
    stream.pipe(this.parser)
    stream.resume()
  }

  /**
   * Mount a subtest, using this Test object as a harness.
   * Exposed so that it can be used by some builtin plugins, but perhaps
   * the least convenient way imaginable to create subtests. Just use
   * `t.test()` to do that, it's much easier.
   *
   * @group Subtest Methods
   *
   * @internal
   */
  sub<T extends Base, O extends BaseOpts>(
    Class: { new (options: O): T },
    extra: O | TestBaseOpts = {},
    caller: (...a: any[]) => unknown = this.sub
  ): PromiseWithSubtest<T> {
    if (this.bailedOut) {
      return Object.assign(Promise.resolve(null), {
        subtest: null,
      })
    }

    if (this.results || this.ended) {
      const msg = this.#promiseEnded
        ? 'cannot create subtest after parent promise resolves'
        : this.#explicitEnded
        ? 'subtest after parent test end()'
        : this.#explicitPlan
        ? 'test count exceeds plan'
        : /* c8 ignore start */
          'cannot create subtest after parent test ends'
      /* c8 ignore stop */
      const er = new Error(msg)
      Error.captureStackTrace(er, caller)
      this.threw(er)
      return Object.assign(Promise.resolve(null), {
        subtest: null,
      })
    }

    extra.childId = this.#nextChildId++
    if (this.shouldSkipChild(extra)) {
      this.currentAssert = this.sub
      this.pass(extra.name || '', extra)
      return Object.assign(Promise.resolve(null), {
        subtest: null,
      })
    }

    extra.indent = '    '
    if (extra.buffered === undefined && !extra.silent) {
      extra.buffered = this.jobs > 1
    }

    extra.parent = this
    if (!extra.at && extra.at !== null) {
      const st = stack.capture(80, caller)
      extra.at = st[0]
      extra.stack = st.map(c => String(c)).join('\n')
    }
    extra.context = this.context

    this.#inheritFlags(extra)

    const t = new Class(extra as O)

    this.queue.push(t)
    this.subtests.push(t)
    this.emit('subtestAdd', t)
    // this would make its way here eventually anyway, but the
    // test bailing out might be waiting for its turn in the pool
    // to be processed, and bailout should happen ASAP.
    t.on('bailout', reason => this.bailout(reason))

    const d = new Deferred<FinalResults>()
    t.deferred = d
    this.#process()
    return Object.assign(d.promise, { subtest: t })
  }

  /**
   * Method called when an unrecoverable error is encountered in a test.
   *
   * Typically, in tests you would not call this, you'd just actually throw
   * an error.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  threw(
    er: any,
    extra?: Extra,
    proxy: boolean = false
  ): Extra | void | undefined {
    this.debug('TestBase.threw', this.name, er.message)
    // this can happen if a beforeEach throws.  capture the error here
    // and raise it once we've started the test officially.
    if (this.parent && !this.started) {
      this.cb = () => {
        this.threw(er)
        this.end()
      }
      return
    }

    // suppress the callsite for non-error throws, since
    // it'll always just be useless noise pointing back here.
    if (typeof er === 'string') {
      er = { message: er, at: null }
    }

    if (this.name && !proxy) {
      er.test = this.name
    }
    if (!proxy) {
      extra = extraFromError(er, extra)
    }
    this.debug('T.t call Base.threw', this.name, er, extra)
    const ended =
      !!this.results ||
      // should be impossible, when we hit the plan end, we end
      /* c8 ignore start */
      (this.#explicitPlan && this.count === this.#planEnd)
    /* c8 ignore stop */
    this.parser.ok = false
    const threwInfo = super.threw(er, extra, proxy, ended)

    // Handle the failure here, but only if we (a) don't have
    // results yet (indicating an end) and (b) are not currently
    // at the plan end (which would mean that any failure is
    // ignored to prevent infinite regress in "plan exceeded"
    // failures)
    if (!ended && threwInfo) {
      const msg = threwInfo.message as string
      extra ??= { at: null }
      if (this.parent && extra.test === this.name) {
        // remove extraneous indicator if it's already nested
        // in a TAP subtest
        delete extra.test
      }
      if (extra.error === msg) {
        delete extra.error
      }
      if (!extra.stack && er.stack && typeof er.stack === 'string') {
        // trim off the first line if it looks like the standard
        // error `Name: message` line.
        /* c8 ignore start */
        const f = `${er.name || 'Error'}: ${er.message}\n`
        const st = er.stack.startsWith(f)
          ? er.stack.substring(f.length)
          : er.stack
        /* c8 ignore stop */
        const p = stack.parseStack(st)
        extra.at = p[0] || null
        extra.stack = p.map(c => String(c) + '\n').join('')
      }
      if (!extra.at && !extra.stack) extra.at = null
      this.fail(msg, extra)
      if (this.ended || this.#awaitingEnd) {
        this.ended = false
        this.#awaitingEnd = false
        this.end(IMPLICIT)
      }
    }
    if (this.#occupied && this.#occupied instanceof Waiter) {
      this.#occupied.abort(
        Object.assign(
          new Error('error thrown while awaiting Promise'),
          { thrown: er }
        )
      )
      this.#occupied = null
    }
    if (!proxy) {
      this.#end(IMPLICIT)
      this.#processing = false
    }

    this.#process()
    /* c8 ignore start */
    this.donePromise?.tapAbortPromise?.()
    /* c8 ignore stop */
  }

  /**
   * Method called when the parser encounters a bail out
   *
   * To listen to bailout events, listen to the
   * {@link @tapjs/core!base.TapBaseEvents#bailout} event:
   *
   * ```ts
   * t.on('bailout', message => {
   *   // test bailed out!
   * })
   * ```
   *
   * @internal
   *
   * @group Internal Machinery
   */
  onbail(message?: string) {
    super.onbail(message)
    this.#end(IMPLICIT)
    if (!this.parent) {
      this.endAll()
    } else {
      throw 'bailout'
    }
  }

  /**
   * Called when a test times out or bails out, or the process ends,
   * marking all currently active or queued subtests as incomplete.
   *
   * No need to ever call this directly, exposed so that it can be extended by
   * {@link @tapjs/core!spawn.Spawn} and {@link @tapjs/core!worker.Worker},
   * which have special behaviors that are required when a process hangs
   * indefinitely.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  endAll(sub: boolean = false) {
    if (this.bailedOut) return
    this.#endingAll = true
    this.#endingAllSub = sub

    // in the case of the root TAP test object, we might sometimes
    // call endAll on a bailing-out test, as the process is ending
    // In that case, we WILL have a this.occupied and a full queue
    // These cases are very rare to encounter in other Test objs tho
    this.#processing = true
    if (this.#occupied) {
      const p = this.#occupied

      if (p instanceof Waiter) p.abort(new Error('test unfinished'))
      else if (typeof (p as TestBase).endAll === 'function') {
        // first try to end explicitly, then endAll if that didn't work
        const pt = p as TestBase
        pt.endAll(true)
      } else p.parser.abort('test unfinished')
      this.#occupied = null
    } else if (sub) {
      this.#process()
      if (
        queueEmpty(this) &&
        (this.#planEnd === -1 || this.count < this.#planEnd)
      ) {
        const options = Object.assign({}, this.options)
        options.test = this.name
        this.fail('test unfinished', options)
      }
    }

    this.donePromise?.tapAbortPromise?.()

    for (let i = 0; i < this.queue.length; i++) {
      const p = this.queue[i]
      if (p instanceof Base && !p.readyToProcess) {
        const msg = `child test left in queue: ${p.name}`
        delete p.options.skip
        delete p.options.todo
        this.queue[i] = new TestPoint(false, msg, p.options)
        this.count++
      }
    }

    this.#processing = false
    this.#process()
    this.end(IMPLICIT)
    this.#process()
  }

  /**
   * Return true if the child test represented by the options object
   * should be skipped.  Extended by the `@tapjs/filter` plugin.
   *
   * @internal
   *
   * @group Internal Machinery
   */
  shouldSkipChild<O extends BaseOpts>(
    extra: O | TestBaseOpts | BaseOpts
  ): boolean {
    return !!(extra.skip || extra.todo)
  }
}

import { diags } from './diags.js'
import { esc } from './esc.js'
import { Extra } from './index.js'

/**
 * Very simple result object, just indicating pass/fail status,
 * message, and the {@link @tapjs/core!index.Extra} object provided to the
 * assertion method.
 */
export interface Result {
  ok: boolean
  message: string
  extra: Extra
}

/**
 * Object representing a single test point `ok` / `not ok` line
 */
export class TestPoint {
  ok: 'ok ' | 'not ok '
  name: string
  message: string
  extra: { [key: string]: any }
  res: Result

  constructor(
    ok: boolean,
    message: string,
    extra?: { [key: string]: any }
  ) {
    extra = extra || {}
    this.ok = ok ? 'ok ' : 'not ok '
    message = message
      .trim()
      .replace(/[\n\r]/g, ' ')
      .replace(/\t/g, '  ')
    this.res = { ok, message, extra }
    this.extra = extra
    this.name = message
    this.message = tpMessage(esc(this.name), extra)
  }
}

const tpMessage = (
  description: string,
  extra: { [k: string]: any }
): string => {
  let message = description ? ` - ${description}` : ''

  if (extra.skip) {
    message += ' # SKIP'
    if (typeof extra.skip === 'string') {
      message += ' ' + esc(extra.skip)
    }
  } else if (extra.todo) {
    message += ' # TODO'
    if (typeof extra.todo === 'string') {
      message += ' ' + esc(extra.todo)
    }
  } else if (extra.time) {
    message += ' # time=' + extra.time + 'ms'
  }

  // TODO: add # time if duration_ms in extra

  const diagYaml = extra.diagnostic ? '\n' + diags(extra) : ''
  message += diagYaml + '\n'

  return message
}

/**
 * Basically a fancy Deferred, wrapped around an existing promise, used by
 * the {@link @tapjs/core!test-base.TestBase#waitOn} method, and
 * tracked in the {@link @tapjs/core!test-base.TestBase#queue}.
 *
 * The callback function is called when it's been either resolved or
 * rejected. The Waiter internal promise is resolved if the wrapped
 * promise matches our expectation. The value member is the resolved
 * value or rejection error.
 */
export class Waiter {
  /**
   * Callback to call when the promise resolves, or null if not provided
   */
  cb: null | ((w: Waiter) => any)
  /**
   * whether or not this waiter is ready to process
   */
  ready: boolean = false
  /**
   * The resolved value, or the error that was raised on rejection
   */
  value: any = undefined
  /**
   * True if the promise resolved successfully
   */
  resolved: boolean = false
  /**
   * True if the promise rejected
   */
  rejected: boolean = false
  /**
   * Set when the Waiter's promise has either resolved or rejected
   */
  done: boolean = false
  finishing: boolean = false
  expectReject: boolean
  promise: Promise<void>
  resolve: null | ((value?: any) => void) = null

  constructor(
    promise: Promise<any | void>,
    cb: (w: Waiter) => any,
    expectReject: boolean = false
  ) {
    this.cb = cb
    this.expectReject = !!expectReject
    this.promise = new Promise<void>(res => (this.resolve = res))
    promise
      .then(value => {
        // promises should always resolve/reject at most one time.
        /* c8 ignore start */
        if (this.done) {
          return
        }
        /* c8 ignore stop */

        this.resolved = true
        this.value = value
        this.done = true
        this.finish()
      })
      .catch(er => this.reject(er))
  }

  /**
   * called when the promise rejects
   */
  reject(er: any) {
    // promises should always resolve/reject at most one time.
    /* c8 ignore start */
    if (this.done) {
      return
    }
    /* c8 ignore stop */

    this.value = er
    this.rejected = true
    this.done = true
    this.finish()
  }

  // TODO: consider AbortSignal maybe?
  /**
   * Tell the waiter to abandon the promise and stop waiting.
   * Called when tests time out or bail out.
   */
  abort(er: Error) {
    /* c8 ignore start */
    if (this.done) {
      return
    }
    /* c8 ignore stop */

    this.ready = true
    this.finishing = false
    this.done = true
    this.value = er
    // make it clear that this is a problem by doing
    // the opposite of what was requested.
    this.rejected = !this.expectReject
    return this.finish()
  }

  /**
   * Called when the waiter is ready, and processed by its owning
   * {@link @tapjs/core!test-base.TestBase}
   */
  finish() {
    if (this.ready && this.done && !this.finishing) {
      this.finishing = true
      this.cb?.(this)
      this.resolve?.()
    }
  }
}

import { Base, TapBaseEvents } from './base.js'
import { cwd, env } from './proc.js'

import { format } from 'node:util'
import { Worker as NodeWorker } from 'node:worker_threads'
import { FinalResults } from 'tap-parser'
import { Extra } from './index.js'
import { TestBaseOpts } from './test-base.js'
import { throwToParser } from './throw-to-parser.js'

/**
 * Events emitted by {@link @tapjs/core!worker.Worker} instances
 */
export interface WorkerEvents extends TapBaseEvents {
  process: [NodeWorker]
}

/**
 * Options that can be provided to a {@link @tapjs/core!worker.Worker}
 */
export interface WorkerOpts extends TestBaseOpts {
  /**
   * Data that will be available on `t.workerData` on the root
   * {@link @tapjs/core!tap.TAP} object in the worker thread.
   */
  workerData?: any
  /**
   * Environment variables that are set in the worker thread
   */
  env?: { [k: string]: string } | NodeJS.ProcessEnv
  /**
   * If true, treat the `filename` argument as a string of JavaScript to
   * be evaluated by Node in the worker thread.
   */
  eval?: boolean
  /**
   * Set internally to the numeric thread identifier once the worker is
   * instantiated.
   *
   * @internal
   */
  threadId?: number
}

/**
 * Class representing a TAP generating node worker thread
 *
 * Instantiated by `t.worker()`, typically.
 *
 * @internal
 */
export class Worker extends Base<WorkerEvents> {
  declare options: WorkerOpts

  eval: boolean
  filename: string
  cb?: () => void
  worker: null | NodeWorker = null
  #childId: string
  env: { [k: string]: string } | NodeJS.ProcessEnv
  // doesn't have to be cryptographically secure, just a gut check
  #childKey: string = String(Math.random())
  #timedOut?: { expired?: string }
  #workerEnded: boolean = false

  constructor(options: WorkerOpts) {
    const { filename } = options
    if (!filename) {
      throw new TypeError('no filename provided for t.worker()')
    }
    options.name =
      options.name || Worker.procName(filename, !!options.eval)
    super(options)
    this.#childId = String(options.childId || env.TAP_CHILD_ID || '1')
    this.filename = filename
    this.eval = !!options.eval
    this.env = {
      ...(options.env || env),
      TAP: '1',
      TAP_CHILD_ID: this.#childId,
      TAP_BAIL: this.bail ? '1' : '0',
      TAP_CHILD_KEY: this.#childKey,
    }
    this.bail = !!options.bail
  }

  main(cb: () => void) {
    this.cb = cb
    this.setTimeout(this.options.timeout || 0)

    this.parser.on('comment', c => {
      const tomatch = c.match(/# timeout=([0-9]+)\n$/)
      if (tomatch) {
        this.setTimeout(+tomatch[1])
      }
    })

    this.parent?.emit('worker', this)
    const options = {
      ...this.options,
      eval: this.eval,
      stdout: true,
      env: this.env,
    }
    this.emit('preprocess', options)
    this.worker = new NodeWorker(this.filename, options)
    this.worker.stdout.pipe(this.parser)
    this.worker.on('error', e => this.threw(e))
    this.worker.on('exit', () => this.#onworkerexit())
    this.worker.on('message', msg => {
      const m = msg as {
        key: string
        child: string
        setTimeout: number
      }
      if (
        m &&
        m.key === this.#childKey &&
        m.child === this.#childId
      ) {
        this.setTimeout(m.setTimeout)
        return
      }
      this.comment(...(Array.isArray(msg) ? msg : [msg]))
    })
    this.emit('process', this.worker)
  }

  threw(er: any, extra?: Extra): Extra | void | undefined {
    return throwToParser(this.parser, super.threw(er, extra))
  }

  #onworkerexit() {
    this.#workerEnded = true
    if (this.results) this.#callCb()
  }

  timeout(options: { expired?: string } = { expired: this.name }) {
    this.#timedOut = options
    // try to send the timeout signal.  If the child test process is
    // using node-tap as the test runner, and not caught in a busy
    // loop, it will trigger a dump of outstanding handles and refs.
    // If that doesn't do the job, then we fall back to thread termination.
    const worker = this.worker
    if (worker) {
      try {
        worker.postMessage({
          tapAbort: 'timeout',
          key: this.#childKey,
          child: this.childId,
        })
        /* c8 ignore start */
      } catch {}
      // need to ignore this bit because there's no way (by design) to
      // ignore the timeout signal, but it's theoretically possible that
      // it could be dropped or some busy-wait process prevents it from
      // being processed.
      const t = setTimeout(() => {
        // try to give it a chance to note the timeout and report handles
        try {
          worker.terminate()
        } catch (er) {}
      }, 500)
      if (t.unref) t.unref()
      /* c8 ignore stop */
    }
  }

  oncomplete(results: FinalResults) {
    this.results = results
    if (this.#workerEnded) this.#callCb()
  }

  #callCb() {
    if (this.#timedOut) super.timeout(this.#timedOut)
    const { results } = this
    /* c8 ignore start */
    if (!results) {
      throw new Error('worker calling cb before parser ended??')
    }
    /* c8 ignore stop */
    // worker closing with no tests is treated as a skip.
    if (results.plan && results.plan.skipAll) {
      this.options.skip = results.plan.skipReason || true
    }
    super.oncomplete(results)

    const { cb } = this
    /* c8 ignore start */
    if (!cb) {
      throw new Error('tap worker finished before receiving cb??')
    }
    /* c8 ignore stop */
    cb()
  }

  comment(...args: any[]) {
    const body = format(...args)
    const message =
      ('# ' + body.split(/\r?\n/).join('\n# ')).trim() + '\n'
    // it's almost impossible to send a message that will arrive
    // AFTER the stdout closes, as this only happens when the worker
    // thread closes, but it is theoretically possible, since messages
    // are asynchronous.
    /* c8 ignore start */
    if (this.parser.results) {
      if (this.parent && !this.parent.results) {
        this.parent.parser.write(message)
      } else {
        console.log(message.trimEnd())
      }
    } else {
      /* c8 ignore stop */
      this.parser.write(message)
    }
  }

  endAll() {
    if (this.worker) {
      this.parser.abort('test unfinished')
      this.worker.terminate()
    }
  }

  static procName(filename: string, ev: boolean) {
    const pref = '<worker> node'
    if (ev) return `${pref} -e <inline code>`
    if (filename.indexOf(cwd) === 0) {
      filename = './' + filename.substring(cwd.length + 1)
    }
    filename = filename.replace(/\\/g, '/')
    return `${pref} ${filename}`
  }
}

/**
 * The root TAP object singleton, the `t` you get when you
 * `import t from 'tap'`
 *
 * Inherits from {@link @tapjs/test!index.Test}, with all plugins applied, and
 * has additional functionality to automatically pipe to standard output, set
 * the process exit code appropriately, and infer options from environment
 * variables.
 *
 * @module
 *
 * @see {@link @tapjs/core!tap.TAP}
 */
import { Test, TestOpts } from '@tapjs/test'
import { Domain } from 'async-hook-domain'
import { Minipass, PipeOptions } from 'minipass'
import { isMainThread, parentPort } from 'node:worker_threads'
import { Handler, onExit } from 'signal-exit'
import { FinalResults } from 'tap-parser'
import { diags } from './diags.js'
import { IMPLICIT } from './implicit-end-sigil.js'
import { Extra, MILLISECONDS } from './index.js'
import { env, proc } from './proc.js'
import { TestBase } from './test-base.js'

const stdout = proc?.stdout

const privSym = Symbol('private constructor')
type PrivateTAPCtor = {
  [privSym]: true
}
const privateTAPCtor: PrivateTAPCtor = {
  [privSym]: true,
}

let instance: TAP | undefined = undefined

const envFlag = (key: string) =>
  env[key] === undefined ? undefined : env[key] === '1'

let piped = false
let registered = false
let autoend = false

/**
 * This is a singleton subclass of the {@link @tapjs/test!index.Test} base
 * class.
 *
 * Instantiate it by calling the exported {@link @tapjs/core!tap.tap} method.
 *
 * It has all of the same plugins, fields, properties etc of a "normal"
 * Test object, but with some additional characteristics to make it
 * suitable for use as the root test runner.
 *
 * - The {@link @tapjs/core!tap.TAP#register} method will hook onto the process
 *   object, to set the exit code to 1 if there are test failures, and ignore
 *   any `EPIPE` errors that happen on stdout.  (This is quite common in cases
 *   where a test aborts, and then attempts to write more data.)
 *
 * - A brief summary is printed at the end of the test run.
 *
 * - If piped to stdout, then `this.register()` will be called automatically.
 *
 * - If not piped anywhere else, the first time it writes any data, it will
 *   begin piping to stdout.
 *
 * - Options are set based on relevant environment variables, rather than
 *   taking an options object, since in normal cases, it will be instantiated
 *   automatically before any user code is run.
 *
 * - The test will automatically implicitly end when the process exits.  If
 *   there are any unfinished tests at this time, they will be emitted as
 *   failures.
 *
 * - If a `teardown` function is added, then the test will automatically
 *   implicitly end if it is idle for 3 consecutive `setTimeout` deferrals.
 *   This is a bit of a kludge, but it allows tests to run servers or other
 *   things that would prevent a graceful process exit, and close them down
 *   in a teardown function.
 *
 * - Lastly, since test files are often spawned by the runner using
 *   `t.spawn()`, this class listens for the timeout signal, and attempts to
 *   print diagnostic information about currently active handles and requests,
 *   as these are usually the cause of a test hanging indefinitely.
 */
class TAP extends Test {
  /**
   * @internal
   */
  constructor(priv: PrivateTAPCtor, opts: TestOpts = {}) {
    /* c8 ignore start */
    if (priv !== privateTAPCtor) {
      throw new Error(
        'the TAP singleton should not be instantiated directly'
      )
    }
    const timeoutEnv = env.TAP_TIMEOUT || '30'
    /* c8 ignore stop */

    const timeout = Number(timeoutEnv) * 1000
    const options = {
      name: 'TAP',
      diagnostic: envFlag('TAP_DIAG'),
      bail: envFlag('TAP_BAIL'),
      debug:
        envFlag('TAP_DEBUG') ||
        /\btap\b/i.test(env['NODE_DEBUG'] || ''),
      omitVersion: envFlag('TAP_OMIT_VERSION'),
      preserveWhitespace: !envFlag('TAP_OMIT_WHITESPACE'),
      timeout,
      failTodo: envFlag('TAP_FAIL_TODO'),
      failSkip: envFlag('TAP_FAIL_SKIP'),
      passes: envFlag('TAP_PASSES'),
      // these are always set in our tests.
      /* c8 ignore start */
      childId: Number(env.TAP_CHILD_ID) || 0,
      jobId: Number(env.TAP_JOB_ID) || 0,
      /* c8 ignore stop */
      ...opts,
    }

    // plugins get applied right here:
    super(options)

    instance = this
    this.on('idle', () => maybeAutoend())
    this.on('complete', (results: FinalResults) =>
      this.#oncomplete(results)
    )

    // only attach the teardown autoend if we're using the teardown plugin
    // we test in this convoluted manner rather than this.pluginLoaded
    // because otherwise we have a cyclical dep link between `@tapjs/core`
    // and `@tapjs/after` which prevents TS from being able to build properly
    // from a cold start.
    const td = this as typeof this & {
      teardown?: (fn: () => any) => void
    }
    const { teardown } = td
    if (typeof teardown === 'function') {
      type TD = typeof teardown
      td.teardown = (...args: Parameters<TD>): ReturnType<TD> => {
        autoend = true
        td.teardown = teardown
        return td.teardown(...args)
      }
    }

    this.runMain(() => {})
  }

  get registered() {
    return registered
  }

  /**
   * register this tap instance as being in charge of the current process
   * ignore epipe errors, set exit code, etc.
   *
   * Happens automatically if piped to stdout.
   */
  register() {
    if (registered) return
    registered = true
    registerTimeoutListener(this)
    ignoreEPIPE()
    proc?.once('beforeExit', () => {
      ;(this as unknown as TestBase).end(IMPLICIT)
    })
    proc?.once('exit', () => {
      if (!this.results) {
        this.endAll()
      }
    })
    // create a root domain to handle throws that happen outside
    // of any subtest.
    const rootDomain = new Domain((er, type) =>
      this.hookDomain.onerror(er, type)
    )
    this.hook.onDestroy = () => rootDomain.destroy()
  }

  onbail(reason?: string) {
    if (registered) {
      this.debug('bailout, exit 1')
      super.write(`Bail out!${reason ? ' ' + reason : ''}\n`)
    }
    super.onbail(reason)
    // don't fast-exit in the runner, so we can print the reason for
    // the bailout on test failure.
    if (registered && this.context !== Symbol.for('tap.isRunner')) {
      proc?.exit(1)
    }
  }

  /**
   * Just the normal Minipass.pipe method, but automatically registers
   * if the destination is stdout.
   */
  pipe<W extends Minipass.Writable>(dest: W, opts?: PipeOptions): W {
    piped = true
    if (stdout && dest === stdout) {
      this.register()
    }
    return super.pipe(dest, opts)
  }

  /**
   * Just the normal Minipass.write method, but automatically pipes
   * to stdout if not piped anywhere else.
   */
  write(chunk: string): boolean {
