import {
  argv,
  cwd,
  env,
  mainScript,
  TapPlugin,
  TestBase,
} from '@tapjs/core'
import { basename, dirname, relative, resolve } from 'node:path'
import { rimraf, rimrafSync } from 'rimraf'
import {
  Fixture,
  FixtureContent,
  FixtureDirContent,
  FixtureType,
} from './fixture.js'
export * from './fixture.js'

export interface TestFixturesOptions {
  /**
   * Directory to store test fixtures.
   * Defaults to `./.tap/fixtures/${test name}`
   */
  testdir?: string

  /**
   * Set to true to keep the fixture dir after the test ends.
   * Otherwise, any `t.testdir()` directories are deleted when the
   * test is done.
   */
  saveFixture?: boolean
}

export class TestFixtures {
  #t: TestBase
  static #refs: Map<TestBase, TestFixtures> = new Map()
  #testdir: string
  #didOnEOF: boolean = false
  #createdTestdir: boolean = false
  #saveFixture: boolean = false

  constructor(t: TestBase, opts: TestFixturesOptions) {
    TestFixtures.#refs.set(t, this)
    this.#testdir = opts.testdir || TestFixtures.#getTestdir(t)
    if (opts.saveFixture !== undefined) {
      this.#saveFixture = !!opts.saveFixture
    } else {
      this.#saveFixture = env.TAP_SAVE_FIXTURE === '1'
    }
    this.#t = t
  }

  /**
   * Create a fixture object for use in a
   * {@link @tapjs/fixture!index.TestFixtures#testdir} method.
   *
   * @group Spies, Mocks, and Fixtures
   */
  fixture<T extends FixtureType>(
    type: T,
    content: FixtureContent<T>
  ) {
    return new Fixture(type, content)
  }

  /**
   * Set whether the fixture should be saved or not
   *
   * Must be set *BEFORE* calling
   * {@link @tapjs/fixture!index.TestFixtures#testdir}, or it will not have any
   * effect.
   */
  set saveFixture(save: boolean) {
    this.#saveFixture = save
  }
  get saveFixture() {
    return this.#saveFixture
  }

  /**
   * Create a test directory, optionally filling it up with contents
   *
   * If the `@tapjs/after` plugin is loaded, the testdir will be automatically
   * deleted at the end of the test.
   *
   * To _not_ delete the directory after the test, use the
   * `saveFixture: true` option when creating the test, or specify
   * `--save-fixture` on the command line or in the tap configuration.
   *
   * @group Spies, Mocks, and Fixtures
   */
  testdir(content?: FixtureDirContent) {
    const dir = resolve(this.testdirName)
    rimrafSync(dir)
    Fixture.make(dir, content || {})
    this.#createdTestdir = true
    if (!this.#didOnEOF && !this.#saveFixture) {
      this.#didOnEOF = true
      const { onEOF } = this.#t
      this.#t.onEOF = async () => {
        this.#t.onEOF = onEOF
        await rimraf(dir)
        await onEOF()
      }
    }
    return dir
  }

  /**
   * The name of the folder that this test will use with
   * {@link @tapjs/fixture!index.TestFixtures#testdir}.
   *
   * By default, it uses a folder name based on the name of the test file
   * and subtest, within \`.tap/fixtures\` in the root of the project.
   *
   * @group Spies, Mocks, and Fixtures
   */
  get testdirName() {
    return this.#testdir
  }

  set testdirName(td: string) {
    if (this.#createdTestdir && td !== this.#testdir) {
      this.#didOnEOF = false
    }
    this.#testdir = td
  }

  static #getTestdir(t: TestBase) {
    const re = /[^a-zA-Z0-9\._\-]+/gi
    const p = t.parent && TestFixtures.#refs.get(t.parent)
    if (!p) {
      const main = mainScript()
      /* c8 ignore start */
      const root = env.TAP_CWD || cwd
      /* c8 ignore stop */
      const name = [
        dirname(relative(root, main)),
        basename(main),
        ...argv.slice(2),
      ]
        .join(' ')
        .trim()
        .replace(re, '-')
      return resolve(root, '.tap/fixtures', name)
    }

    /* c8 ignore start */
    const name = t.name || 'unnamed test'
    /* c8 ignore stop */

    return `${p.testdirName}-${name.replace(re, '-')}`
  }
}

export const plugin: TapPlugin<TestFixtures, TestFixturesOptions> = (
  t: TestBase,
  opts: TestFixturesOptions
) => new TestFixtures(t, opts)

/**
 * Options added by this plugin
 *
 * @group Configuration
 */
export const config = {
  /**
   * flag
   *
   * Do not clean up fixtures created with `t.testdir()`
   *
   * @group Configuration
   */
  'save-fixture': {
    type: 'boolean',
    short: 'F',
    description:
      'Do not clean up fixtures created with `t.testdir()`',
  },
}

import { plugin as TeardownPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import { at, CallSiteLike, captureString } from '@tapjs/stack'

const notConfig = (
  m: string,
  property: PropertyKey,
  caller: ((...a: any[]) => any) | Function
) => {
  const s =
    typeof property === 'string'
      ? `'${property}'`
      : property.toString()
  const er = new Error(
    `Cannot ${m} ${s}, defined {configurable:false}`
  )
  Error.captureStackTrace(er, caller)
  return er
}

/**
 * An object type containing all of the methods of the object
 * type provided
 */
export type MethodsObject<O extends object> = {
  [k in keyof O]: O[k] extends (...a: any[]) => any ? k : never
}

/**
 * The union type of all methods in an object
 */
export type Methods<O extends object> =
  MethodsObject<O>[keyof MethodsObject<O>]

/**
 * Base type of result objects generated by
 * {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultBase<F extends (...a: any[]) => any> {
  args: OverloadParams<F>
  receiver: Receiver<F>
  at?: CallSiteLike
  stack?: string
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * returned
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultReturned<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  returned: OverloadReturnType<F>
  threw: false
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture} where function
 * threw
 *
 * @typeParam F - the function that was captured
 */
export interface CaptureResultThrew<F extends (...a: any[]) => any>
  extends CaptureResultBase<F> {
  threw: true
}

/**
 * Results of {@link @tapjs/intercept!Interceptor#capture}
 *
 * @typeParam F - the function that was captured
 */
export type CaptureResult<F extends (...a: any[]) => any> =
  | CaptureResultReturned<F>
  | CaptureResultThrew<F>
  | CaptureResultBase<F>

/**
 * like ReturnType<F>, but a union of the return types of up to 10 overloads
 *
 * @typeParam F - the (possibly overloaded) function
 */
export type OverloadReturnType<F> = TupleUnion<
  UnarrayArray<FilterTupleUnknown<ORTuple<F>>>
>

/**
 * Get member type from array/tuple type
 */
export type Unarray<A> = A extends (infer V)[] ? V : A

/**
 * Get tuple of member types from array of array types
 */
export type UnarrayArray<L> = L extends [infer H, ...infer T]
  ? H extends unknown[]
    ? T extends unknown[][]
      ? [Unarray<H>, ...UnarrayArray<T>]
      : [Unarray<H>]
    : true
  : L

/**
 * Get overloaded return values as tuple of arrays
 */
export type ORTuple<F> = F extends {
  (...a: any[]): infer A0
  (...a: any[]): infer A1
  (...a: any[]): infer A2
  (...a: any[]): infer A3
  (...a: any[]): infer A4
  (...a: any[]): infer A5
  (...a: any[]): infer A6
  (...a: any[]): infer A7
  (...a: any[]): infer A8
  (...a: any[]): infer A9
}
  ? [A0[], A1[], A2[], A3[], A4[], A5[], A6[], A7[], A8[], A9[]]
  : never

/**
 * like Parameters<F>, but a union of parameter sets for up to 10 overloads
 */
export type OverloadParams<F> = TupleUnion<
  FilterUnknown<OverloadParamsTuple<F>>
>

/**
 * Get overloaded Parameters types as 10-tuple. When the function has less
 * than 10 overloaded signatures, the remaining param sets will be set to
 * `unknown`.
 *
 * @internal
 */
export type OverloadParamsTuple<F> = F extends {
  (...a: infer A0): any
  (...a: infer A1): any
  (...a: infer A2): any
  (...a: infer A3): any
  (...a: infer A4): any
  (...a: infer A5): any
  (...a: infer A6): any
  (...a: infer A7): any
  (...a: infer A8): any
  (...a: infer A9): any
}
  ? [A0, A1, A2, A3, A4, A5, A6, A7, A8, A9]
  : never

/**
 * Convert all `unknown[]` types in an array type to `never`
 */
export type NeverUnknown<T extends unknown[]> = unknown[] extends T
  ? (T extends {}[] ? true : false) extends true
    ? any[]
    : never
  : T

/**
 * Convert all `unknown[]` types in an array type to `never[]`
 */
export type NeverTupleUnknown<T extends unknown[]> =
  unknown[] extends T
    ? (T extends {}[] ? true : false) extends true
      ? any[]
      : never[]
    : T

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never`
 */
export type FilterUnknown<L> = L extends [infer H, ...infer T]
  ? H extends unknown[]
    ? T extends unknown[][]
      ? [NeverUnknown<H>, ...FilterUnknown<T>]
      : [NeverUnknown<H>]
    : FilterUnknown<T>
  : L

/**
 * Filter out `unknown[]` types from a tuple by converting them to `never[]`
 */
export type FilterTupleUnknown<L> = L extends [infer H, ...infer T]
  ? H extends unknown[]
    ? T extends unknown[][]
      ? [NeverTupleUnknown<H>, ...FilterTupleUnknown<T>]
      : [NeverTupleUnknown<H>]
    : FilterTupleUnknown<T>
  : L

/**
 * Create a union from a tuple type
 */
export type TupleUnion<L> = L extends [infer H, ...infer T]
  ? H | TupleUnion<T>
  : never

/**
 * Infer the `this` target of a function
 */
export type Receiver<F> = F extends (
  this: infer T,
  ...a: any[]
) => any
  ? T
  : never

/**
 * The method returned by {@link @tapjs/intercept!Interceptor#capture},
 * which returns the {@link @tapjs/intercept!CaptureResult} array when
 * called, and has methods to restore or get args, and exposes the list of
 * calls
 */
export type CaptureResultsMethod<
  T extends {},
  M extends Methods<T>,
  F = T[M]
> = (() => CaptureResult<
  F extends (...a: any[]) => any ? F : (...a: any[]) => any
>[]) & {
  restore: () => void
  calls: CaptureResult<
    F extends (...a: any[]) => any ? F : (...a: any[]) => any
  >[]
  args: () => OverloadParams<
    F extends { (...a: any[]): any } ? F : (...a: any[]) => any
  >[]
}

/**
 * Base class for objects created by
 * {@link @tapjs/intercept!Interceptor#intercept}
 */
export interface InterceptResultBase {
  receiver: any
  at?: CallSiteLike
  stack?: string
  value: any
  success: boolean
  threw: boolean
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for gets
 */
export interface InterceptResultGet extends InterceptResultBase {
  type: 'get'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept} for sets
 */
export interface InterceptResultSet extends InterceptResultBase {
  type: 'set'
}

/**
 * Result from {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResult = InterceptResultGet | InterceptResultSet

/**
 * Method returned by {@link @tapjs/intercept!Interceptor#intercept}
 */
export type InterceptResultsMethod = (() => InterceptResult[]) & {
  restore: () => void
}

/**
 * Implementation class providing the
 * {@link @tapjs/intercept!Interceptor#intercept},
 * {@link @tapjs/intercept!Interceptor#capture}, and
 * {@link @tapjs/intercept!Interceptor#captureFn} methods.
 */
export class Interceptor {
  #t: TestBase

  constructor(t: TestBase) {
    this.#t = t
  }

  /**
   * Intercept and track object property sets and gets.
   *
   * If a PropertyDescriptor is set, then it will be used as the replacement
   * value. Otherwise, the original descriptor will be used.
   *
   * If the `strictMode` param is set, then attempts to write to read-only
   * properties will throw an error.
   *
   * @group Spies, Mocks, and Fixtures
   */
  intercept<T extends object>(
    obj: T,
    prop: keyof T,
    desc?: PropertyDescriptor,
    strictMode: boolean = true
  ): InterceptResultsMethod {
    const resList: InterceptResult[] = []

    // find the original property descriptor, if it exists.
    let orig = Object.getOwnPropertyDescriptor(obj, prop)
    let src = obj
    while (!orig && src) {
      if (!(orig = Object.getOwnPropertyDescriptor(src, prop))) {
        src = Object.getPrototypeOf(src)
      }
    }
    if (orig && !orig.configurable) {
      throw notConfig('intercept property', prop, this.#t.t.intercept)
    }

    let restore: () => void

    // if we have an original, then that serves as the basis for
    // the value, if desc isn't set. If we don't have a desc, and
    // don't have an orig, then it's just { value: undefined }
    if (!orig || src !== obj) {
      // either it came from the proto chain, or wasn't found.
      // either way, we're assigning here.
      restore = () => {
        delete obj[prop]
        restore = () => {}
      }
    } else {
      // have an original, on the object itself
      restore = () => {
        Object.defineProperty(obj, prop, orig as PropertyDescriptor)
        restore = () => {}
      }
    }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    if (!desc) {
      desc = orig
        ? { ...orig }
        : {
            value: undefined,
            configurable: true,
            writable: true,
            enumerable: true,
          }
    }

    const base = desc as PropertyDescriptor
    const interceptor: PropertyDescriptor & {
      get: () => any
      set: (v: any) => void
    } = {
      enumerable: desc.enumerable,
      configurable: true,

      get() {
        // return the current value, and track
        let threw = true
        let success = false
        let value: any
        try {
          value = base.get ? base.get.call(obj) : base.value
          threw = false
          success = true
        } finally {
          resList.push({
            receiver: this,
            type: 'get',
            at: at(interceptor.get),
            stack: captureString(interceptor.get),
            value,
            threw,
            success,
          })
          if (!threw) return value
        }
      },

      set(value: any) {
        let threw = true
        let success = false
        try {
          if (base.set) {
            base.set.call(obj, value)
            success = true
          } else if (base.get) {
            if (strictMode) {
              const er = new TypeError(
                `Cannot set property '${String(
                  prop
                )}' of ${Object.prototype.toString.call(
                  obj
                )} which has only a getter`
              )
              Error.captureStackTrace(er, interceptor.set)
              throw er
            }
          } else {
            if (base.writable) {
              base.value = value
              success = true
            } else {
              if (strictMode) {
                const er = new TypeError(
                  `Cannot assign to read only property '${String(
                    prop
                  )}' of ${Object.prototype.toString.call(obj)}`
                )
                Error.captureStackTrace(er, interceptor.set)
                throw er
              }
            }
          }
          threw = false
        } finally {
          resList.push({
            receiver: this,
            type: 'set',
            at: at(interceptor.set),
            stack: captureString(interceptor.set),
            value,
            success,
            threw,
          })
        }
      },
    }

    Object.defineProperty(obj, prop, interceptor)
    return Object.assign(
      () => {
        const r = resList.slice()
        resList.length = 0
        return r
      },
      { restore: () => restore() }
    )
  }

  /**
   * Intercept calls to a method to track the arguments, call site,
   * and return/throw status, and replace the implementation.
   *
   * By default, the method is set to a no-op. To retain the method behavior,
   * pass the current value of the method as the third argument.  For example:
   *
   * ```
   * const results = t.capture(obj, 'foo', obj.foo)
   * ```
   *
   * Automatically restores at `t.teardown()` if the `@tapjs/after`
   * plugin is not disabled.  Otherwise, it is important to call the
   * `restore()` method on the returned function when you are done capturing.
   *
   * @group Spies, Mocks, and Fixtures
   */
  capture<T extends {}, M extends Methods<T>>(
    obj: T,
    method: M,
    impl: (this: T, ...a: any[]) => any = (..._: any[]) => {}
  ): CaptureResultsMethod<T, M> {
    const prop = Object.getOwnPropertyDescriptor(obj, method)
    if (prop && !prop.configurable) {
      throw notConfig('capture method', method, this.#t.t.capture)
    }

    // if we don't have a prop we can restore by just deleting
    // otherwise, we restore by putting it back as it was
    let restore = prop
      ? () => {
          Object.defineProperty(obj, method, prop)
          restore = () => {}
        }
      : () => {
          delete obj[method]
          restore = () => {}
        }

    if (this.#t.t?.pluginLoaded(TeardownPlugin)) {
      this.#t.t?.teardown(restore)
    }

    const fn = Object.assign(this.captureFn(impl), {
      restore: () => restore(),
    })

    Object.defineProperty(obj, method, {
      enumerable: prop ? prop.enumerable : true,
      writable: true,
      value: fn,
      configurable: true,
    })

    return Object.assign(
      () => {
        const r = fn.calls.slice()
        fn.calls.length = 0
        return r
      },
      {
        restore: () => restore(),
        calls: fn.calls,
        args: () => {
          const r = fn.calls.slice()
          fn.calls.length = 0
          return r.map(({ args }) => args) as OverloadParams<
            T[M] extends { (...a: any[]): any }
              ? T[M]
              : (...a: any[]) => any
          >[]
        },
      }
    )
  }

  /**
   * Just wrap the function and return it.  Does not have any
   * logic to restore, since it's not actually modifying anything.
   * The results hang off the function as the 'calls' property.
   *
   * The added `fn.args()` method will return an array of the arguments
   * passed to each call since the last time it was inspected.
   *
   * @group Spies, Mocks, and Fixtures
   */
  captureFn<F extends (this: any, ...a: any[]) => any>(
    original: F
  ): F & {
    calls: CaptureResult<F>[]
    args: () => OverloadParams<F>[]
  } {
    const calls: CaptureResult<F>[] = []
    const args = () => calls.map(({ args }) => args)
    return Object.assign(
      function wrapped(
        this: Receiver<F>,
        ...args: OverloadParams<F>
      ) {
        const res: CaptureResultBase<F> = {
          receiver: this,
          args,
          at: at(wrapped),
          stack: captureString(wrapped),
        }
        try {
          const returned = res as CaptureResultReturned<F>
          returned.returned = original.call(this, ...args)
          returned.threw = false
          calls.push(res)
          return returned.returned
        } finally {
          if ((res as CaptureResultReturned<F>).threw !== false) {
            ;(res as CaptureResultThrew<F>).threw = true
            calls.push(res)
          }
        }
      } as F,
      { calls, args }
    )
  }
}

/**
 * plugin method that instantiates an {@link @tapjs/intercept!Interceptor}
 */
export const plugin: TapPlugin<Interceptor> = t => new Interceptor(t)

import { plugin as AfterPlugin } from '@tapjs/after'
import { TapPlugin, TestBase } from '@tapjs/core'
import * as stack from '@tapjs/stack'
import { isBuiltin } from 'node:module'
import { mockRequire } from './mock-require.js'
import { MockService } from './mock-service.js'

/**
 * Implementation class providing the
 * {@link @tapjs/mock!index.TapMock#mockRequire},
 * {@link @tapjs/mock!index.TapMock#mockImport}, and
 * {@link @tapjs/mock!index.TapMock#createMock} methods.
 */
export class TapMock {
  #t: TestBase
  #didTeardown: boolean = false
  #mocks: MockService[] = []

  #allMock: Record<string, any>
  static #refs = new Map<TestBase, TapMock>()

  constructor(t: TestBase) {
    this.#t = t
    TapMock.#refs.set(t, this)
    // inherit #allMock
    const p = t.parent && TapMock.#refs.get(t.parent)
    this.#allMock = Object.assign(
      Object.create(null),
      p ? p.#allMock : {}
    )
  }

  /**
   * Convenience method to create a mock from an existing object by
   * overriding some (possibly deeply nested) methods or properties.
   *
   * Example:
   *
   * ```ts
   * import * as fs from 'node:fs'
   * const mockedThing = t.mockRequire('./module.js', t.createMock(
   *   { fs },
   *   { fs: { statSync: myMockedStatSync }}
   * )
   * ```
   *
   * This can also appear anywhere in the object hierarchy, which may
   * be more convenient in some cases:
   *
   * ```ts
   * import * as blah from '@long-name/blah-api'
   * const mockedThing = t.mockRequire('./module.js', {
   *   fs: t.createMock(fs, { statSync: myMockedStatSync }),
   *   child_process: t.createMock(child_process, { spawn: mockSpawn }),
   *   '@long-name/blah-api': t.createMock(blah, {
   *     some: {
   *       nested: {
   *         prop: true
   *       }
   *     }
   *   })
   * })
   * ```
   *
   * To *remove* a property, set it as undefined in the override.
   *
   * @group Spies, Mocks, and Fixtures
   */
  createMock<
    B extends { [k: PropertyKey]: any } | Array<any>,
    O extends { [k: string]: any } | Array<any>
  >(bases: B, overrides: O): MockedObject<B, O> {
    if (Array.isArray(overrides))
      return overrides as unknown as MockedObject<B, O>
    return Object.fromEntries(
      Object.entries(bases)
        .map(([k, v]) => {
          if (k in overrides) {
            const bobj = !!v && typeof v === 'object'
            const oobj =
              !!overrides[k] && typeof overrides[k] === 'object'
            if (oobj && bobj) {
              return [k, this.createMock(v, overrides[k])]
            } else {
              return [k, overrides[k]]
            }
          }
          return [k, v]
        })
        .concat(
          Object.entries(overrides).filter(([k]) => !(k in bases))
        )
    ) as MockedObject<B, O>
  }

  /**
   * Deprecated alias for {@link @tapjs/mock!index.TapMock#mockRequire}
   *
   * Prints a warning to stderr the first time it used, otherwise
   * identical.
   *
   * @group Spies, Mocks, and Fixtures
   *
   * @deprecated
   */
  mock(module: string, mocks: { [k: string]: any } = {}) {
    /* c8 ignore start */
    const at = stack.at(this.#t.t.mock)?.toJSON() || ''
    /* c8 ignore stop */
    console.error(
      't.mock() is now t.mockRequire(). Please update your tests.',
      at
    )
    return mockRequire(module, mocks, this.#t.t.mock)
  }

  /**
   * Load the supplied module asynchronously using import(),
   * replacing any of the referenced modules with the mocks provided.
   *
   * Works with either ESM or CommonJS modules, but as with `import()` of
   * CommonJS modules, the `module.exports` value will be set as the
   * `default` property on the resolved object, making
   * {@link @tapjs/mock!index.TapMock#mockRequire} somewhat more intuitive in those cases.
   *
   * For type info, cast result to `as typeof import(...)`, as
   * TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = await t.mockImport('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * }) as typeof import('../my-thing.js')
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  async mockImport(module: string, mocks: Record<string, any> = {}) {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockImport
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked'
      )
      return {}
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    if (!this.#didTeardown && this.#t.t.pluginLoaded(AfterPlugin)) {
      this.#didTeardown = true
      this.#t.t.teardown(() => this.unmock())
    }
    const service = MockService.create(
      module,
      mocks,
      this.#t.t.mockImport
    )
    this.#mocks.push(service)
    return Promise.resolve(service.module).then(s => import(s))
  }

  /**
   * Load the supplied module synchronously using `require()`,
   * replacing any of the referenced modules with the mocks provided.
   *
   * Only works with CommonJS modules.
   *
   * For type info, cast result to `as typeof import(...)`, as
   * TypeScript lacks a way to infer imports dynamically.
   *
   * For example:
   *
   * ```ts
   * const myThing = t.mockRequire('../my-thing.js', {
   *   some: { tricky: 'mocks' },
   * }) as typeof import('../my-thing.js')
   * ```
   *
   * @group Spies, Mocks, and Fixtures
   */
  mockRequire(module: string, mocks: Record<string, any> = {}) {
    if (isBuiltin(module)) {
      this.#t.t.currentAssert = this.mockRequire
      this.#t.t.fail(
        'Node built-in modules cannot have their imports mocked'
      )
      return {}
    }
    mocks = Object.assign({}, this.#allMock, mocks)
    return mockRequire(module, mocks, this.#t.t.mockRequire)
  }

  /**
   * Set some mocks that will be used for all subsequent
   * {@link @tapjs/mock!index.TapMock#mockImport} and
   * {@link @tapjs/mock!index.TapMock#mockRequire} calls made by this test.
   *
   * Mocks added with `mockAll` are overridden by any explicit mocks set in the
   * `t.mockRequire` or `t.mockImport` call.
   *
   * Repeated calls to `t.mockAll()` will *add* mocks to the set. If the same
   * name is used again, it will replace the previous value, not merge.
   *
   * If a key is set to `undefined` or `null`, then it will be removed from
   * the `mockAll` set.
   *
   * Reset by calling `t.mockAll(null)`
   *
   * Call with no args to return the current `mockAll` object.
   */
  mockAll(mocks?: Record<string, any> | null): Record<string, any> {
    if (mocks === null) this.#allMock = Object.create(null)
    else if (mocks !== undefined) {
      this.#allMock = Object.assign(this.#allMock, mocks)
      for (const [k, v] of Object.entries(this.#allMock)) {
        if (v === undefined || v === null) delete this.#allMock[k]
      }
    }
    return this.#allMock
  }

  /**
   * Unwind the mocks and free up the memory at the end of the test.
   *
   * Called automatically if the `@tapjs/after` plugin is not disabled.
   *
   * @group Spies, Mocks, and Fixtures
   */
  unmock() {
    for (const m of this.#mocks) {
      m.unmock()
    }
  }
}

/**
 * Utility type, overrides the properties in B with the properties
 * in O, deeply nested.
 */
export type MockedObject<B, O> = O extends Array<any>
  ? O
  : B extends { [k: PropertyKey]: any }
  ? O extends Function
    ? O
    : O extends { [k: string]: any }
    ? {
        [k in keyof B]: k extends keyof O
          ? MockedObject<B[k], O[k]>
          : B[k]
      }
    : O
  : O

/**
 * Loader that supports {@link @tapjs/mock!index.TapMock#mockImport}
 */
export const loader = '@tapjs/mock/loader'

/**
 * Importer for use with node --import
 */
export const importLoader = '@tapjs/mock/import'

/**
 * plugin method that instantiates {@link @tapjs/mock!index.TapMock}
 */
export const plugin: TapPlugin<TapMock> = (t: TestBase) =>
  new TapMock(t)

import type { TAP, TapPlugin } from '@tapjs/core'
import { env } from '@tapjs/core'
import { serialize } from './serialize.js'

let serialized = false
export const plugin: TapPlugin<{}> = t => {
  if (
    t.name === 'TAP' &&
    !t.parent &&
    env.NODE_TEST_CONTEXT === 'child-v8' &&
    !serialized
  ) {
    // make sure we don't serialize anything else in this process
    serialized = true
    env.NODE_TEST_CONTEXT = 'child'
    serialize(t as TAP)
  }
  return {}
}

import type { TapPlugin, TestBase } from '@tapjs/core'
import {
  argv,
  cwd,
  env,
  mainScript,
  MessageExtra,
  normalizeMessageExtra,
} from '@tapjs/core'
import { isPromise } from 'is-actual-promise'
import { relative, resolve } from 'path'
import { CompareOptions, format, strict } from 'tcompare'
import { Deferred } from 'trivial-deferred'
import { fileURLToPath } from 'url'
import { cleanCWD } from './clean-cwd.js'
import { SnapshotProviderDefault } from './provider.js'

const defaultFormatSnapshot =
  (co: CompareOptions = {}) =>
  (obj: any) =>
    format(obj, { sort: true, ...co })

/**
 * Interface provided by the class set in the `snapshotProvider` option.
 * `save()` may be an async method, but `read()` must be synchronous.
 */
export interface SnapshotProvider {
  file: string
  read(msg: string): string
  snap(data: string, msg: string): void
  save(): void | Promise<void>
}

export interface SnapshotOptions {
  compareOptions?: CompareOptions

  /**
   * Class to use to store and load snapshot data.
   * Defaults to SnapshotProviderDefault, which writes
   * to files in ./tap-snapshots
   * There's no hard requirement that "file" be a file on
   * disk of course. Could easily be in a database, localStorage,
   * whatever.
   */
  snapshotProvider?: {
    new (file: string): SnapshotProvider
  }

  /**
   * the "file" used to store snapshots.
   * Defaults to a filename based on the test file name and arguments,
   * in ./tap-snapshots.  Specifying the same filename will result in
   * getting the same SnapshotProvider.
   */
  snapshotFile?: string

  /**
   * whether or not to write the snapshot file.
   * Defaults true if TAP_SNAPSHOT=1 in the environment.
   */
  writeSnapshot?: boolean

  /**
   * Function used to serialize snapshotted objects to a string.
   * If a non-string is returned, then the default formatting will
   * be used, so this can also transform the object, if needed.
   */
  formatSnapshot?: (obj: any) => any

  /**
   * Function called on the string snapshot result, can be used to
   * remove changeable data, platform-specific stuff, etc.
   */
  cleanSnapshot?: (snapshotData: string) => string
}

export const plugin: TapPlugin<SnapshotPlugin, SnapshotOptions> = (
  t: TestBase,
  opts: SnapshotOptions = {}
) => new SnapshotPlugin(t, opts)

export class SnapshotPlugin {
  static #refs = new Map<TestBase, SnapshotPlugin>()
  #t: TestBase
  #provider: Exclude<SnapshotOptions['snapshotProvider'], undefined>
  #cleanSnapshot: SnapshotOptions['cleanSnapshot']
  #formatSnapshot: SnapshotOptions['formatSnapshot']
  #snapshot: SnapshotProvider
  writeSnapshot: boolean = false
  #compareOptions: CompareOptions

  constructor(t: TestBase, opts: SnapshotOptions) {
    SnapshotPlugin.#refs.set(t, this)
    this.#compareOptions = opts.compareOptions || {}
    this.#t = t
    const snapshotFile = SnapshotPlugin.#getFilename(t, opts)
    if (typeof opts.cleanSnapshot === 'function') {
      this.#cleanSnapshot = opts.cleanSnapshot
    }
    if (typeof opts.formatSnapshot === 'function') {
      this.#formatSnapshot = opts.formatSnapshot
    }
    // if the filename matches, and the provider type matches,
    // use the parent's snapshot provider object.  Otherwise,
    // create a new one.
    const p = t.parent && SnapshotPlugin.#refs.get(t.parent)
    const pp = p && p.#provider
    const pf = p && p.snapshotFile
    this.#provider =
      opts.snapshotProvider || pp || SnapshotProviderDefault

    if (typeof opts.writeSnapshot === 'boolean') {
      this.writeSnapshot = opts.writeSnapshot
    } else {
      if (p) {
        this.writeSnapshot = p.writeSnapshot
      } else {
        this.writeSnapshot =
          env.TAP_SNAPSHOT === '1' ||
          env.npm_lifecycle_event === 'snap' ||
          env.npm_lifecycle_event === 'snapshot'
      }
    }

    if (p && this.#provider === pp && snapshotFile === pf) {
      this.#snapshot = p.#snapshot
    } else {
      this.#snapshot = this.#newSnapshot(snapshotFile)
    }
  }

  #newSnapshot(f: string): SnapshotProvider {
    const snapshot = new this.#provider(f)
    if (this.writeSnapshot) {
      const onEOF = this.#t.onEOF
      this.#t.onEOF = () => {
        onEOF.call(this.#t)
        return snapshot.save()
      }
    }
    return snapshot
  }

  /**
   * Options that will be used when formatting snapshots and diffing/comparing
   * objects using any assertion methods.
   */
  get compareOptions(): Exclude<
    SnapshotOptions['compareOptions'],
    undefined
  > {
    return this.#compareOptions
  }
  set compareOptions(
    cmt: Exclude<SnapshotOptions['compareOptions'], undefined>
  ) {
    this.#compareOptions = cmt
    this.#t.options.compareOptions = cmt
  }

  /**
   * Method that will be called on snapshot strings. This can be used
   * to remove transient run-specific data from snapshots using simple
   * string transforms.
   */
  get cleanSnapshot(): SnapshotOptions['cleanSnapshot'] {
    return this.#cleanSnapshot
  }
  set cleanSnapshot(clean: SnapshotOptions['cleanSnapshot']) {
    this.#cleanSnapshot = clean
    this.#t.options.cleanSnapshot = clean
  }

  /**
   * Function that turns an object into a snapshot string.
   *
   * By default {@link tcompare!format} is used. If a string is returned,
   * then that string is the snapshot string. If any other type is returned,
   * then the returned value will be formatted using {@link tcompare!format}.
   */
  get formatSnapshot(): SnapshotOptions['formatSnapshot'] {
    return this.#formatSnapshot
  }
  set formatSnapshot(
    format: SnapshotOptions['formatSnapshot'] | undefined
  ) {
    format ??= defaultFormatSnapshot(this.#compareOptions)
    this.#formatSnapshot = format
    this.#t.options.formatSnapshot = format
  }

  /**
   * The file where snapshots will be written to and read from
   */
  get snapshotFile(): string {
    return this.#snapshot.file
  }
  set snapshotFile(f: string | URL) {
    const p =
      this.#t.parent && SnapshotPlugin.#refs.get(this.#t.parent)
    if (f instanceof URL || f.startsWith('file://')) {
      f = fileURLToPath(f)
    }
    if (p && this.#snapshot === p.#snapshot) {
      this.#snapshot = this.#newSnapshot(f)
    } else {
      this.#snapshot.file = f
    }
  }

  /**
   * In `--snapshot` mode, takes a snapshot of the object provided, and writes
   * to the snapshot file.
   *
   * Otherwise, reads the snapshot file, and verifies that a snapshot of the
   * object provided matches the stored snapshot.
   *
   * @group Assertion Methods
   */
  matchSnapshot(found: any, ...[msg, extra]: MessageExtra): boolean {
    this.#t.currentAssert = this.#t.t.matchSnapshot
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra('must match snapshot', args)
    const m = this.#t.fullname + ' > ' + me[0]
    if (typeof found !== 'string') {
      if (!this.#formatSnapshot) {
        for (
          let p = this.#t.parent;
          p && p.t.pluginLoaded(plugin);
          p = p.parent
        ) {
          const fs = p.t.formatSnapshot
          if (fs) {
            this.#formatSnapshot = fs
            break
          }
        }
      }
      const format =
        this.#formatSnapshot ||
        defaultFormatSnapshot(this.#compareOptions)
      found = format(found)
      if (typeof found !== 'string') {
        found = defaultFormatSnapshot(this.#compareOptions)(found)
      }
    }

    // see if a parent had defined it if we don't.
    // pretty common to define once on the root t for the whole test.
    if (!this.#cleanSnapshot) {
      for (
        let p = this.#t.parent;
        p && p.t.pluginLoaded(plugin);
        p = p.parent
      ) {
        const cs = p.t.cleanSnapshot
        if (cs) {
          this.#cleanSnapshot = cs
        }
      }
    }

    if (this.#cleanSnapshot) {
      found = this.#cleanSnapshot(found)
    }

    // \r\n line endings are annoying with git and other systems that
    // will "helpfully" make them into \n instead. Plus, when loaded
    // as JavaScript, they're just turned into \n anyway.
    if (found.includes('\r\n')) {
      found = defaultFormatSnapshot({
        ...this.compareOptions,
        bufferChunkSize: 16,
      })(Buffer.from(found))
    }

    // for the cwd, probably the most common error in snapshot
    // testing, see https://github.com/tapjs/tapjs/issues/885
    if (process.env.TAP_SNAPSHOT_CLEAN_CWD !== '0') {
      found = cleanCWD(found)
    }

    if (this.writeSnapshot) {
      this.#snapshot.snap(found, m)
      return this.#t.pass(...me)
    }
    const wanted = this.#snapshot.read(m)
    if (found === wanted) {
      return this.#t.pass(...me)
    }
    const { diff } = strict(found, wanted, this.#compareOptions)

    Object.assign(me[1], {
      found,
      wanted,
      diff,
      compare: '===',
    })
    return this.#t.fail(...me)
  }

  /**
   * Resolve a promise, and verify that the resulting value matches the
   * snapshot.
   *
   * @group Assertion Methods
   */
  async resolveMatchSnapshot<T extends any = any>(
    fnOrPromise: Promise<T> | (() => Promise<T>),
    ...[msg, extra]: MessageExtra
  ): Promise<boolean> {
    const args = [msg, extra] as MessageExtra
    const me = normalizeMessageExtra(
      'promise must resolve to match snapshot',
      args
    )

    let p!: Promise<T>
    try {
      p =
        typeof fnOrPromise === 'function'
          ? fnOrPromise()
          : fnOrPromise
    } catch (er) {
      p = Promise.reject(er)
    }

    if (!isPromise(p)) {
      return this.#t.fail(
        'no promise or async function provided to t.resolveMatchSnapshot'
      )
    }

    const d = new Deferred<boolean>()
    this.#t.waitOn(d.promise)
    this.#t.currentAssert = this.#t.t.resolveMatchSnapshot
    try {
      d.resolve(this.matchSnapshot(await p, ...me))
    } catch (er) {
      d.resolve(this.#t.fail(...me))
    }
    return d.promise
  }

  static #getFilename(t: TestBase, opts: SnapshotOptions): string {
    if (opts.snapshotFile) {
      return opts.snapshotFile
    }
    const p = t.parent && SnapshotPlugin.#refs.get(t.parent)
    if (p) {
      return p.snapshotFile
    }
    // get name from main file and args
    const main = mainScript()
    const args = argv.slice(2)
    const head = relative(cwd, resolve(main))
    const tail =
      args.length === 0
        ? ''
        : '-' + args.join(' ').replace(/[^a-zA-Z0-9\._\-]/g, '-')
    return resolve(cwd, 'tap-snapshots', head + tail + '.test.cjs')
  }
}

