import { mkdirpSync } from 'mkdirp'
import {
  linkSync,
  statSync,
  symlinkSync,
  writeFileSync,
} from 'node:fs'
import { dirname, resolve } from 'path'

export type FixtureType = 'file' | 'dir' | 'link' | 'symlink'
export interface FixtureDir {
  [entry: string]: FixtureDirContent
}

export type FixtureDirContent =
  | string
  | Buffer
  | Uint8Array
  | FixtureDir
  | Fixture<'file'>
  | Fixture<'dir'>
  | Fixture<'link'>
  | Fixture<'symlink'>

export type FixtureContent<T> = T extends 'file'
  ? string | Buffer | Uint8Array
  : T extends 'link' | 'symlink'
  ? string
  : T extends 'dir'
  ? FixtureDir
  : never

type GetType<T extends FixtureDirContent> = T extends Fixture<
  infer Type
>
  ? Type
  : T extends string | Buffer | Uint8Array
  ? 'file'
  : T extends number | symbol | bigint
  ? never
  : T extends {}
  ? 'dir'
  : never

const validateDirContents = (
  content: Record<string, FixtureDirContent>,
  seen: Set<Record<string, FixtureDirContent>>
) => {
  for (const [f, v] of Object.entries(content)) {
    const t = rawToType(v)
    if (
      v &&
      typeof v === 'object' &&
      t === 'dir' &&
      !(v instanceof Fixture)
    ) {
      const r = v as Record<string, FixtureDirContent>
      if (seen.has(r)) {
        throw new Error(
          'cycle detected in t.fixture contents at ' + f
        )
      }
      seen.add(r)
      validateDirContents(r, seen)
    }
  }
}

const assertValidContent: (
  type: FixtureType,
  content: any
) => void = <T extends FixtureType>(
  type: T,
  content: any
): asserts content is FixtureContent<T> => {
  switch (type) {
    case 'dir':
      if (!content || typeof content !== 'object') {
        throw new TypeError('dir fixture must have object content')
      }
      validateDirContents(content, new Set([content]))
      break
    case 'file':
      if (
        typeof content !== 'string' &&
        !Buffer.isBuffer(content) &&
        !(content instanceof Uint8Array)
      ) {
        throw new TypeError(
          'file fixture must have string/buffer content'
        )
      }
      break
    case 'link':
    case 'symlink':
      if (typeof content !== 'string') {
        throw new TypeError(type + ' fixture must have string target')
      }
      break
    default:
      throw new Error('invalid fixture type: ' + type)
  }
}

const rawToType = (f: FixtureDirContent): FixtureType => {
  if (f instanceof Fixture) {
    return f.type
  } else if (
    typeof f === 'string' ||
    Buffer.isBuffer(f) ||
    f instanceof Uint8Array
  ) {
    return 'file'
  } else if (f && typeof f === 'object') {
    return 'dir'
  }
  throw new Error('invalid fixture type: ' + f)
}

const rawToFixture = (
  f: FixtureDirContent
): Fixture<GetType<typeof f>> =>
  f instanceof Fixture ? f : new Fixture(rawToType(f), f)

const isSymlinkF = (
  f: Fixture<FixtureType>
): f is Fixture<'symlink'> => f.type === 'symlink'
const isLinkF = (f: Fixture<FixtureType>): f is Fixture<'link'> =>
  f.type === 'link'
const isDirF = (f: Fixture<FixtureType>): f is Fixture<'dir'> =>
  f.type === 'dir'
const isFileF = (f: Fixture<FixtureType>): f is Fixture<'file'> =>
  f.type === 'file'

export class Fixture<T extends FixtureType> {
  type: T
  content: FixtureContent<T>
  constructor(type: T, content: FixtureContent<T>) {
    assertValidContent(type, content)
    this.type = type
    this.content = content
  }

  get [Symbol.toStringTag]() {
    return 'Fixture<' + this.type + '>'
  }

  // have to gather up symlinks for the end
  static make(
    abs: string,
    content: FixtureDirContent,
    symlinks: null | { [k: string]: string } = null
  ) {
    const f = rawToFixture(content)

    const isRoot = symlinks === null
    symlinks = symlinks || {}

    if (isSymlinkF(f)) {
      // have to gather up symlinks for the end, because windows
      symlinks[abs] = f.content
    } else if (isLinkF(f)) {
      linkSync(resolve(dirname(abs), f.content), abs)
    } else if (isDirF(f)) {
      mkdirpSync(abs)
      for (const [name, fixture] of Object.entries(f.content))
        Fixture.make(`${abs}/${name}`, fixture, symlinks)
    } else if (isFileF(f)) {
      writeFileSync(abs, f.content)
      /* c8 ignore start */
    } else {
      // already validated above, impossible here
      throw new Error('uknown fixture type: ' + f.type)
    }
    /* c8 ignore stop */

    // create all those symlinks we were asked for
    if (isRoot) {
      for (const [abs, target] of Object.entries(symlinks)) {
        symlinkSync(
          target,
          abs,
          isDir(abs, target) ? 'junction' : 'file'
        )
      }
    }
  }
}

// check if a symlink target is a directory
const isDir = (abs: string, target: string) => {
  try {
    return statSync(resolve(dirname(abs), target)).isDirectory()
  } catch (er) {
    return false
  }
}

import * as stack from '@tapjs/stack'
import { createTwoFilesPatch } from 'diff'
import { readFileSync } from 'node:fs'
import { format, strict } from 'tcompare'

const tryReadFile = (path: string) => {
  try {
    return readFileSync(path, 'utf8')
  } catch (_) {
    return
  }
}

const hasOwn = (obj: { [k: string]: any }, key: string) =>
  Object.prototype.hasOwnProperty.call(obj, key)

/**
 * Prepare an object for printing to YAML diagnostics.
 *
 * Looks up source, calculates diffs of actual/expected values, and so on.
 */
export const cleanYamlObject = (object: { [k: string]: any }) => {
  const res = { ...object }
  if (hasOwn(res, 'stack') && !hasOwn(res, 'at')) {
    const st = Array.isArray(res.stack)
      ? res.stack.map(s => String(s).trimEnd() + '\n').join('')
      : String(res.stack)
    const p = stack.parseStack(st)
    res.at = p[0]
    res.stack = p.map(c => String(c) + '\n').join('')
  }
  if (
    typeof res.stack === 'string' &&
    res.stack &&
    !res.stack.endsWith('\n')
  ) {
    res.stack = res.stack.trimEnd() + '\n'
  }

  if (res.errorOrigin && typeof res.errorOrigin === 'object') {
    res.errorOrigin = cleanYamlObject(res.errorOrigin)
  }

  if (
    res.at &&
    res.at instanceof stack.CallSiteLike &&
    res.at.fileName &&
    res.at.absoluteFileName &&
    res.at.lineNumber &&
    !res.source
  ) {
    const file = res.at.absoluteFileName
    const content = tryReadFile(file)
    if (content) {
      const lines = content.split('\n')
      if (res.at.lineNumber <= lines.length) {
        const startLine = Math.max(res.at.lineNumber - 3, 0)
        const endLine = Math.min(res.at.lineNumber + 2, lines.length)
        const line = lines[res.at.lineNumber - 1]
        const caret =
          res.at.columnNumber &&
          line &&
          res.at.columnNumber <= line.length
            ? ['-'.repeat(res.at.columnNumber - 1) + '^']
            : []
        const context = lines
          .slice(startLine, res.at.lineNumber)
          .concat(caret)
          .concat(lines.slice(res.at.lineNumber, endLine))
        const csplit = context.join('\n').trimEnd()
        if (csplit) res.source = csplit + '\n'
      }
    }
  }
  if (res.at && res.at instanceof stack.CallSiteLike) {
    res.at = res.at.toJSON()
  }

  // show a line by line string diff
  // diff the yaml, to make it more humane, especially
  // when strings or buffers are very large or multi-line
  // the shipped compare methods will generally supply
  // their own diff, which is much nicer.
  if (
    'found' in res &&
    'wanted' in res &&
    res.found !== res.wanted &&
    !res.diff
  ) {
    const f = res.found
    const w = res.wanted
    if (typeof f === 'string' && typeof w === 'string')
      res.diff = createTwoFilesPatch(
        'expected',
        'actual',
        w + '\n',
        f + '\n'
      ).replace(/^=+\n/, '')
    else if (
      f &&
      w &&
      typeof f === 'object' &&
      typeof w === 'object'
    ) {
      const s = strict(f, w)
      if (!s.match) {
        res.diff = s.diff
      } else {
        res.note = 'object identities differ'
      }
    } else {
      // some mixed stringly bits
      const ff = format(f)
      const fw = format(w)
      const fs = (typeof f === 'string' ? f : ff) + '\n'
      const ws = (typeof w === 'string' ? w : fw) + '\n'
      if (fw !== ff) {
        res.diff = createTwoFilesPatch(
          'expected',
          'actual',
          ws,
          fs
        ).replace(/^=+\n/, '')
      }
    }
    if (res.diff === '--- expected\n+++ actual\n') {
      delete res.diff
    }
    if (res.diff) {
      delete res.found
      delete res.wanted
    }
  }

  for (const [key, value] of Object.entries(res)) {
    if (shouldDeleteKey(key, value)) {
      delete res[key]
    }
  }

  // if the 'message' is a string, then we print it on the
  // test point, so no need to repeat in the diags
  if (typeof res.message === 'string') delete res.message

  // worker: remove inline code
  if (
    res.eval === true &&
    typeof res.filename === 'string' &&
    res.filename.includes('\n')
  ) {
    res.filename = '<inline code>'
  }

  return res
}

/**
 * Properties that are *always* removed from the diagnostics, either because
 * they are internal (eg, `time`), overly noisy (eg, `parent`), or captured
 * elsewhere in the TAP output (eg, `skip`).
 */
export const deleteAlways = new Set([
  'todo',
  'time',
  'childId',
  'cb',
  'name',
  'indent',
  'skip',
  'bail',
  'diagnostic',
  'buffered',
  'parent',
  // only relevant if activated, a failedTodo or failedSkip will be added
  'failSkip',
  'failTodo',
  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'grep',
  'grepInvert',
  'only',
  'saveFixture',
  'env',
])

/**
 * Fields on this list are removed from YAML diagnostics if they are empty
 * (ie, falsey, empty array, or object with no keys)
 */
export const deleteIfEmpty = new Set([
  'at',
  'stack',
  'context',
  'debug',

  // TODO: keys added by plugins, but referenced here
  // How can this list be adjusted by plugins?
  'runOnly',
  'compareOptions',
])

/**
 * Fields are removed from YAML diagnostics if they match any of these
 * patterns.
 */
export const deleteIfMatch = [
  /^_?tapChild/,
  /^tapStream/,
  // TODO: create a @tapjs/mocha plugin
  /^tapMochaTest/,
]

const shouldDeleteKey = (key: string, value: any) =>
  deleteAlways.has(key) ||
  (deleteIfEmpty.has(key) && isEmpty(value)) ||
  deleteIfMatch.some(r => r.test(key))

// return true if object is empty, including inherited properties
const isEmpty = (obj: any): obj is {} => {
  if (!obj) {
    return true
  }
  if (typeof obj !== 'object') {
    return false
  }
  if (Array.isArray(obj)) {
    return obj.length === 0
  }
  for (const _ in obj) {
    return false
  }
  return true
}

