import type { ProcessInfo } from './index.js'

export interface ProcessInfoNodeData {
  // set initially, but deleted before it is written
  hrstart?: [number, number]

  // always set
  date: string
  argv: string[]
  execArgv: string[]
  NODE_OPTIONS?: string
  cwd: string
  pid: number
  ppid: number
  parent: string | null
  uuid: string
  files: string[]
  sources: Record<string, string[]>

  // fields that are only set when the process completes
  root?: string | null
  externalID?: string | null
  code?: number | null
  signal?: NodeJS.Signals | null
  runtime?: number
  globalsAdded?: string[]
}

export class ProcessInfoNode {
  date!: string
  argv!: string[]
  execArgv!: string[]
  NODE_OPTIONS?: string
  cwd!: string
  pid!: number
  ppid!: number
  uuid!: string
  code?: number | null
  signal?: NodeJS.Signals | null
  runtime?: number
  globalsAdded?: string[]
  files: string[] = []
  sources: Record<string, string[]> = Object.create(null)
  parent?: ProcessInfoNode | null = null
  root?: ProcessInfoNode | null = null
  children?: Set<ProcessInfoNode> | null = null
  descendants: Set<ProcessInfoNode> | null = null
  externalID: string | null = null

  #data: ProcessInfoNodeData

  constructor(data: ProcessInfoNodeData) {
    this.#data = data
    Object.assign(this, data)
  }

  toJSON() {
    return Object.fromEntries(
      Object.entries(this)
        .filter(([_, val]) => val !== null && val !== undefined)
        .map(([key, val]) =>
          val instanceof ProcessInfoNode
            ? [key, val.uuid]
            : key === 'children' || key === 'descendants'
            ? [key, [...val].map(c => c.uuid)]
            : [key, val]
        )
    )
  }

  link(db: ProcessInfo) {
    db.uuids.set(this.uuid, this)
    if (this.#data.parent) {
      this.parent = db.uuids.get(this.#data.parent) || this.parent
    }
    if (this.#data.root) {
      this.root = db.uuids.get(this.#data.root) ?? this.root
    }

    if (this.parent === null) {
      this.root = this
      const pendingRoot = db.pendingRoot.get(this.uuid)
      if (pendingRoot) {
        this.descendants = pendingRoot
        for (const n of pendingRoot) {
          n.root = this
        }
        db.pendingRoot.delete(this.uuid)
      }
      db.roots.add(this)
    } else if (typeof this.root === 'string') {
      const pendingRoot = db.pendingRoot.get(this.root)
      if (pendingRoot) {
        pendingRoot.add(this)
      } else {
        db.pendingRoot.set(this.root, new Set([this]))
      }
    } else if (this.root) {
      this.root.descendants ??= new Set()
      this.root.descendants.add(this)
    }

    if (typeof this.parent === 'string') {
      const pendingParent = db.pendingParent.get(this.parent)
      if (pendingParent) {
        pendingParent.add(this)
      } else {
        db.pendingParent.set(this.parent, new Set([this]))
      }
    } else if (this.parent) {
      if (!this.parent.children) {
        this.parent.children = new Set([this])
      } else {
        this.parent.children.add(this)
      }
    }

    const pendingParent = db.pendingParent.get(this.uuid)
    if (pendingParent) {
      this.children = pendingParent
      for (const n of this.children) {
        n.parent = this
      }
      db.pendingParent.delete(this.uuid)
    }

    for (const f of this.files as string[]) {
      const files = db.files.get(f)
      if (!files) {
        db.files.set(f, new Set<ProcessInfoNode>([this]))
      } else {
        files.add(this)
      }
    }

    if (this.externalID) {
      // newer tagged entry wins
      const e = db.externalIDs.get(this.externalID)
      if (e && Date.parse(e.date) > Date.parse(this.date)) {
        return
      }
      db.externalIDs.set(this.externalID, this)
    }
  }
}

/**
 * Implementation of the {@link TapMock#mockRequire} method
 */
import * as stack from '@tapjs/stack'
import Module, { createRequire, isBuiltin } from 'module'
import { dirname, resolve } from 'path'
import { walkUp } from 'walk-up-path'

// for some reason @types/node incorrectly believes that
// Module.prototype.require is a RequireFunction, but it isn't.
/**
 * Export of the Module class, but with the `require()` and `load()` methods
 * exposed.
 */
export type CorrectModule = Omit<Module, 'require'> & {
  require(id: string): any
  load(id: string): void
}

export type CorrectModuleCtor = Omit<
  typeof Module,
  'new' | 'prototype'
> & {
  new (
    id: string,
    parent: CorrectModule | MockedModule
  ): CorrectModule
  _cache: { [k: string]: CorrectModule }
}

const CorrectModule: CorrectModuleCtor =
  Module as unknown as CorrectModuleCtor

/**
 * A child class of Module, which loads modules from the mock object
 * if specified, otherwise will load *unmocked* modules in its own
 * mocked environment, so that they will load the mocked modules as well.
 */
export class MockedModule extends CorrectModule {
  #resolve: (id: string) => string
  #mocker: Mocker

  constructor(
    id: string,
    parent: CorrectModule | MockedModule,
    mocker?: Mocker
  ) {
    super(id, parent)
    this.filename ??= id
    this.#resolve = createRequire(this.filename).resolve
    if (parent instanceof MockedModule && parent.#mocker) {
      this.#mocker = parent.#mocker
    } else if (mocker) {
      this.#mocker = mocker
      /* c8 ignore start */
    } else {
      throw new Error('no mocker provided to MockedModule')
    }
    /* c8 ignore stop */
  }

  /**
   * Override require() to perform our mocked require
   */
  require(id: string): any {
    const requiredFilePath = this.#resolve(id)
    if (this.#mocker.hasMock(requiredFilePath)) {
      return this.#mocker.getMock(requiredFilePath)
    }

    if (isBuiltin(requiredFilePath)) {
      // has to be a builtin module, and we didn't mock it.
      return super.require(id)
    }

    const s = this.#mocker.getSeen(requiredFilePath)
    if (s) return s.exports

    const mod = new MockedModule(requiredFilePath, this)
    this.#mocker.setSeen(requiredFilePath, mod)
    mod.load(requiredFilePath)
    return mod.exports
  }
}

/**
 * Class that handles swapping out real modules for mocks as needed
 */
export class Mocker {
  #mocks: Map<string, any> = new Map()
  #seen: Map<string, MockedModule | Module> = new Map()
  module: MockedModule
  constructor(
    parentFileName: string,
    fileName: string,
    mocks: { [k: string]: any } = {}
  ) {
    const require = createRequire(parentFileName)
    const filePath = require.resolve(fileName)
    for (const [key, mock] of Object.entries(mocks)) {
      const mockFilePath = require.resolve(key)
      this.#mocks.set(mockFilePath, mock)
      // builtins can be either with or without the node: prefix
      if (mockFilePath.startsWith('node:')) {
        const bare = mockFilePath.substring('node:'.length)
        if (!(bare in mocks) && isBuiltin(bare)) {
          this.#mocks.set(bare, mock)
        }
      } else if (isBuiltin(mockFilePath)) {
        const prefixed = `node:${mockFilePath}`
        if (!(prefixed in mocks)) {
          this.#mocks.set(prefixed, mock)
        }
      }
    }

    this.#seen = new Map<string, Module>()
    const callerModule = CorrectModule._cache[parentFileName]
    let p: CorrectModule
    /* c8 ignore start */
    if (callerModule) p = callerModule
    else {
      /* c8 ignore stop */
      // if calling t.mockRequire from esm, you won't get a parent
      // but we should still pretend to have one.
      p = new Module(parentFileName) as unknown as CorrectModule
      p.filename = parentFileName
      p.loaded = true
      p.paths = [...walkUp(dirname(parentFileName))].map(path =>
        resolve(path, 'node_modules')
      )
    }

    this.module = new MockedModule(filePath, p, this)
    this.module.load(filePath)
  }

  /**
   * Return the module we've previously loaded from the specified filePath,
   * or undefined if it's new.
   */
  getSeen(filePath: string) {
    return this.#seen.get(filePath)
  }

  /**
   * Set the module in the internal cache once loaded
   */
  setSeen(filePath: string, mod: MockedModule) {
    this.#seen.set(filePath, mod)
  }

  /**
   * returns true if the set of mocks includes the specified ID
   */
  hasMock(id: string) {
    return this.#mocks.has(id)
  }

  /**
   * Get the mock at the specified identifier, or undefined if it's not mocked
   */
  getMock(id: string) {
    return this.#mocks.get(id)
  }
}

/**
 * Function that actually performs a mocked require()
 */
export const mockRequire: (
  module: string,
  mocks?: { [k: string]: any },
  caller?: Function | ((...a: any[]) => any)
) => any = (module, mocks = {}, caller = mockRequire) => {
  const needIgnoreTap = !stack.getIgnoredPackages().includes('@tapjs')
  if (needIgnoreTap) stack.addIgnoredPackage('@tapjs')
  const at = stack.at(caller)
  if (needIgnoreTap) stack.removeIgnoredPackage('@tapjs')
  const file = at?.absoluteFileName
  if (!file) {
    throw new Error('could not get current call site')
  }
  return new Mocker(file, module, mocks).module.exports
}

// Serialize the root TAP object, and output a node:test style
// serialized message stream.

import { Base, TAP } from '@tapjs/core'
import {
  TestStreamDeserialize,
  TestStreamSerialize,
} from '@tapjs/error-serdes'

import { proc } from '@tapjs/core'
import { inspect } from 'node:util'
import { commentMethod } from './comment.js'
import { onAddFn } from './on-add.js'
import { printMessagesFn } from './print-messages.js'
import { serializeStdio } from './stdio.js'
import { TestMap } from './test-map.js'

if (!proc) {
  throw new Error('Cannot serialize TAP stream, no process object')
}

const { stdout } = proc

export const serialize = (tap: TAP): void => {
  if (tap.registered) {
    throw new Error('Cannot serialize TAP stream, already registered')
  }
  tap.register?.()
  tap.options.passes = true

  const subsMap = new TestMap<Base[]>()
  const diagsMap = new TestMap<DiagnosticData[]>([[tap, []]])

  const stream = new TestStreamSerialize()
  // just for inspecting in dev.
  /* c8 ignore start */
  if (process.env.TAP_SERIALIZE_DEBUG === '1') {
    stream
      .pipe(new TestStreamDeserialize())
      .on('data', c =>
        stdout.write(
          inspect(JSON.parse(JSON.stringify(c)), {
            colors: true,
            depth: Infinity,
          }) + '\n'
        )
      )
  } else {
  /* c8 ignore stop */
    stream.pipe(stdout)
  }

  const comment = commentMethod(stream, diagsMap)

  // unpossible, we already checked on module load
  /* c8 ignore start */
  if (!proc) {
    throw new Error('Cannot serialize TAP stream, no process object')
  }
  /* c8 ignore stop */
  if (process.env.TAP_SERIALIZE_DEBUG !== '1') {
    serializeStdio(stream, proc, 'stderr')
    serializeStdio(stream, proc, 'stdout')
  }

  const onAdd = onAddFn(comment, diagsMap, subsMap)

  const printMessages = printMessagesFn(
    tap,
    stream,
    subsMap,
    diagsMap
  )

  onAdd(tap)
  tap.on('complete', () => {
    printMessages(tap)
    stream.end()
  })
}

import { cwd } from '@tapjs/core'
import { pathToFileURL } from 'node:url'

// escape a string to json, then unwrap the "
// used for cwd detection
const esc = (s: string) => {
  const j = JSON.stringify(s)
  return j.substring(1, j.length - 1)
}
const cwdURL = String(pathToFileURL(cwd)).substring('file://'.length)
const cwdPosix = cwd.replace(/\\/g, '/')
const cwdLcase = cwd.toLowerCase()
const cwdPosixLcase = cwdPosix.toLowerCase()
const cwds = new Set([cwdURL, cwd, cwdPosix, cwdLcase, cwdPosixLcase])
for (const c of [...cwds]) {
  cwds.add(esc(c))
  cwds.add(esc(esc(c)))
  cwds.add(esc(esc(esc(c))))
  cwds.add(esc(esc(esc(esc(c)))))
}

export const cleanCWD = (snap: string) => {
  const tag = '{CWD}'
  for (const c of cwds) {
    let i = -1
    // pad it out so that the length matches through the walk
    const replace = tag + '\u0001'.repeat(c.length - tag.length)
    while (
      -1 !== (i = snap.toLowerCase().indexOf(c.toLowerCase(), i))
    ) {
      snap =
        snap.substring(0, i) +
        replace +
        snap.substring(i + replace.length)
    }
    snap = snap.split(replace).join(tag)
  }
  return snap
}

import { mkdirSync, statSync, unlinkSync, writeFileSync } from 'fs'
import { dirname } from 'path'
import type { SnapshotProvider } from './index.js'
import { req } from './require.js'

const envMsg = `
Run with TAP_SNAPSHOT=1 in the environment to create snapshots`

const snapshotHeading = `/* IMPORTANT
 * This snapshot file is auto-generated, but designed for humans.
 * It should be checked into source control and tracked carefully.
 * Re-generate by setting TAP_SNAPSHOT=1 and running tests.
 * Make sure to inspect the output below.  Do not ignore changes!
 */
'use strict'`

const snapshotExport = (k: string, v: string) =>
  `exports[\`${escape(k)}\`] = \`\n${escape(v)}\n\`\n`

const snapshotData = (data: { [k: string]: string }) =>
  `${snapshotHeading}
${Object.entries(data)
  .sort(([a], [b]) => a.localeCompare(b, 'en'))
  .map(([k, v]) => snapshotExport(k, v))
  .join('\n')}`

const escape = (s: string) =>
  s
    .replace(/\\/g, '\\\\')
    .replace(/\`/g, '\\`')
    .replace(/\$\{/g, '\\${')

/**
 * This is the default snapshot provider that ships with the plugin.
 * It can be overridden by providing a `snapshotProvider` option to
 * the test options.
 */
export class SnapshotProviderDefault implements SnapshotProvider {
  #indexes: Map<string, number> = new Map()
  #snapshot?: { [k: string]: string }
  file: string

  constructor(file: string) {
    this.file = file
  }

  read(msg: string): string {
    if (!this.#snapshot) {
      this.#load()
    }

    // bump the index if this one gets used again
    const index = this.#indexes.get(msg) || 1

    const entry = msg + ' ' + index
    const s = this.#snapshot?.[entry]
    if (s === undefined) {
      throw new Error(`Snapshot entry not found: "${msg}"${envMsg}`)
    }

    this.#indexes.set(msg, index + 1)
    return s.replace(/^\n|\n$/g, '')
  }

  snap(data: string, msg: string) {
    const index = this.#indexes.get(msg) || 1
    this.#indexes.set(msg, index + 1)
    this.#snapshot = this.#snapshot || {}
    this.#snapshot[msg + ' ' + index] = data
  }

  save() {
    if (!this.#snapshot) {
      try {
        unlinkSync(this.file)
      } catch (err) {
        const er = err as NodeJS.ErrnoException
        if (er.code !== 'ENOENT') throw er
      }
      return
    }
    mkdirSync(dirname(this.file), { recursive: true })
    writeFileSync(this.file, snapshotData(this.#snapshot))
  }

  #load() {
    try {
      if (!statSync(this.file).isFile()) {
        throw 'not a file'
      }
      this.#snapshot = req(this.file)
    } catch (er) {
      throw new Error(
        `Snapshot file not found: ${this.file}${envMsg}`
      )
    }
  }
}

import * as util from 'util'

const pv =
  typeof process === 'object' &&
  !!process &&
  typeof process.version === 'string'
    ? process.version
    : 'v0.0.0'
const pvs = pv
  .replace(/^v/, '')
  .split('.')
  .map(s => parseInt(s, 10))

/* c8 ignore start */
const [major = 0, minor = 0] = pvs
/* c8 ignore stop */

let {
  parseArgs: pa,
}: typeof import('util') | typeof import('@pkgjs/parseargs') = util

/* c8 ignore start */
if (
  !pa ||
  major < 16 ||
  (major === 18 && minor < 11) ||
  (major === 16 && minor < 19)
) {
  /* c8 ignore stop */
  // Ignore because we will clobber it for commonjs
  //@ts-ignore
  pa = (await import('@pkgjs/parseargs')).parseArgs
}

export const parseArgs = pa as typeof import('util')['parseArgs']

import { Style, styles, StyleType } from './styles.js'

const arrayFrom = (obj: any) => {
  try {
    return Array.from(obj)
  } catch (_) {
    return null
  }
}

const { toString } = Object.prototype
const objToString = (obj: any) => toString.call(obj)

/**
 * Options to control the formatting of objects.
 */
export interface FormatOptions {
  /** sort items alphabetically by key */
  sort?: boolean
  /** how to print this thing */
  style?: StyleType
  /**
   * optinally override {@link tcompare!styles.Style#bufferChunkSize }
   * */
  bufferChunkSize?: number
  /**
   * Include any and all enumerable properties, including those inherited on
   * the prototype chain. By default, only `own` properties are printed.
   */
  includeEnumerable?: boolean
  /**
   * Include getter properties
   */
  includeGetters?: boolean
  /**
   * Represent and compare react elements as JSX strings.
   *
   * Only supported in the 'pretty' formatting style.
   */
  reactString?: boolean

  /**
   * set when formatting keys and values of collections
   *
   * @internal
   * */
  parent?: Format
  /**
   * test whether an object has been seen, and get a reference to the
   * Format handling them, if so.
   *
   * overridden in child classes when doing simplePrint()
   *
   * @internal
   */
  seen?: (obj?: any) => false | Format
  /**
   * Set when printing child fields
   *
   * @internal
   */
  key?: any
  /**
   * used when formatting Map keys
   *
   * @internal
   */
  isKey?: boolean
  /**
   * level within the object graph being printed
   *
   * @internal
   */
  level?: number
  /**
   * indentation level of this object within the object graph
   *
   * @internal
   */
  indent?: string
  /**
   * used when provisionally exploring a path for comparison
   *
   * @internal
   */
  provisional?: boolean
  /**
   * The object being compared against in comparison classes. (Not used
   * in {@link tcompare!format.Format}.)
   *
   * @internal
   */
  expect?: any
}

/**
 * The base class for all other comparators, and used
 * directly by comparators for their "simplePrint" methods.
 * It doesn't do comparison, just formatting.
 */
export class Format {
  options: FormatOptions
  parent: Format | null
  memo: null | string
  sort: boolean
  id: null | number
  idCounter: number
  idMap: Map<any, number>
  style: Style
  bufferChunkSize: number
  key: any
  isKey: boolean
  level: number
  indent: string
  match: boolean
  object: any
  expect: any

  constructor(obj: any, options: FormatOptions = {}) {
    this.options = options
    options.reactString = options.reactString !== false
    this.parent = options.parent || null
    this.memo = null
    this.sort = !!options.sort
    if (typeof options.seen === 'function') {
      this.seen = options.seen
    }
    this.id = null
    this.idCounter = 0
    this.idMap = this.parent ? this.parent.idMap : new Map()
    const style = this.parent
      ? this.parent.style
      : styles[options.style || 'pretty']
    if (!style) {
      throw new TypeError(`unknown style: ${options.style}`)
    }
    this.style = style
    this.bufferChunkSize =
      this.style.bufferChunkSize === Infinity
        ? Infinity
        : options.bufferChunkSize || this.style.bufferChunkSize

    // for printing child values of pojos and maps
    this.key = options.key

    // for printing Map keys
    this.isKey = !!options.isKey
    if (this.isKey && !(this.parent && this.parent.isMap())) {
      throw new Error('isKey should only be set for Map keys')
    }

    this.level = this.parent ? this.parent.level + 1 : 0
    this.indent = this.parent
      ? this.parent.indent
      : typeof options.indent === 'string'
      ? options.indent
      : '  '
    this.match = true
    this.object = obj
    this.expect = obj
  }

  incId(): number {
    return this.parent ? this.parent.incId() : (this.idCounter += 1)
  }

  getId(): number {
    if (this.id) {
      return this.id
    }
    const fromMap = this.idMap.get(this.object)
    if (fromMap) {
      return (this.id = fromMap)
    }
    const id = this.incId()
    this.idMap.set(this.object, id)
    return (this.id = id)
  }

  seen(_?: any): false | Format {
    if (!this.object || typeof this.object !== 'object') {
      return false
    }

    for (let p = this.parent; p; p = p.parent) {
      if (p.object === this.object) {
        p.id = p.id || p.getId()
        return p
      }
    }
    return false
  }

  child(obj: any, options: FormatOptions, cls?: typeof Format) {
    // This raises an error because ts thinks 'typeof Class' is
    // a normal function, not an instantiable class. Ignore.
    //@ts-expect-error
    return new (cls || this.constructor)(obj, {
      ...this.options,
      isKey: false,
      provisional: false,
      ...options,
      parent: this,
    })
  }

  // type testing methods
  isError(): boolean {
    return this.object instanceof Error
  }

  isArguments(): boolean {
    return objToString(this.object) === '[object Arguments]'
  }

  isArray(): boolean {
    return (
      Array.isArray(this.object) ||
      this.isArguments() ||
      this.isIterable()
    )
  }

  isReactElement(element: any = this.object): boolean {
    return (
      !!this.options.reactString &&
      !!this.style.reactElement &&
      !!element &&
      typeof element === 'object' &&
      typeof element.$$typeof === 'symbol' &&
      !!Symbol.keyFor(element.$$typeof)?.startsWith('react.') &&
      this.isReactElementChildren(element.props?.children)
    )
  }
  isReactElementChildren(children: any): boolean {
    return !children || typeof children === 'string'
      ? true
      : typeof children === 'object'
      ? children instanceof Set || Array.isArray(children)
        ? ![...children].some(c => !this.isReactElementChildren(c))
        : Format.prototype.isReactElement.call(this, children)
      : false
  }

  // technically this means "is an iterable we don't have another fit for"
  // sets, arrays, maps, and streams all handled specially.
  isIterable(): boolean {
    return (
      this.object &&
      typeof this.object === 'object' &&
      !this.isSet() &&
      !this.isMap() &&
      !this.isStream() &&
      typeof this.object[Symbol.iterator] === 'function'
    )
  }

  isKeyless(): boolean {
    return (
      !this.parent ||
      this.parent.isSet() ||
      this.parent.isArray() ||
      this.parent.isString() ||
      this.isKey
    )
  }

  isStream(): boolean {
    const s = this.object
    return (
      !!s &&
      typeof s === 'object' &&
      (typeof s.pipe === 'function' || // readable
        typeof s.pipeTo === 'function' || // whatwg readable
        (typeof s.write === 'function' &&
          typeof s.end === 'function')) // writable
    )
  }

  isMap(): boolean {
    return this.object instanceof Map
  }

  isSet(): boolean {
    return this.object instanceof Set
  }

  isBuffer(): boolean {
    return Buffer.isBuffer(this.object)
  }

  isString(): boolean {
    return typeof this.object === 'string'
  }

  // end type checking functions

  getClass(): string {
    const ts = objToString(this.object).slice(8, -1)
    return this.object.constructor !== Object &&
      this.object.constructor &&
      this.object.constructor.name &&
      this.object.constructor.name !== ts
      ? this.object.constructor.name
      : !Object.getPrototypeOf(this.object)
      ? 'Null Object'
      : ts
  }

  get objectAsArray(): any[] | null {
    // return the object as an actual array, if we can
    const value = Array.isArray(this.object)
      ? this.object
      : this.isArray()
      ? arrayFrom(this.object)
      : null

    if (value === null) {
      this.isArray = () => false
    }

    Object.defineProperty(this, 'objectAsArray', {
      value,
      configurable: true,
    })
    return value
  }

  // printing methods

  // Change from v5: ONLY the print() method returns a string
  // everything else mutates this.memo, so that child classes
  // can track both this.memo AND this.expectMemo, and then calculate
  // a diff at the end.
  print(): string {
    if (this.memo !== null) {
      return this.memo
    }
    this.memo = ''
    const seen = this.seen(this.object)
    if (seen) {
      this.printCircular(seen)
    } else {
      this.printValue()
    }
    this.printStart()
    this.printEnd()
    // this should be impossible
    /* c8 ignore start */
    if (typeof this.memo !== 'string') {
      throw new Error('failed to build memo string in print() method')
    }
    /* c8 ignore stop */
    return this.memo
  }

  printValue(): void {
    switch (typeof this.object) {
      case 'undefined':
        this.printUndefined()
        break

      case 'object':
        if (!this.object) {
          this.printNull()
        } else if (this.object instanceof RegExp) {
          this.printRegExp()
        } else if (this.object instanceof Date) {
          this.printDate()
        } else {
          this.printCollection()
        }
        break

      case 'symbol':
        this.printSymbol()
        break

      case 'bigint':
        this.printBigInt()
        break

      case 'string':
        this.printString()
        break

      case 'boolean':
        this.printBoolean()
        break

      case 'number':
        this.printNumber()
        break

      case 'function':
        this.printFn()
        break
    }
  }

  printReactElement(): void {
    // already verified in isReactElement before getting here.
    /* c8 ignore start */
    if (!this.style.reactElement) return this.printPojo()
    /* c8 ignore stop */
    const indent = this.indentLevel()
    this.memo += this.style
      .reactElement(this.object)
      .trim()
      .split('\n')
      .join('\n' + indent)
  }

  printDate(): void {
    this.memo += this.object.toISOString()
  }

  printRegExp(): void {
    this.memo += this.object.toString()
  }

  printUndefined(): void {
    this.memo += 'undefined'
  }

  printNull(): void {
    this.memo += 'null'
  }

  #printSymbol(sym: symbol): string {
    const keyFor = Symbol.keyFor(sym)
    const s = String(sym)
    const key = s.substring('Symbol('.length, s.length - 1)
    if (s.startsWith('Symbol(Symbol.')) {
      // check to see if it's a key on the Symbol global.
      // return Symbol.iterator, not Symbol(Symbol.iterator)
      const symKey = key.substring('Symbol.'.length, s.length - 1)
      if (
        symKey &&
        Symbol[symKey as keyof SymbolConstructor] === sym
      ) {
        return `Symbol.${symKey}`
      }
    }
    return this.style.symbol(keyFor ? 'Symbol.for' : 'Symbol', key)
  }
  printSymbol(): void {
    this.memo += this.#printSymbol(this.object)
  }

  printBigInt(): void {
    this.memo += this.object.toString() + 'n'
  }

  printBoolean(): void {
    this.memo += JSON.stringify(this.object)
  }

  printNumber(): void {
    this.memo += JSON.stringify(this.object)
  }

  printStart(): void {
    if (!this.parent) {
      this.memo = this.nodeId() + this.memo
      return
    }
    const indent = this.isKey ? '' : this.indentLevel()
    const key = this.isKeyless() ? '' : this.getKey()
    const sep = !key
      ? ''
      : this.parent && this.parent.isMap()
      ? this.style.mapKeyValSep()
      : this.style.pojoKeyValSep()
    this.memo =
      this.style.start(indent, key, sep) + this.nodeId() + this.memo
  }

  printEnd(): void {
    if (!this.parent) {
      return
    }
    this.memo +=
      this.isKey || !this.parent
        ? ''
        : this.parent.isMap()
        ? this.style.mapEntrySep()
        : this.parent.isBuffer()
        ? ''
        : this.parent.isArray()
        ? this.style.arrayEntrySep()
        : this.parent.isSet()
        ? this.style.setEntrySep()
        : this.parent.isString()
        ? ''
        : this.style.pojoEntrySep()
  }

  getKey(): string {
    return this.parent && this.parent.isMap()
      ? this.style.mapKeyStart() +
          this.parent.child(this.key, { isKey: true }, Format).print()
      : typeof this.key === 'string'
      ? JSON.stringify(this.key)
      : `[${this.#printSymbol(this.key)}]`
  }

  printCircular(seen: Format): void {
    this.memo += this.style.circular(seen)
  }

  indentLevel(n = 0): string {
    return this.indent.repeat(this.level + n)
  }

  printCollection(): void {
    return this.isError()
      ? this.printError()
      : this.isSet()
      ? this.printSet()
      : this.isMap()
      ? this.printMap()
      : this.isBuffer()
      ? this.printBuffer()
      : this.isArray() && this.objectAsArray
      ? this.printArray()
      : this.isReactElement()
      ? this.printReactElement()
      : // TODO streams, JSX
        this.printPojo()
  }

  nodeId(): string {
    return this.id ? this.style.nodeId(this.id) : ''
  }

  printBuffer(): void {
    if (this.parent && this.parent.isBuffer()) {
      this.memo +=
        this.style.bufferKey(this.key) +
        this.style.bufferKeySep() +
        this.style.bufferLine(this.object, this.bufferChunkSize)
    } else if (this.object.length === 0) {
      this.memo += this.style.bufferEmpty()
    } else if (this.bufferIsShort()) {
      this.memo +=
        this.style.bufferStart() +
        this.style.bufferBody(this.object) +
        this.style.bufferEnd(this.object)
    } else {
      this.printBufferHead()
      this.printBufferBody()
      this.printBufferTail()
    }
  }

  bufferIsShort(): boolean {
    return this.object.length < this.bufferChunkSize + 5
  }

  printBufferHead(): void {
    this.memo += this.style.bufferHead()
  }

  printBufferBody(): void {
    const c = this.bufferChunkSize
    let i: number
    for (i = 0; i < this.object.length - c; i += c) {
      this.printBufferLine(i, this.object.slice(i, i + c))
    }
    this.printBufferLastLine(i, this.object.slice(i, i + c))
  }

  printBufferLine(key: number, val: Buffer): void {
    this.printBufferLastLine(key, val)
    this.memo += this.style.bufferLineSep()
  }

  printBufferLastLine(key: any, val: Buffer): void {
    const child = this.child(val, { key })
    child.print()
    this.memo += child.memo
  }

  printBufferTail(): void {
    this.memo += this.style.bufferTail(this.indentLevel())
  }

  printSet(): void {
    if (this.setIsEmpty()) {
      this.printSetEmpty()
    } else {
      this.printSetHead()
      this.printSetBody()
      this.printSetTail()
    }
  }

  setIsEmpty(): boolean {
    return this.object.size === 0
  }

  printSetEmpty(): void {
    this.memo += this.style.setEmpty(this.getClass())
  }

  printSetHead(): void {
    this.memo += this.style.setHead(this.getClass())
  }

  printSetBody(): void {
    for (const val of this.object) {
      this.printSetEntry(val)
    }
  }

  printSetTail(): void {
    this.memo += this.style.setTail(this.indentLevel())
  }

  printSetEntry(val: any): void {
    const child = this.child(val, { key: val })
    child.print()
    this.memo += child.memo
  }

  printMap(): void {
    if (this.mapIsEmpty()) {
      this.printMapEmpty()
    } else {
      this.printMapHead()
      this.printMapBody()
      this.printMapTail()
    }
  }

  mapIsEmpty(): boolean {
    return this.object.size === 0
  }

  printMapEmpty(): void {
    this.memo += this.style.mapEmpty(this.getClass())
  }

  printMapHead(): void {
    this.memo += this.style.mapHead(this.getClass())
  }

  getMapEntries(obj: any = this.object): [string, any][] {
    // can never get here unless obj is already a map
    /* c8 ignore start */
    if (!(obj instanceof Map)) {
      throw new TypeError('cannot get map entries for non-Map object')
    }
    /* c8 ignore stop */
    return [...obj.entries()]
  }

  printMapBody(): void {
    for (const [key, val] of this.getMapEntries()) {
      this.printMapEntry(key, val)
    }
  }

  printMapTail(): void {
    this.memo += this.style.mapTail(this.indentLevel())
  }

  printMapEntry(key: any, val: any): void {
    const child = this.child(val, { key })
    child.print()
    this.memo += child.memo
  }

  printFn(): void {
    this.memo += this.style.fn(this.object, this.getClass())
  }

  printString(): void {
    if (this.parent && this.parent.isString()) {
      this.memo = this.style.stringLine(this.object)
    } else if (this.stringIsEmpty()) {
      this.printStringEmpty()
    } else if (this.stringIsOneLine()) {
      return this.printStringOneLine()
    } else {
      this.printStringHead()
      this.printStringBody()
      this.printStringTail()
    }
  }

  stringIsEmpty(): boolean {
    return this.object.length === 0
  }

  printStringEmpty(): void {
    this.memo += this.style.stringEmpty()
  }

  stringIsOneLine(): boolean {
    return /^[^\n]*\n?$/.test(this.object)
  }

  printStringOneLine(): void {
    this.memo += this.style.stringOneLine(this.object)
  }

  printStringHead(): void {
    this.memo += this.style.stringHead()
  }

  printStringBody(): void {
    const lines: string[] = this.object.split('\n')
    const lastLine = lines.pop()
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i]
      this.printStringLine(i, line + '\n')
    }
    this.printStringLastLine(lines.length, lastLine + '\n')
  }

  printStringLine(key: any, val: string): void {
    this.printStringLastLine(key, val)
    this.memo += this.style.stringLineSep()
  }

  printStringLastLine(key: any, val: string): void {
    const child = this.child(val, { key })
    child.print()
    this.memo += child.memo
  }

  printStringTail(): void {
    this.memo += this.style.stringTail(this.indentLevel())
  }

  printArray(): void {
    if (this.arrayIsEmpty()) {
      this.printArrayEmpty()
    } else {
      this.printArrayHead()
      this.printArrayBody()
      this.printArrayTail()
    }
  }

  arrayIsEmpty(): boolean {
    const a = this.objectAsArray
    return !!a && a.length === 0
  }

  printArrayEmpty(): void {
    this.memo += this.style.arrayEmpty(this.getClass())
  }

  printArrayHead(): void {
    this.memo += this.style.arrayHead(this.getClass())
  }

  printArrayBody(): void {
    if (this.objectAsArray) {
      this.objectAsArray.forEach((val, key) =>
        this.printArrayEntry(key, val)
      )
    }
  }

  printArrayTail(): void {
    this.memo += this.style.arrayTail(this.indentLevel())
  }

  printArrayEntry(key: any, val: any): void {
    const child = this.child(val, { key })
    child.print()
    this.memo += child.memo
  }

  printError(): void {
    if (this.errorIsEmpty()) {
      this.printErrorEmpty()
    } else {
      this.printErrorHead()
      this.printErrorBody()
      this.printErrorTail()
    }
  }

  errorIsEmpty(): boolean {
    return this.pojoIsEmpty()
  }

  printErrorEmpty(): void {
    this.memo += this.style.errorEmpty(this.object, this.getClass())
  }

  printErrorHead(): void {
    this.memo += this.style.errorHead(this.object, this.getClass())
  }

  printErrorTail(): void {
    this.memo += this.style.errorTail(this.indentLevel())
  }

  printErrorBody(): void {
    this.printPojoBody()
  }

  #getPojoKeys(obj: any, symbols = false): PropertyKey[] {
    // fast path, own string props only
    if (!symbols) {
      return Object.keys(obj)
    }
    // get all enumerable symbols
    const keys: symbol[] = Object.getOwnPropertySymbols(obj)
    return keys.filter(
      k => Object.getOwnPropertyDescriptor(obj, k)?.enumerable
    )
  }

  getPojoKeys(obj: any = this.object): PropertyKey[] {
    const keys: PropertyKey[] = []
    // always include known non-enumerable properties of Error objects
    if (obj instanceof Error) {
      const known = ['errors', 'cause']
      for (const prop of known) {
        const desc = Object.getOwnPropertyDescriptor(obj, prop)
        if (desc && !desc.enumerable) keys.push(prop)
      }
    }
    if (this.options.includeEnumerable) {
      // optimized fast path, for/in over enumerable string keys only
      for (const i in obj) {
        keys.push(i)
      }
      // walk up proto chain collecting all strings and symbols
      for (let p = obj; p; p = Object.getPrototypeOf(p)) {
        keys.push(...this.#getPojoKeys(p, true))
      }
      return keys
    }

    keys.push(
      ...this.#getPojoKeys(obj).concat(this.#getPojoKeys(obj, true))
    )
    if (!this.options.includeGetters) {
      return keys
    }

    const own = new Set(keys)
    const proto = Object.getPrototypeOf(obj)
    if (proto) {
      const desc = Object.getOwnPropertyDescriptors(proto)
      for (const [name, prop] of Object.entries(desc)) {
        if (prop.enumerable && typeof prop.get === 'function') {
          // public wrappers around internal things are worth showing
          own.add(name)
        }
      }
    }
    return Array.from(own)
  }

  printPojo(): void {
    if (this.pojoIsEmpty()) {
      this.printPojoEmpty()
    } else {
      this.printPojoHead()
      this.printPojoBody()
      this.printPojoTail()
    }
  }

  pojoIsEmpty(obj: any = this.object): boolean {
    return this.getPojoKeys(obj).length === 0
  }

  printPojoEmpty(): void {
    this.memo += this.style.pojoEmpty(this.getClass())
  }

  printPojoHead(): void {
    // impossible
    /* c8 ignore start */
    if (this.memo === null) {
      throw new Error('pojo head while memo is null')
    }
    /* c8 ignore stop */
    this.memo += this.style.pojoHead(this.getClass())
  }

  printPojoBody(): void {
    const ent = this.getPojoEntries(this.object)
    for (const [key, val] of ent) {
      this.printPojoEntry(key, val)
    }
  }

  getPojoEntries(obj: any): [PropertyKey, any][] {
    const ent: [PropertyKey, any][] = this.getPojoKeys(obj).map(k => {
      try {
        return [k, obj[k]]
      } catch {
        return [k, undefined]
      }
    })
    return this.sort
      ? ent.sort((a, b) =>
          String(a[0]).localeCompare(String(b[0]), 'en')
        )
      : ent
  }

  printPojoTail(): void {
    this.memo += this.style.pojoTail(this.indentLevel())
  }

  printPojoEntry(key: any, val: any): void {
    const child = this.child(val, { key })
    child.print()
    this.memo += child.memo
  }
}

import { Has } from './has.js'
import { Strict } from './strict.js'
/**
 * The same as {@link tcompare!has.Has}, but strictly compare all properties
 */
export class HasStrict extends Has {
  test() {
    const a = this.object
    const b = this.expect
    // constructor match is relevant to Strict, but HasStrict should
    // not do that, it's inconvenient, since it means you can't do
    // hasStrict(new URL('https://x.com/y'), { pathname: '/y' })
    // So, for objects, we call Same.  Everything else, call Strict.
    if (
      a &&
      b &&
      typeof a === 'object' &&
      typeof b === 'object' &&
      Array.isArray(a) === Array.isArray(b)
    ) {
      return super.test()
    } else {
      return Strict.prototype.test.call(this)
    }
  }
}

import { Format } from './format.js'
import { Same } from './same.js'
/**
 * The same as {@link tcompare!same.Same}, but ignore any fields present in the
 * test object and not present in the expect pattern.
 */
export class Has extends Same {
  // don't care about object shape, only that it has
  // matching fields of the same type.
  simpleMatch() {
    this.simple = this.test()
    if (!this.simple) {
      this.unmatch()
    }
  }

  isArray() {
    return super.isArray() && new Format(this.expect).isArray()
  }

  // just return the entries that exist in the expect object
  getPojoEntries(obj: any) {
    if (obj !== this.object) {
      return super.getPojoEntries(obj)
    }
    const expKeys = this.getPojoKeys(this.expect)
    const expSet = new Set(expKeys)
    const objKeys = this.getPojoKeys(obj)
    const objSet = new Set(objKeys)
    for (const k of expKeys) {
      if (!objSet.has(k) && this.expect[k] == undefined) {
        objKeys.push(k)
      }
    }
    const ent: [PropertyKey, any][] = objKeys
      .filter(k => expSet.has(k))
      .map(k => [k, obj[k]])

    return this.sort
      ? ent.sort((a, b) =>
          String(a[0]).localeCompare(String(b[0]), 'en')
        )
      : ent
  }

  printMapEntryUnexpected(_key: any, _val: any) {
    // nothing to do, this is fine
  }

  // only test expected array entries within the expect array length
  get objectAsArray() {
    const arr = super.objectAsArray
    if (arr) {
      const exp = super.expectAsArray
      if (exp && exp.length < arr.length) {
        const value = arr.slice(0, exp.length)
        Object.defineProperty(this, 'objectAsArray', {
          value,
          configurable: true,
        })
        return value
      }
    }
    const value = arr
    Object.defineProperty(this, 'objectAsArray', {
      value,
      configurable: true,
    })
    return value
  }

  // always include message/name, so you can do t.has(er, { message }) etc.
  printErrorBody() {
    super.printErrorBody()
    const expKeys = new Set(this.getPojoKeys(this.expect))
    if (expKeys.has('name') && this.expect.name) {
      this.printPojoEntry('name', this.object.name)
    }
    if (expKeys.has('message') && this.expect.message) {
      this.printPojoEntry('message', this.object.message)
    }
  }

  // this one is a little tricky, because we need to only walk the ones that
  // actually exist in the expect set.
  printSetBody() {
    // if there are MORE items in the expectation, that's always a fail.
    if (this.expect.size > this.object.size) {
      this.unmatch()
      this.memo += this.simplePrint(this.object)
      this.memoExpect += this.simplePrint(this.expect)
      return
    }
    const seen = new Set()
    // skip all identity matches
    for (const val of this.expect) {
      if (this.object.has(val)) {
        seen.add(val)
        continue
      }
    }
    for (const exp of this.expect) {
      if (seen.has(exp)) {
        continue
      }
      let sawMatch = false
      for (const val of this.object) {
        if (seen.has(val)) {
          continue
        }
        const s = this.child(val, {
          expect: exp,
          provisional: true,
        })
        s.print()
        if (s.match) {
          sawMatch = true
          seen.add(val)
          break
        }
      }
      if (!sawMatch) {
        this.unmatch()
        this.memo += this.simplePrint(this.object)
        this.memoExpect += this.simplePrint(this.expect)
        return
      }
    }
  }
}

import { Match } from './match.js'
import { Same } from './same.js'

/**
 * this uses the test method from {@link tcompare!match.Match}, but requires
 * that *only* the specified fields in the pattern are present in the object.
 *
 * It does still allow a field to be present in the object and not the pattern
 * if the value is set to null or undefined.
 */
export class MatchOnly extends Same {
  test() {
    return Match.prototype.test.call(this)
  }
}

import { Match } from './match.js'

/**
 * Identical to {@link tcompare!match.Match}, *except* in the case of two
 * simple types. If the value loosely equals the expected pattern, but is not
 * strictly equal, then the test returns false.
 */
export class MatchStrict extends Match {
  test() {
    // equal on type coercion, but not equal strictly.
    if (this.expect == this.object && this.expect !== this.object) {
      return false
    }
    return super.test()
  }
}

import { MatchStrict } from './match-strict.js'
import { Same } from './same.js'

/**
 * this uses the test method from {@link tcompare!match-strict.MatchStrict},
 * but requires that *only* the specified fields in the pattern are present in
 * the object.
 *
 * It does still allow a field to be present in the object and not the pattern
 * if the value is set to undefined.
 */
export class MatchOnlyStrict extends Same {
  test() {
    return MatchStrict.prototype.test.call(this)
  }
}

import { createTwoFilesPatch } from 'diff'

import { Format, FormatOptions } from './format.js'

const arrayFrom = (obj: any) => {
  try {
    return Array.from(obj)
  } catch (_) {
    return null
  }
}

const { hasOwnProperty } = Object.prototype
const { defineProperty } = Object

/**
 * Options for all comparator operations
 */
export interface SameOptions extends FormatOptions {
  /** the pattern to test against */
  expect: any
  parent?: Same
  key?: any
  expectKey?: any
  /**
   * how many lines of context to print around changes in diffs
   * @default 10
   */
  diffContext?: number
}

/**
 * Base class for all comparators
 *
 * We walk through both of the expect and actual objects,
 * creating a Same node for each field in common, based on
 * their similarity:
 * - true (they're a match) omit from the result (the child node is discarded)
 * - false (they're simply nonmatching) format both expect and object
 * - COMPLEX - walk through child nodes
 *   - if match: child node is discarded
 *   - else, child node is retained (along with its non-matching children)
 *
 * We 'discard' by just having the print method return ''
 *
 * When walking child nodes, we use the shouldCompare(key) method to determine
 * whether to check a given field.  In this class, this is always true (because
 * we are testing for full deep sameness), but in {@link tcompare!has.Has} and
 * subclasses, it's more complicated (only test nodes that exist in the expect
 * object).
 */
export class Same extends Format {
  provisional: boolean
  expect: any
  parent: Same | null
  simple: boolean | 'COMPLEX' | null = null
  match: boolean = true
  diffContext: number = 10
  memoDiff: string | null = null

  memoExpect: string | null = null

  constructor(obj: any, options: SameOptions) {
    if (!options || typeof options !== 'object') {
      throw new TypeError('must supply options object')
    }
    if (!('expect' in options)) {
      throw new TypeError('must supply expected value')
    }
    super(obj, options)
    this.parent = options.parent || null
    this.expect = options.expect
    if (!this.style.diffable) {
      throw new Error(
        `"${options.style}" style not appropriate for diffs`
      )
    }

    if (options.diffContext) {
      this.diffContext = options.diffContext
    }
    this.provisional = !!options.provisional
    this.simpleMatch()
  }

  simpleMatch() {
    this.simple = this.test()
    if (this.seen() !== this.seenExpect()) {
      this.simple = false
    }
    if (!this.simple) {
      this.unmatch()
    }
  }

  test() {
    const a = this.object
    const b = this.expect
    return typeof a === 'function' && typeof b === 'function'
      ? a === b ||
          (a.name === b.name && a.toString() === b.toString())
      : typeof a === 'symbol' || typeof b === 'symbol'
      ? typeof a === typeof b && a.toString() === b.toString()
      : typeof a !== 'object' && typeof b !== 'object' && a == b
      ? true
      : a === b
      ? true
      : a === null || b === null
      ? a == b
      : a !== a
      ? b !== b
      : typeof a !== 'object' || typeof b !== 'object'
      ? false
      : !this.isError() && b instanceof Error
      ? false
      : this.isError() &&
        ((b.message && b.message !== a.message) ||
          (b.name && b.name !== a.name))
      ? false
      : this.isSet() && !new Format(b).isSet()
      ? false
      : this.isMap() && !new Format(b).isMap()
      ? false
      : this.isArray() && !new Format(b).isArray()
      ? false
      : Buffer.isBuffer(a) && Buffer.isBuffer(b)
      ? a.equals(b)
      : a instanceof Date && b instanceof Date
      ? a.getTime() === b.getTime()
      : a instanceof RegExp && b instanceof RegExp
      ? this.regexpSame(a, b)
      : 'COMPLEX' // might still be a deeper mismatch, of course
  }

  regexpSame(a: RegExp, b: RegExp) {
    return (
      a.source === b.source &&
      a.flags === b.flags &&
      a.lastIndex === b.lastIndex
    )
  }

  unmatch() {
    if (this.match) {
      this.match = false
      if (!this.provisional) {
        this.parent && this.parent.unmatch()
      }
    }
  }

  // just print the thing as-is
  simplePrint(obj: any, options: FormatOptions = {}) {
    return new Format(obj, {
      ...this.options,
      ...options,
    }).print()
  }

  simplePrintExpect() {
    return new Format(this.expect, {
      ...this.options,
      seen: this.seenExpect,
    }).print()
  }

  seenExpect() {
    if (!this.expect || typeof this.expect !== 'object') {
      return false
    }

    for (let p = this.parent; p; p = p.parent) {
      if (p.expect === this.expect) {
        p.id = p.id || p.getId()
        return p
      }
    }
    return false
  }

  // if it's the root, then we do the diff
  // otherwise, we do the dual-walk of both trees,
  // building up the object and expect memos
  // this actually returns '' for any non-root node.
  print(): string {
    if (this.memo === null && this.memoExpect === null) {
      this.memo = ''
      this.memoExpect = ''
      if (!this.simple) {
        this.unmatch()
        this.memo += this.simplePrint(this.object)
        this.memoExpect += this.simplePrintExpect()
      } else {
        const seen = this.seen()
        const seenExpect = this.seenExpect()
        if (this.simple === true && seen === seenExpect) {
          this.memo = ''
          this.memoExpect = ''
        } else {
          if (seen) {
            this.printCircular(this.object)
          } else {
            this.printCollection()
          }
        }
      }
    }
    return this.diff()
  }

  printCircular(seen: Format): void {
    this.memo += this.style.circular(seen)
    const seenExpect = this.seenExpect()
    this.memoExpect = this.memoExpect || ''
    if (seenExpect) {
      this.memoExpect += this.style.circular(seenExpect)
    }
  }

  diff(): string {
    // impossible
    /* c8 ignore start */
    if (this.memoExpect === null || this.memo === null) {
      throw new TypeError('called diff() prior to print()')
    }
    /* c8 ignore stop */

    if (this.parent || this.match || this.memoExpect === this.memo) {
      return (this.memoDiff = '')
    }

    if (this.memoDiff !== null) {
      return this.memoDiff
    }

    return (this.memoDiff = createTwoFilesPatch(
      'expected',
      'actual',
      this.memoExpect + '\n',
      this.memo + '\n',
      undefined,
      undefined,
      { context: this.diffContext }
    ).replace(/^\=+\n/, ''))
  }

  child(
    obj: any,
    options: FormatOptions | SameOptions,
    cls?: typeof Same
  ) {
    const expectKey = hasOwnProperty.call(options, 'expectKey')
      ? (options as SameOptions).expectKey
      : options.key
    return super.child(
      obj,
      {
        expect: this.childExpect(expectKey),
        ...options,
      },
      cls
    )
  }

  childExpect(key: any) {
    // if we get here, we know that both expect and actual
    // are collections of the same type.  Otherwise they
    // would have gotten the simple printed diff.
    return this.isSet()
      ? key
      : this.isMap()
      ? this.expect.get(key)
      : this.isArray()
      ? (this.expectAsArray as any[])[key]
      : this.expect[key]
  }

  get expectAsArray() {
    const value = Array.isArray(this.expect)
      ? this.expect
      : new Format(this.expect).isArray()
      ? arrayFrom(this.expect)
      : /* c8 ignore start */
        null
    /* c8 ignore stop */

    defineProperty(this, 'expectAsArray', { value })
    return value
  }

  printStart(): void {
    if (!this.parent) {
      this.memo = this.nodeId() + this.memo
      this.memoExpect = this.nodeId() + this.memoExpect
      return
    }
    // we always simple print keys
    /* c8 ignore start */
    const indent = this.isKey ? '' : this.indentLevel()
    /* c8 ignore stop */
    // this will always be keyless, because Array and Set
    // objects are always simple printed.  But if that
    // chagnes, this will be relevant.
    /* c8 ignore start */
    const key = this.isKeyless() ? '' : this.getKey()
    /* c8 ignore stop */
    const sep = !key
      ? ''
      : this.parent && this.parent.isMap()
      ? this.style.mapKeyValSep()
      : this.style.pojoKeyValSep()
    const start = this.style.start(indent, key, sep)
    this.memo = start + this.nodeId() + this.memo
    this.memoExpect = start + this.nodeId() + this.memoExpect
  }

  printEnd(): void {
    if (!this.parent || this.isKey) {
      return
    }
    const end = this.parent.isMap()
      ? this.style.mapEntrySep()
      : this.parent.isArray()
      ? this.style.arrayEntrySep()
      : // these types are always simple printed
      /* c8 ignore start */
      this.parent.isSet()
      ? this.style.setEntrySep()
      : this.parent.isBuffer()
      ? ''
      : this.parent.isString()
      ? ''
      : /* c8 ignore stop */
        this.style.pojoEntrySep()
    this.memo += end
    this.memoExpect += end
  }

  isReactElement() {
    return (
      super.isReactElement(this.object) &&
      super.isReactElement(this.expect)
    )
  }
  printReactElement() {
    const obj = this.simplePrint(this.object)
    const exp = this.simplePrintExpect()
    this.memo += obj
    this.memoExpect += exp

    if (obj === exp) {
      return
    }

    // they don't match as JSX strings, but if we would consider the objects
    // to be equivalent, then still treat it as a match.
    const subDiff = new (this.constructor as typeof Same)(
      this.object,
      {
        ...this.options,
        expect: this.expect,
        parent: this.parent || undefined,
        reactString: false,
      }
    )
    subDiff.print()
    if (!subDiff.match) {
      this.unmatch()
    } else {
      this.memo += obj
      this.memoExpect += obj
    }
  }

  printPojo() {
    if (!this.memo) this.memo = ''
    if (!this.memoExpect) this.memoExpect = ''
    // even though it's not a simple mismatch, it's possible that
    // a child entry will cause a mismatch, so we have to print
    // the body *before* doing the head.  If we still aren't unmatched
    // after walking the graph, then nothing to do.
    if (this.pojoIsEmpty()) {
      this.printPojoEmpty()
    } else {
      this.printPojoBody()
      if (!this.match) {
        this.printPojoHead()
        this.printStart()
        this.printPojoTail()
        this.printEnd()
      }
    }
  }
  pojoIsEmpty() {
    return super.pojoIsEmpty() && this.pojoExpectIsEmpty()
  }
  pojoExpectIsEmpty() {
    return super.pojoIsEmpty(this.expect)
  }
  printPojoEmpty() {
    // both are empty and not a simple mismatch, nothing to do
  }
  getPojoKeys(obj: any = this.object): PropertyKey[] {
    const fromSuper = super.getPojoKeys(obj)
    if (obj === this.expect) {
      return fromSuper
    }
    return fromSuper.concat(
      this.getPojoKeys(this.expect).filter(k => k in obj)
    )
  }
  printPojoHead() {
    const h = this.style.pojoHead(this.getClass())

    this.memo = h + this.memo
    this.memoExpect = h + this.memoExpect
  }
  printPojoTail() {
    const t = this.style.pojoTail(this.indentLevel())
    this.memo += t
    this.memoExpect += t
  }
  printPojoBody() {
    const objEnt = new Map(this.getPojoEntries(this.object))
    const expEnt = new Map(this.getPojoEntries(this.expect))
    for (const [key, val] of objEnt.entries()) {
      if (!expEnt.has(key)) {
        this.unmatch()
      }
      this.printPojoEntry(key, val, false)
    }
    for (const key of expEnt.keys()) {
      if (objEnt.has(key)) {
        continue
      }
      this.unmatch()
      this.printPojoEntry(key, undefined, true)
    }
  }

  printPojoEntry(key: any, val: any, notFound?: boolean) {
    const child = this.child(val, { key })
    child.print()
    if (!notFound) {
      this.memo += child.memo
    }
    if (notFound || hasOwnProperty.call(this.expect, key)) {
      this.memoExpect += child.memoExpect
    }
  }

  // error is just a pojo with some fancy styling
  printError() {
    if (this.errorIsEmpty()) {
      return this.printErrorEmpty()
    } else {
      this.printErrorBody()
      if (!this.match) {
        this.printErrorHead()
        this.printStart()
        this.printErrorTail()
        this.printEnd()
      }
    }
  }
  errorIsEmpty() {
    return super.errorIsEmpty() && this.expectErrorIsEmpty()
  }
  expectErrorIsEmpty() {
    return (
      this.getPojoEntries(this.expect).filter(
        ([k]) => k !== 'name' && k !== 'message'
      ).length === 0
    )
  }
  printErrorEmpty() {
    // nothing to do
  }
  printErrorHead() {
    const headObj = this.style.errorHead(this.object, this.getClass())
    this.memo = headObj + this.memo
    const headExp = this.style.errorHead(this.expect, this.getClass())
    this.memoExpect = headExp + this.memoExpect
  }
  printErrorTail() {
    const t = this.style.errorTail(this.indentLevel())
    this.memo += t
    this.memoExpect += t
  }

  // maps are like pojos with fancier keys
  printMap(): void {
    if (this.mapIsEmpty()) {
      this.printMapEmpty()
    } else {
      this.printMapBody()
      if (!this.match) {
        this.printMapHead()
        this.printStart()
        this.printMapTail()
        this.printEnd()
      }
    }
  }
  mapIsEmpty() {
    return super.mapIsEmpty() && this.mapExpectIsEmpty()
  }
  mapExpectIsEmpty() {
    return this.expect.size === 0
  }
  printMapHead() {
    const h = this.style.mapHead(this.getClass())
    this.memo = h + this.memo
    this.memoExpect = h + this.memoExpect
  }
  printMapTail(): void {
    const t = this.style.mapTail(this.indentLevel())
    this.memo += t
    this.memoExpect += t
  }
  printMapBody(): void {
    // new Map([{}:1]) matches another new Map([{}:1])
    // so we can't rely on key identity.
    const seen = new Set()
    // first pass to get any that are key identity matches
    for (const [key, val] of this.object.entries()) {
      if (this.expect.has(key)) {
        seen.add(key)
        this.printMapEntry(key, val)
        continue
      }
    }
    for (const [key, val] of this.object.entries()) {
      if (seen.has(key)) {
        continue
      }
      // try to find a matching key not yet seen
      let sawMatch = false
      for (const expectKey of this.expect.keys()) {
        if (seen.has(expectKey)) {
          continue
        }
        const s = this.child(key, {
          expect: expectKey,
          provisional: true,
        })
        s.print()
        if (s.match) {
          // it's a match!  test against this one.
          sawMatch = true
          seen.add(key)
          seen.add(expectKey)
          sawMatch = true
          this.printMapEntry(key, val, expectKey)
          break
        }
      }

      if (!sawMatch) {
        this.printMapEntryUnexpected(key, val)
        seen.add(key)
      }
    }

    // now loop over all expected values not found in object
    for (const [key, val] of this.expect.entries()) {
      if (seen.has(key)) {
        continue
      }
      this.printMapEntryNotFound(key, val)
    }
  }

  printMapEntry(key: any, val: any, expectKey: any = key) {
    const child = this.child(val, { key, expectKey })
    child.print()
    this.memo += child.memo
    this.memoExpect += child.memoExpect
  }
  printMapEntryNotFound(key: any, val: any) {
    this.unmatch()
    this.memoExpect += this.simplePrint(val, {
      parent: this,
      key,
      seen: this.seenExpect,
    })
  }
  printMapEntryUnexpected(key: any, val: any) {
    this.unmatch()
    this.memo += this.simplePrint(val, {
      key,
      parent: this,
    })
  }

  // arrays and sets don't have useful keys, so it's really hard to see
  // where the mismatch occurs with only the path context. For example,
  // if you have an array of objects with many keys, that mismatches on
  // only one key in one object, we would get a diff that looks like:
  //  [
  // +  {key: value},
  // -  {key: otherValue},
  // ]
  // which isn't super helpful, since you don't know which index it failed
  // on, or even have the other properties of the object or key path to
  // use to find it.
  // So, if it's not a match, we simplePrint both the expected and object,
  // and let the diff sort it out, since it does a pretty good job of that
  // anyway.
  // This can be somewhat noisy, if you have an array with a single large
  // object, of course. An alternative approach to consider is to do the
  // full simplePrint for Sets, but include the Array index in the array
  // print, so it's at least clear where it deviated.
  printArray(): void {
    if (this.arrayIsEmpty()) {
      this.printArrayEmpty()
    } else {
      this.printArrayBody()
    }
  }
  arrayIsEmpty() {
    return super.arrayIsEmpty() && this.arrayExpectIsEmpty()
  }
  arrayExpectIsEmpty(): boolean {
    const a = this.expectAsArray
    return !!a && a.length === 0
  }
  printArrayEmpty() {
    // nothing to do
  }
  printArrayBody() {
    // we know that they're both arrays if we got this far
    const obj = this.objectAsArray as any[]
    const exp = this.expectAsArray
    // if lengths match, just call printArrayEntry() for each of them
    if (exp && obj.length === exp.length) {
      super.printArrayBody()
    } else {
      this.unmatch()
    }
    if (!this.match) {
      this.memo += this.simplePrint(this.object)
      this.memoExpect = this.memoExpect || ''
      this.memoExpect += this.simplePrintExpect()
    }
  }
  printArrayEntry(key: number, val: any) {
    const child = this.child(val, { key })
    child.print()
  }

  printSet() {
    if (this.setIsEmpty()) {
      this.printSetEmpty()
    } else {
      this.printSetBody()
    }
  }
  setExpectIsEmpty() {
    return this.expect.size === 0
  }
  setIsEmpty() {
    return super.setIsEmpty() && this.setExpectIsEmpty()
  }
  printSetBody() {
    if (this.expect.size !== this.object.size) {
      this.unmatch()
      this.memo += this.simplePrint(this.object)
      this.memoExpect = this.memoExpect || ''
      this.memoExpect += this.simplePrintExpect()
      return
    }
    const seen = new Set()
    // skip all identity matches, nothing to do for these
    for (const val of this.object) {
      if (this.expect.has(val)) {
        seen.add(val)
        continue
      }
    }
    for (const val of this.object) {
      if (seen.has(val)) {
        continue
      }
      let sawMatch = false
      for (const exp of this.expect) {
        if (seen.has(exp)) {
          continue
        }
        const s = this.child(val, {
          expect: exp,
          provisional: true,
        })
        s.print()
        if (s.match) {
          sawMatch = true
          seen.add(exp)
          break
        }
      }
      if (!sawMatch) {
        this.unmatch()
        this.memo += this.simplePrint(this.object)
        this.memoExpect = this.memoExpect || ''
        this.memoExpect += this.simplePrintExpect()
        return
      }
    }
  }
}

import { Dirent, Stats } from 'fs'
import { GlobOptions } from 'glob'

const typeOrUndef = (val: any, t: string) =>
  typeof val === 'undefined' || typeof val === t

export const isRimrafOptions = (o: any): o is RimrafOptions =>
  !!o &&
  typeof o === 'object' &&
  typeOrUndef(o.preserveRoot, 'boolean') &&
  typeOrUndef(o.tmp, 'string') &&
  typeOrUndef(o.maxRetries, 'number') &&
  typeOrUndef(o.retryDelay, 'number') &&
  typeOrUndef(o.backoff, 'number') &&
  typeOrUndef(o.maxBackoff, 'number') &&
  (typeOrUndef(o.glob, 'boolean') || (o.glob && typeof o.glob === 'object')) &&
  typeOrUndef(o.filter, 'function')

export const assertRimrafOptions: (o: any) => void = (
  o: any
): asserts o is RimrafOptions => {
  if (!isRimrafOptions(o)) {
    throw new Error('invalid rimraf options')
  }
}

export interface RimrafAsyncOptions {
  preserveRoot?: boolean
  tmp?: string
  maxRetries?: number
  retryDelay?: number
  backoff?: number
  maxBackoff?: number
  signal?: AbortSignal
  glob?: boolean | GlobOptions
  filter?:
    | ((path: string, ent: Dirent | Stats) => boolean)
    | ((path: string, ent: Dirent | Stats) => Promise<boolean>)
}

export interface RimrafSyncOptions extends RimrafAsyncOptions {
  filter?: (path: string, ent: Dirent | Stats) => boolean
}

export type RimrafOptions = RimrafSyncOptions | RimrafAsyncOptions

const optArgT = <T extends RimrafOptions>(
  opt: T
):
  | (T & {
      glob: GlobOptions & { withFileTypes: false }
    })
  | (T & { glob: undefined }) => {
  assertRimrafOptions(opt)
  const { glob, ...options } = opt
  if (!glob) {
    return options as T & { glob: undefined }
  }
  const globOpt =
    glob === true
      ? opt.signal
        ? { signal: opt.signal }
        : {}
      : opt.signal
      ? {
          signal: opt.signal,
          ...glob,
        }
      : glob
  return {
    ...options,
    glob: {
      ...globOpt,
      // always get absolute paths from glob, to ensure
      // that we are referencing the correct thing.
      absolute: true,
      withFileTypes: false,
    },
  } as T & { glob: GlobOptions & { withFileTypes: false } }
}

export const optArg = (opt: RimrafAsyncOptions = {}) => optArgT(opt)
export const optArgSync = (opt: RimrafSyncOptions = {}) => optArgT(opt)

// This is the same as rimrafPosix, with the following changes:
//
// 1. EBUSY, ENFILE, EMFILE trigger retries and/or exponential backoff
// 2. All non-directories are removed first and then all directories are
//    removed in a second sweep.
// 3. If we hit ENOTEMPTY in the second sweep, fall back to move-remove on
//    the that folder.
//
// Note: "move then remove" is 2-10 times slower, and just as unreliable.

import { Dirent, Stats } from 'fs'
import { parse, resolve } from 'path'
import { RimrafAsyncOptions, RimrafSyncOptions } from './index.js'
import { fixEPERM, fixEPERMSync } from './fix-eperm.js'
import { lstatSync, promises, rmdirSync, unlinkSync } from './fs.js'
import { ignoreENOENT, ignoreENOENTSync } from './ignore-enoent.js'
import { readdirOrError, readdirOrErrorSync } from './readdir-or-error.js'
import { retryBusy, retryBusySync } from './retry-busy.js'
import { rimrafMoveRemove, rimrafMoveRemoveSync } from './rimraf-move-remove.js'
const { unlink, rmdir, lstat } = promises

const rimrafWindowsFile = retryBusy(fixEPERM(unlink))
const rimrafWindowsFileSync = retryBusySync(fixEPERMSync(unlinkSync))
const rimrafWindowsDirRetry = retryBusy(fixEPERM(rmdir))
const rimrafWindowsDirRetrySync = retryBusySync(fixEPERMSync(rmdirSync))

const rimrafWindowsDirMoveRemoveFallback = async (
  path: string,
  opt: RimrafAsyncOptions
): Promise<boolean> => {
  /* c8 ignore start */
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  /* c8 ignore stop */
  // already filtered, remove from options so we don't call unnecessarily
  const { filter, ...options } = opt
  try {
    return await rimrafWindowsDirRetry(path, options)
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'ENOTEMPTY') {
      return await rimrafMoveRemove(path, options)
    }
    throw er
  }
}

const rimrafWindowsDirMoveRemoveFallbackSync = (
  path: string,
  opt: RimrafSyncOptions
): boolean => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  // already filtered, remove from options so we don't call unnecessarily
  const { filter, ...options } = opt
  try {
    return rimrafWindowsDirRetrySync(path, options)
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer?.code === 'ENOTEMPTY') {
      return rimrafMoveRemoveSync(path, options)
    }
    throw er
  }
}

const START = Symbol('start')
const CHILD = Symbol('child')
const FINISH = Symbol('finish')

export const rimrafWindows = async (path: string, opt: RimrafAsyncOptions) => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  try {
    return await rimrafWindowsDir(path, opt, await lstat(path), START)
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'ENOENT') return true
    throw er
  }
}

export const rimrafWindowsSync = (path: string, opt: RimrafSyncOptions) => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  try {
    return rimrafWindowsDirSync(path, opt, lstatSync(path), START)
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'ENOENT') return true
    throw er
  }
}

const rimrafWindowsDir = async (
  path: string,
  opt: RimrafAsyncOptions,
  ent: Dirent | Stats,
  state = START
): Promise<boolean> => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }

  const entries = ent.isDirectory() ? await readdirOrError(path) : null
  if (!Array.isArray(entries)) {
    // this can only happen if lstat/readdir lied, or if the dir was
    // swapped out with a file at just the right moment.
    /* c8 ignore start */
    if (entries) {
      if (entries.code === 'ENOENT') {
        return true
      }
      if (entries.code !== 'ENOTDIR') {
        throw entries
      }
    }
    /* c8 ignore stop */
    if (opt.filter && !(await opt.filter(path, ent))) {
      return false
    }
    // is a file
    await ignoreENOENT(rimrafWindowsFile(path, opt))
    return true
  }

  const s = state === START ? CHILD : state
  const removedAll = (
    await Promise.all(
      entries.map(ent => rimrafWindowsDir(resolve(path, ent.name), opt, ent, s))
    )
  ).reduce((a, b) => a && b, true)

  if (state === START) {
    return rimrafWindowsDir(path, opt, ent, FINISH)
  } else if (state === FINISH) {
    if (opt.preserveRoot === false && path === parse(path).root) {
      return false
    }
    if (!removedAll) {
      return false
    }
    if (opt.filter && !(await opt.filter(path, ent))) {
      return false
    }
    await ignoreENOENT(rimrafWindowsDirMoveRemoveFallback(path, opt))
  }
  return true
}

const rimrafWindowsDirSync = (
  path: string,
  opt: RimrafSyncOptions,
  ent: Dirent | Stats,
  state = START
): boolean => {
  const entries = ent.isDirectory() ? readdirOrErrorSync(path) : null
  if (!Array.isArray(entries)) {
    // this can only happen if lstat/readdir lied, or if the dir was
    // swapped out with a file at just the right moment.
    /* c8 ignore start */
    if (entries) {
      if (entries.code === 'ENOENT') {
        return true
      }
      if (entries.code !== 'ENOTDIR') {
        throw entries
      }
    }
    /* c8 ignore stop */
    if (opt.filter && !opt.filter(path, ent)) {
      return false
    }
    // is a file
    ignoreENOENTSync(() => rimrafWindowsFileSync(path, opt))
    return true
  }

  let removedAll = true
  for (const ent of entries) {
    const s = state === START ? CHILD : state
    const p = resolve(path, ent.name)
    removedAll = rimrafWindowsDirSync(p, opt, ent, s) && removedAll
  }

  if (state === START) {
    return rimrafWindowsDirSync(path, opt, ent, FINISH)
  } else if (state === FINISH) {
    if (opt.preserveRoot === false && path === parse(path).root) {
      return false
    }
    if (!removedAll) {
      return false
    }
    if (opt.filter && !opt.filter(path, ent)) {
      return false
    }
    ignoreENOENTSync(() => {
      rimrafWindowsDirMoveRemoveFallbackSync(path, opt)
    })
  }
  return true
}

import { parse, resolve } from 'path'
import { inspect } from 'util'
import { RimrafAsyncOptions } from './index.js'
import platform from './platform.js'

const pathArg = (path: string, opt: RimrafAsyncOptions = {}) => {
  const type = typeof path
  if (type !== 'string') {
    const ctor = path && type === 'object' && path.constructor
    const received =
      ctor && ctor.name
        ? `an instance of ${ctor.name}`
        : type === 'object'
        ? inspect(path)
        : `type ${type} ${path}`
    const msg =
      'The "path" argument must be of type string. ' + `Received ${received}`
    throw Object.assign(new TypeError(msg), {
      path,
      code: 'ERR_INVALID_ARG_TYPE',
    })
  }

  if (/\0/.test(path)) {
    // simulate same failure that node raises
    const msg = 'path must be a string without null bytes'
    throw Object.assign(new TypeError(msg), {
      path,
      code: 'ERR_INVALID_ARG_VALUE',
    })
  }

  path = resolve(path)
  const { root } = parse(path)

  if (path === root && opt.preserveRoot !== false) {
    const msg = 'refusing to remove root directory without preserveRoot:false'
    throw Object.assign(new Error(msg), {
      path,
      code: 'ERR_PRESERVE_ROOT',
    })
  }

  if (platform === 'win32') {
    const badWinChars = /[*|"<>?:]/
    const { root } = parse(path)
    if (badWinChars.test(path.substring(root.length))) {
      throw Object.assign(new Error('Illegal characters in path.'), {
        path,
        code: 'EINVAL',
      })
    }
  }

  return path
}

export default pathArg

// https://youtu.be/uhRWMGBjlO8?t=537
//
// 1. readdir
// 2. for each entry
//   a. if a non-empty directory, recurse
//   b. if an empty directory, move to random hidden file name in $TEMP
//   c. unlink/rmdir $TEMP
//
// This works around the fact that unlink/rmdir is non-atomic and takes
// a non-deterministic amount of time to complete.
//
// However, it is HELLA SLOW, like 2-10x slower than a naive recursive rm.

import { basename, parse, resolve } from 'path'
import { defaultTmp, defaultTmpSync } from './default-tmp.js'

import { ignoreENOENT, ignoreENOENTSync } from './ignore-enoent.js'

import {
  chmodSync,
  lstatSync,
  promises as fsPromises,
  renameSync,
  rmdirSync,
  unlinkSync,
} from './fs.js'
const { lstat, rename, unlink, rmdir, chmod } = fsPromises

import { Dirent, Stats } from 'fs'
import { RimrafAsyncOptions, RimrafSyncOptions } from './index.js'
import { readdirOrError, readdirOrErrorSync } from './readdir-or-error.js'

// crypto.randomBytes is much slower, and Math.random() is enough here
const uniqueFilename = (path: string) => `.${basename(path)}.${Math.random()}`

const unlinkFixEPERM = async (path: string) =>
  unlink(path).catch((er: Error & { code?: string }) => {
    if (er.code === 'EPERM') {
      return chmod(path, 0o666).then(
        () => unlink(path),
        er2 => {
          if (er2.code === 'ENOENT') {
            return
          }
          throw er
        }
      )
    } else if (er.code === 'ENOENT') {
      return
    }
    throw er
  })

const unlinkFixEPERMSync = (path: string) => {
  try {
    unlinkSync(path)
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'EPERM') {
      try {
        return chmodSync(path, 0o666)
      } catch (er2) {
        if ((er2 as NodeJS.ErrnoException)?.code === 'ENOENT') {
          return
        }
        throw er
      }
    } else if ((er as NodeJS.ErrnoException)?.code === 'ENOENT') {
      return
    }
    throw er
  }
}

export const rimrafMoveRemove = async (
  path: string,
  opt: RimrafAsyncOptions
) => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  try {
    return await rimrafMoveRemoveDir(path, opt, await lstat(path))
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'ENOENT') return true
    throw er
  }
}

const rimrafMoveRemoveDir = async (
  path: string,
  opt: RimrafAsyncOptions,
  ent: Dirent | Stats
): Promise<boolean> => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  if (!opt.tmp) {
    return rimrafMoveRemoveDir(
      path,
      { ...opt, tmp: await defaultTmp(path) },
      ent
    )
  }
  if (path === opt.tmp && parse(path).root !== path) {
    throw new Error('cannot delete temp directory used for deletion')
  }

  const entries = ent.isDirectory() ? await readdirOrError(path) : null
  if (!Array.isArray(entries)) {
    // this can only happen if lstat/readdir lied, or if the dir was
    // swapped out with a file at just the right moment.
    /* c8 ignore start */
    if (entries) {
      if (entries.code === 'ENOENT') {
        return true
      }
      if (entries.code !== 'ENOTDIR') {
        throw entries
      }
    }
    /* c8 ignore stop */
    if (opt.filter && !(await opt.filter(path, ent))) {
      return false
    }
    await ignoreENOENT(tmpUnlink(path, opt.tmp, unlinkFixEPERM))
    return true
  }

  const removedAll = (
    await Promise.all(
      entries.map(ent => rimrafMoveRemoveDir(resolve(path, ent.name), opt, ent))
    )
  ).reduce((a, b) => a && b, true)
  if (!removedAll) {
    return false
  }

  // we don't ever ACTUALLY try to unlink /, because that can never work
  // but when preserveRoot is false, we could be operating on it.
  // No need to check if preserveRoot is not false.
  if (opt.preserveRoot === false && path === parse(path).root) {
    return false
  }
  if (opt.filter && !(await opt.filter(path, ent))) {
    return false
  }
  await ignoreENOENT(tmpUnlink(path, opt.tmp, rmdir))
  return true
}

const tmpUnlink = async (
  path: string,
  tmp: string,
  rm: (p: string) => Promise<any>
) => {
  const tmpFile = resolve(tmp, uniqueFilename(path))
  await rename(path, tmpFile)
  return await rm(tmpFile)
}

export const rimrafMoveRemoveSync = (path: string, opt: RimrafSyncOptions) => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  try {
    return rimrafMoveRemoveDirSync(path, opt, lstatSync(path))
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code === 'ENOENT') return true
    throw er
  }
}

const rimrafMoveRemoveDirSync = (
  path: string,
  opt: RimrafSyncOptions,
  ent: Dirent | Stats
): boolean => {
  if (opt?.signal?.aborted) {
    throw opt.signal.reason
  }
  if (!opt.tmp) {
    return rimrafMoveRemoveDirSync(
      path,
      { ...opt, tmp: defaultTmpSync(path) },
      ent
    )
  }
  const tmp: string = opt.tmp

  if (path === opt.tmp && parse(path).root !== path) {
    throw new Error('cannot delete temp directory used for deletion')
  }

  const entries = ent.isDirectory() ? readdirOrErrorSync(path) : null
  if (!Array.isArray(entries)) {
    // this can only happen if lstat/readdir lied, or if the dir was
    // swapped out with a file at just the right moment.
    /* c8 ignore start */
    if (entries) {
      if (entries.code === 'ENOENT') {
        return true
      }
      if (entries.code !== 'ENOTDIR') {
        throw entries
      }
    }
    /* c8 ignore stop */
    if (opt.filter && !opt.filter(path, ent)) {
      return false
    }
    ignoreENOENTSync(() => tmpUnlinkSync(path, tmp, unlinkFixEPERMSync))
    return true
  }

  let removedAll = true
  for (const ent of entries) {
    const p = resolve(path, ent.name)
    removedAll = rimrafMoveRemoveDirSync(p, opt, ent) && removedAll
  }
  if (!removedAll) {
    return false
  }
  if (opt.preserveRoot === false && path === parse(path).root) {
    return false
  }
  if (opt.filter && !opt.filter(path, ent)) {
    return false
  }
  ignoreENOENTSync(() => tmpUnlinkSync(path, tmp, rmdirSync))
  return true
}

const tmpUnlinkSync = (
  path: string,
  tmp: string,
  rmSync: (p: string) => void
) => {
  const tmpFile = resolve(tmp, uniqueFilename(path))
  renameSync(path, tmpFile)
  return rmSync(tmpFile)
}

