import { chmodSync, promises } from './fs.js'
const { chmod } = promises

export const fixEPERM =
  (fn: (path: string) => Promise<any>) => async (path: string) => {
    try {
      return await fn(path)
    } catch (er) {
      const fer = er as NodeJS.ErrnoException
      if (fer?.code === 'ENOENT') {
        return
      }
      if (fer?.code === 'EPERM') {
        try {
          await chmod(path, 0o666)
        } catch (er2) {
          const fer2 = er2 as NodeJS.ErrnoException
          if (fer2?.code === 'ENOENT') {
            return
          }
          throw er
        }
        return await fn(path)
      }
      throw er
    }
  }

export const fixEPERMSync = (fn: (path: string) => any) => (path: string) => {
  try {
    return fn(path)
  } catch (er) {
    const fer = er as NodeJS.ErrnoException
    if (fer?.code === 'ENOENT') {
      return
    }
    if (fer?.code === 'EPERM') {
      try {
        chmodSync(path, 0o666)
      } catch (er2) {
        const fer2 = er2 as NodeJS.ErrnoException
        if (fer2?.code === 'ENOENT') {
          return
        }
        throw er
      }
      return fn(path)
    }
    throw er
  }
}

export const ignoreENOENT = async (p: Promise<any>) =>
  p.catch(er => {
    if (er.code !== 'ENOENT') {
      throw er
    }
  })

export const ignoreENOENTSync = (fn: () => any) => {
  try {
    return fn()
  } catch (er) {
    if ((er as NodeJS.ErrnoException)?.code !== 'ENOENT') {
      throw er
    }
  }
}

